(window.webpackJsonp=window.webpackJsonp||[]).push([[101],{460:function(_,v,r){"use strict";r.r(v);var n=r(14),b=Object(n.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[_._v("转自中国水利水电科学研究院网站")]),_._v(" "),v("p",[v("strong",[_._v("讣 告")])]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/www.iwhr.com_zgskyww_rootimages_2014_01_05_20140105095216509.jpg",alt:""}})]),_._v(" "),v("p",[_._v("中国共产党的优秀党员，杰出的水力学与河流动力学家，我国水力学学科重要的奠基人和开拓者之一，中国科学院资深院士，中国水利水电科学研究院名誉院长林秉南同志，因病于2014年1月3日18：56分在北京逝世，享年94岁。")]),_._v(" "),v("p",[_._v("林秉南院士1920年4月21日出生于马来西亚，1922年随父母定居广州，1938年考入交通大学唐山工学院土木系学习，毕业后到贵州修文资源委员会修文水电工程处工作，从此与水利结缘。1946年他带着工程实践中的问题赴美留学，在九年时间里，他先后获得美国爱荷华大学硕士学位、博士学位，并在美国科罗拉多州立大学任教和从事研究工作。1955年底他举家回归祖国，参加新中国的水利建设。他于1958年初着手水利水电科学研究院水工所的筹备工作。1978年他参加了全国科技大会，获先进个人奖。同年受组织委托参加水工所恢复重建的领导工作，并于1982年担任水利水电科学研究院院长，为水科院迎来建院后的第二个春天，洒下了辛勤的汗水。1986年后他先后担任三峡工程泥沙论证专家组组长、国务院三峡工程泥沙课题专家组的组长和顾问，为解决三峡工程的泥沙问题做出了重大贡献。")]),_._v(" "),v("p",[_._v("林秉南院士曾是全国政协第5至第8届委员；清华大学水利系兼职教授；美国科罗拉多州立大学客座教授;国际泥沙研究培训中心顾问委员会主席；中国水利学会副理事长（1981.2.")]),_._v(" "),v("ul",[v("li",[_._v("1999.3.）；钱宁泥沙科学奖基金会主任委员（1988-2005年）；1991年当选中国科学院院士。")])]),_._v(" "),v("h2",{attrs:{id:"林秉南院士长期从事水力学与河流动力学的应用研究-无论是在明渠不恒定流研究方面还是在高速水流研究方面都取得了开创性的成果。早在赴美留学期间-他提出了计算明渠不恒定流的指定时段构造特征线网法。此项成果1949"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#林秉南院士长期从事水力学与河流动力学的应用研究-无论是在明渠不恒定流研究方面还是在高速水流研究方面都取得了开创性的成果。早在赴美留学期间-他提出了计算明渠不恒定流的指定时段构造特征线网法。此项成果1949"}},[_._v("#")]),_._v(" 林秉南院士长期从事水力学与河流动力学的应用研究，无论是在明渠不恒定流研究方面还是在高速水流研究方面都取得了开创性的成果。早在赴美留学期间，他提出了计算明渠不恒定流的指定时段构造特征线网法。此项成果1949")]),_._v(" "),v("p",[_._v("1976年先后被收入美、日学术专著。他首次通过较准确的实验证实了泥沙沉降中浓度效应的存在，认识到泥沙颗粒雷诺数是研究浓度效应的另一重要参数，以奥辛方程为基础扩展了原来的近似分析，改变了以往的泥沙沉速公式只适用于单颗粒在无穷介质中沉降的状况。")]),_._v(" "),v("p",[_._v("他是我国最早对长江三峡水库溃坝波问题进行试验研究的学者。1958年，他受命进行长江三峡水库突然泄放对下游影响的试验研究。论文“长江三峡水库水体突然泄放问题研究”、“三峡洪水演进计算方法研究”，不仅为三峡工程论证提供支持，而且为溃坝问题研究打下基础。")]),_._v(" "),v("p",[_._v("他是我国最早运用数学方法模拟不恒定流流场的科学家之一。从上世纪50年代中期开始，他便着手对钱塘江涌潮进行计算分析，指导并参与浙江河口海岸研究所开发出了二维潮汐水流的数学模型和一维潮流输沙的动床模型,为钱塘江河口开发与治理做出了重大贡献。")]),_._v(" "),v("p",[_._v("他是高坝水流宽尾墩新型消能工的发明人之一。1985年他主持完成的《宽尾墩、窄缝挑坎新型消能工及掺气减蚀的研究和应用》获得全国科技进步二等奖。这项研究找到了解决我国高坝建设中出现的极高高速水流对水工建筑物更为严重的空蚀及冲刷问题的方法，为中国高坝建设提供了有效的技术支撑，使中国在高坝泄洪消能方面走进了世界的前列。")]),_._v(" "),v("p",[_._v("1985年后受国务院有关部门的委托，林秉南院士带领几十位专家组织协调大专院校、科研院所，对采用蓄清排浑方式控制三峡水库泥沙淤积的可行性做出了客观论证，并对其实施效果进行了长期的跟踪研究，为三峡工程的实施和建成后的运行提供了科学依据。")]),_._v(" "),v("p",[_._v("林秉南院士非常重视国际交流工作。在改革开放初期，他邀请一批国际著名学者来华访问讲学，并在各种国际会议上介绍我国水利科技的成果。他1983年率团访日促成的《中日河工坝工会议》至今已成功举办27届；在他和其他专家的积极推动下，1984年联合国教科文组织国际泥沙研究培训中心在北京成立,成为我国泥沙研究面向国际的窗口和进行国内外学术交流的平台。1987年他带团参加第22届IAHR（国际水利与工程研究协会）大会，恢复了我国学者在IAHR的活动。1991-1996年他担任IAHR亚太地区分会主席，1997年获得IAHR终身荣誉会员奖。")]),_._v(" "),v("p",[_._v("林秉南院士一贯重视对年轻人的关心和培养。鼓励年轻人养成自学习惯，特别强调要理论联系实际，提高解决各种疑难问题的能力。他力戒说教，时时处处以老一代科技工作者的真我，教育、影响和启发年轻人，为祖国的水利水电事业培养了众多优秀人才。")]),_._v(" "),v("p",[_._v("林秉南院士一生热爱社会主义祖国、热爱中国共产党、热爱水利事业。在将近一个世纪的岁月里，他亲历了中国从苦难走向繁荣、中国的水利事业从一穷二白到兴旺发达的全过程，他把这一切都归功于中国共产党的坚强领导，并于1985年光荣地加入了党组织。他目标坚定、锲而不舍、品德高尚、虚怀若谷；他治学严谨、躬行实践、为人正派、平易近人；他把毕生精力献给了祖国的水利建设事业。林秉南院士的不幸逝世，使我们失去了一位优秀的中国共产党党员、一位杰出的水力学与河流动力学专家，是我国水利事业的巨大损失。我们沉痛悼念林秉南院士，深切缅怀林秉南院士。")]),_._v(" "),v("p",[_._v("林秉南院士遗体告别仪式将于2014年1月9日上午10点在八宝山殡仪馆东礼堂举行，接受亲友吊唁、送别。")]),_._v(" "),v("p",[_._v("特此讣告。")]),_._v(" "),v("p",[_._v("林秉南院士治丧办公室"),v("br"),_._v("\n2014年1月4日")]),_._v(" "),v("p",[_._v("林秉南院士治丧办公室联系方式：")]),_._v(" "),v("p",[_._v("电话 ：010-68781237，010-68785308")]),_._v(" "),v("p",[_._v("传真 ：010-68412598")]),_._v(" "),v("hr"),_._v(" "),v("p",[v("strong",[_._v("附林秉南资料：")]),v("br"),_._v(" "),v("strong",[_._v("1、人物简历")]),v("br"),_._v("\n1920年4月21日，生于马来亚挂捞吡腊（祖籍福建省莆田县）。 林秉南院士(4张)"),v("br"),_._v("\n1931—1934年，广州市立师范学校附中，"),v("br"),_._v("\n1934—1937年，广州市立第一中学。"),v("br"),_._v("\n1939—1942年，交通大学唐山工学院（贵州平越，今西南交通大学）土木系学习。"),v("br"),_._v("\n1942—1943年，任交通大学土木系助教。"),v("br"),_._v("\n1943—1945年，任贵州修文资源委员会修文水电工程处工务员。"),v("br"),_._v("\n1945—1946年，任重庆及南京交通部路政司技士。"),v("br"),_._v("\n1946—1951年，在美国爱荷华州立大学学习，获硕士和博士学位。"),v("br"),_._v("\n1949—1952年，任美国爱荷华州立大学水利研究所副研究员。"),v("br"),_._v("\n1952—1955年，任美国科罗拉多州立大学研究生部助理教授。"),v("br"),_._v("\n1956—1958年，任中国科学院水工研究室研究员、水力学组副组长。"),v("br"),_._v("\n1958—1980年，任水利水电科学研究院水工所副筹建人、副所长、所长。"),v("br"),_._v("\n1978年，兼任清华大学水利系教授。"),v("br"),_._v("\n1980—1981年，任美国科罗拉多州立大学客座教授。"),v("br"),_._v("\n1982—1984年，任水利水电科学研究院院长。"),v("br"),_._v("\n1985—，任中国水利水电科学研究院咨询委员、名誉院长。[1]")]),_._v(" "),v("p",[v("strong",[_._v("2、学术兼职")]),v("br"),_._v("\n1984—2004年，兼任国际泥沙研究与培训中心顾问委员会主席、名誉主席。"),v("br"),_._v("\n1985—1993年，任水利电力部长江三峡工程泥沙与航运专题论证泥沙专家组组长。"),v("br"),_._v("\n1989—1993年，任联合国发展署亚洲地区泥沙冲淤培训项目经理。"),v("br"),_._v("\n1991—1996年，任国际水力学研究协会（IAHR）亚太地区分会主席。"),v("br"),_._v("\n1993—2004年，历任水利部三峡坝区试验专家组、三峡工程开发总公司泥沙专家组、国务院三峡建设委员会办公室三峡工程泥沙课题专家组组长。"),v("br"),_._v("\n1997年，当选为国际水利工程研究协会（IAHR）荣誉会员。"),v("br"),_._v("\n2004—，任国务院三峡建设委员会办公室三峡工程泥沙课题专家组顾问。[1]")]),_._v(" "),v("p",[v("strong",[_._v("3、学术成果")]),v("br"),_._v("\n在国外最早提出两种指定时段构造特征线网法，并提出当时属于先进的明渠不恒定流计算法。同时还展开浓度对泥沙沉速影响和水槽输沙试验研究，成果曾为各方引用。[1-2]"),v("br"),_._v("\n在国外工作时最早提出两种指定时段构造特征线网法，并提出当时属于先进的明渠不恒定流计算法，被分别收入美国H.Rouse编《工程水力学》（1949年）和V.T.Chow著《明渠水力学》（1958年）及日本本间仁和安芸胶一编《物部水理学》（1962年）三部专著中。[1-2]\n1958年，首先应用大模型研究三峡水库水体突泄对下游广大地区的可能影响。对大面积海湾和河口，在国内首先应用二维特征理论和破开算子法，建立了符合实测资料的快速计算方法。泥沙方面，曾在国外研究含沙浓度和颗粒雷诺数对泥沙沉降速度的影响和进行过大量水槽输沙试验。[1-2]"),v("br"),_._v("\n1956年，回国后开展高坝水力学和明渠不恒定流研究。在高坝水力学方面，领导、发展了收缩式新型消能工（包括宽尾墩及窄缝挑坎）的研究，为高坝泄洪消能，特别是在高深峡谷建设的高坝泄洪消能，提供了有效方法，已在多初工程应用。1979年，还指出在一定条件下，宽尾墩下游的部分溢流面的剖面还可以采用非光滑面，从而简化施工。上述新型消能工已在国内多处工程应用。1985年，该项研究成果曾集体获得国家科技进步二等奖。[1-2]")]),_._v(" "),v("p",[v("strong",[_._v("4、荣誉奖励")]),v("br"),_._v("\n1978年，全国科学大会先进个人。"),v("br"),_._v("\n1978年，水电部科学技术先进工作者。"),v("br"),_._v("\n第5至8届，全国政协委员。"),v("br"),_._v("\n1986年，当选为美国爱荷华大学杰出校友。"),v("br"),_._v("\n1993年，当选为美国爱荷华大学杰出校友工程院成员。"),v("br"),_._v("\n1997年，获美国土木工程学会水利工程奖，"),v("br"),_._v("\n1997年，获国际水力学研究协会荣誉会员称号（终身）。"),v("br"),_._v("\n1997年，获美国土木工程学会干旱地区水利工程奖。[1]")]),_._v(" "),v("p",[v("strong",[_._v("5、学术论著")]),v("br"),_._v("\n出版书籍"),v("br"),_._v("\n《工程泥沙》，水利电力出版社，1992年。"),v("br"),_._v("\n《林秉南论文选》，中国水利水电出版社，2000年。[1]")])])}),[],!1,null,null,null);v.default=b.exports}}]);