(window.webpackJsonp=window.webpackJsonp||[]).push([[118],{479:function(t,r,e){"use strict";e.r(r);var n=e(14),v=Object(n.a)({},(function(){var t=this,r=t._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[r("hr"),t._v(" "),r("p",[t._v("|  |  |  |  |")]),t._v(" "),r("p",[r("strong",[t._v("文化的力量")])]),t._v(" "),r("p",[r("strong",[t._v("作者")]),t._v(" ：徐飞 "),r("strong",[t._v("发布时间")]),t._v(" ：2013-10-22")]),t._v(" "),r("p",[r("img",{attrs:{src:"http://xy.swjtu.edu.cn/userfile/jpg/20131022110946R2DR06JRDH.jpg",alt:""}})]),t._v(" "),r("p",[r("a",{attrs:{href:"http://www.swjtu.edu.cn/depart/university/feixu.htm",target:"_blank",rel:"noopener noreferrer"}},[t._v("徐飞 教授"),r("OutboundLink")],1)]),t._v(" "),r("p",[t._v("文化何为?以文化人。尽管在美国学者克鲁伯和克拉克洪合著的《文化，关于概念和定义的检讨》中有关于文化的164种定义，但我更偏爱梁晓声所说，文化是植根于内心的修养，是自我负责的意志，是无须提醒的自觉，是以约束为前提的自由，是为他人着想的善良。大学文化何为？《大学》有云：“大学之道，在明明德，在亲民，在止于至善。”\n大学文化是科学，也是人文；是图书馆，也是实验室；是高楼大厦，也是林荫小道，是振聋发聩，也是耳濡目染。大学文化深邃而敦厚，博大而空灵，润物无声，生生不息。文化之于大学，恰如阳光雨露之于草木，大学文化以其特有的导向、凝聚、激励、塑造等功能，在大学的各行为主体和表现载体中发挥重要作用。")]),t._v(" "),r("p",[t._v("正因为如此，我们才会去发现文化的力量。麻省理工（MIT）第十四任校长保尔·E·格雷指出，一个大学的质量在于它为自己制定的发展水平和前景以及它的原动力和潜在动力。他强调，能否达到大学发展所规划的目标，取决于大学的“原动力和潜在能力”，而大学文化最伟大之处，就在于她通过潜移默化地影响人，从心灵深处塑造人为大学提供生气和动力；她像血脉一样熔铸在大学的各个层面，自发地左右着或自觉地引导着大学人的思想和行为，臻于“入芝兰之室久而自芳”。不仅如此，大学作为时代之表征，其文化还反映时代精神和社会风向，烛照人类前进的方向。弗莱克斯纳曾言，大学文化不是与世隔绝的历史的东西，恰恰相反，它是时代的表现，并对当时和将来都产生影响。没有已故的大学文化，已故的只是陈识。真正的文化必定以某种方式作为软实力而存在，让人感动的大学文化，必定扣动历史的脉搏，感应时代的前奏。")]),t._v(" "),r("p",[t._v("大学离不开文化，何况是在这样一个“三千年未有”之时代。没有文化滋养和浸染的大学，情何以堪？然而，当下的很多高校似乎遗弃了文化，失去了灵魂，虽然已经走得很远，却忘记了当初为何出发。现在，一些高校经过多年的规模化发展，硬件建设迅速改善，很多校园堪称气派。然而，大楼林立，大师罕见，曾被人们视为文化和精神的圣地、学生人格涅槃的庄严学府，如今被诟病为曲学阿世、趋炎附势的官场和名利场，成为公众嘲讽的对象。大学应有的质朴敦厚、清净澄明难寻踪影；独立之精神、自由之思想仅成说辞；诚心正意、求真务实沦为空谈，良知正义、理想使命多为口号；浮华、投机、势利、崇官、媚权随处可见。大学行政化、功利化和产业化渐成痼疾；价值理性和人文理性濒于丧失，理性蜕变为单纯的计算理性或曰工具理性，其论域除了计算大概就剩算计。此外，一些学者严重异化，有些人已变得贪婪、卑下、猥琐，另一些则成为世俗老到、长袖善舞的“精致的利己主义者”。")]),t._v(" "),r("p",[t._v("面对这样严峻的形势，大学文化必须切实地肩负起应有的担当。哈佛大学荣誉校长洛丁根深刻指出，大学应是有着价值追求与精神内涵的心的家园，文化是大学的生命线，是一所大学的精髓所在。没有文化的指引和召唤，大学发展就失去了方向和动力。大学要积极从精神、制度、环境等多层面和多维度加强文化建设，特别是在当今这个开放、多元的时代，调整、转型和变革日益深刻，人们价值选择的独立性和自主性日益增强，价值取向日益增多，价值需求逐渐分化，诸多价值尺度和价值供给所带来的困惑和迷茫前所未有；“纯”和“正”的道统价值不断被功利价值消弭、稀释或曲解；主流价值边缘化、生活意义碎片化、精神生活虚无化几成通病。在这种情形下，大学回归、护卫与坚守其本然使命的任务显得异常艰巨。大学更需秉持以人才培养为根本，以研究高深学问为己任，以学术为志业，大力倡导和践行格物致知、追求真理，崇尚科学、勇于创新，拒绝功利、力戒浮躁，远离喧嚣、淡定从容。与此同时，在传统文化和现代文化、东方文化和西方文化、世俗文化和彼岸文化之间的交锋、交流和融合中，实现从文化自觉、文化自信到文化自强的历史性跨越。")]),t._v(" "),r("p",[t._v("（本文转自西南交通大学校友网）")]),t._v(" "),r("table",[r("thead",[r("tr",[r("th")])]),t._v(" "),r("tbody")])])}),[],!1,null,null,null);r.default=v.exports}}]);