(window.webpackJsonp=window.webpackJsonp||[]).push([[124],{486:function(_,v,p){"use strict";p.r(v);var t=p(14),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[_._v("权力“惜败” 学术惨胜")]),_._v(" "),v("p",[_._v("张曙光只差一票成院士")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/y2.ifengimg.com_haina_2013_39_d3c51c6a0e56271.jpeg@660x440.jpg",alt:""}})]),_._v(" "),v("p",[_._v("(2013年9月10日，张曙光在庭审中供述，曾因参选中科院院士需要花钱，收受贿赂2300万元。）")]),_._v(" "),v("p",[_._v("张曙光需要学界的肯定，某院士2006年便撰文高度赞扬张曙光2007年才开始组织编写的“专著”。")]),_._v(" "),v("p",[_._v("他需要专著，三十多位学者为他捉刀代笔。")]),_._v(" "),v("p",[_._v("面对代笔举报，铁道部有关部门提前排练应对中科院即将到来的调查，并将不配合的专家派遣出国。")]),_._v(" "),v("p",[_._v("2007年一年里，甚少著书立说的他一口气出版了两本专著，发表了5篇论文。")]),_._v(" "),v("p",[_._v("2013年9月11日，原铁道部运输局局长、副总工程师张曙光案开庭。庭审中，检方指控张曙光自2000年至2011年间进行四十余次金钱交易，金额达4755万元。张曙光供述，其中6次收受2300万元的理由，是2007年、2009年两次参选中科院院士“需要花钱”。")]),_._v(" "),v("p",[_._v("中科院的增选历来是舆论热点。尽管张曙光只陈述了索贿理由，并未供他贿选的相关事实，但中科院已不可避免地被牵扯进来。由此，数年前的两起学术风波，因一桩贪腐案的开庭，重回公众视野。")]),_._v(" "),v("p",[_._v("于一般学者而言，中科院是一座可望而不可即的高耸殿堂，通向殿堂的道路漫长而多阻，但张曙光几乎成功。")]),_._v(" "),v("p",[_._v("而对于在2007年前仅发表过两篇论文、博士未毕业的张曙光，要进入这个国家最高的学术殿堂而言，这几乎是一个不可能完成的任务。")]),_._v(" "),v("p",[_._v("张曙光几乎成功。在这个过程中，他并不是没有碰到困难，但是一一化解：他需要资格，铁道部将他推举为候选人；他需要专著，三十多位学者为他捉刀代笔；他面对质疑和举报，铁道部为他背书。张曙光几乎成功，他只差一票，就可以堂而皇之地迈入这座殿堂。")]),_._v(" "),v("p",[_._v("是什么让张曙光几乎无所不能、如有神助？张曙光在法庭上以参选院士为名收受的2300万元巨款发挥了多大的能量？南方周末记者采访多名院士与知情人，还原张曙光参选中科院院士事件始末。")]),_._v(" "),v("p",[_._v("经过调查，在张曙光的两次参选中，对我国最高学术机构独立与公正施加影响的，相较于金钱的力量，不受制约的权力起到的意义更甚。所以，这是一个老生常谈而又前所未有的故事，即公权力的魔力挣脱束缚后，是如何摄人心魂的——即使这是全国最具智慧的机构所在。")]),_._v(" "),v("p",[v("strong",[_._v("资格")])]),_._v(" "),v("p",[_._v("“工程院里对他的底细是了解的，有搞铁路的专家，他是怕，才选的中科院。”")]),_._v(" "),v("p",[_._v("2007年5月，当“张曙光”出现在中科院院士增选有效候选人名单中时，这是一个让大多数院士感到陌生的名字。")]),_._v(" "),v("p",[_._v("此前张曙光为人所知的是他官员的身份。自2003年刘志军担任铁道部部长起，张曙光开始了他的仕途上升期，至2007年，张曙光时任铁道部运输局局长兼副总工程师已有三年，官居正厅级。")]),_._v(" "),v("p",[_._v("但在学术界，张曙光是一个不折不扣的新人，甚至是陌生人。自1982年从兰州铁道学院（现兰州交通大学）车辆专业毕业后，到2007年之前，张曙光没有任何学术专著和理论成果，只发表过两篇论文，并且被中国工程院院士王梦恕评价为“水平很低”。")]),_._v(" "),v("p",[_._v("一个在学术上向来寂寂无名的人，要踏入中国学术界最高殿堂，这看上去是一个不可能完成的任务。")]),_._v(" "),v("p",[_._v("成为“有效候选人”是这个任务中的第一步。接着，他的材料会被审阅讨论，在经过筛选成为“初步候选人”后，再经过3轮投票，如果他能够赢得我国学术界在这个领域里最聪明、最权威的人的2/3的票数，他就能够当选，并且成为他们当中的一员。")]),_._v(" "),v("p",[_._v("“拿清华大学打比方，首先在学校会对人选进行一次简拔，这份名单到了科技部，还要再进行一次简拔，才能够成为有效候选人。”中科院技术科学部院士卢强告诉南方周末记者，对一个普通人来说，成为有效候选人并不是一件容易的事情。")]),_._v(" "),v("p",[_._v("但作为铁道部的高官，张曙光不仅顺利获得了部门推荐，且推荐材料堪称美妙。卢强回忆，“铁道部的推荐表说他在高铁（项目）里面非常重要，（做出）特别杰出的贡献，都是用的最强烈的褒义词。”")]),_._v(" "),v("p",[_._v("多位知情人士描述，在张整个参选过程中，铁道部简直是“倾举部之力”对其进行支持。")]),_._v(" "),v("p",[_._v("中国工程院院士王梦恕却认为张曙光原本完全不具资格。“他毕业后做车辆维修，后来转管理。他就这个过程，一看就不是走学术理论型的路。”")]),_._v(" "),v("p",[_._v("张曙光当时已是铁道部科学研究院的首席专家，但据铁科院一名退休工程师反应，这只是挂名，他从未见过张曙光进过铁科院的门。")]),_._v(" "),v("p",[_._v("南方周末记者所采访的多位院士的一个普遍反映则是，张曙光与中科院显得格格不入，即使张要参选院士，也应该是参选中国工程院院士。")]),_._v(" "),v("p",[_._v("2000年以后，铁道部先后出过三个院士，分别是2001年的部长傅志寰，2005年的副部长孙永福，2009年的总工程师何华武，他们都在中国工程院。与中科院重理论不同，工程院更侧重实践。")]),_._v(" "),v("p",[_._v("此外，中科院院士候选人当中，历来少见官员的身影，张曙光和水利部一副部长，是十年来仅有的两名成为中科院院士有效候选人的官员。相对而言，工程院里官员则更多，仅以2011年为例，共有23名政府高官或央企高管入围候选中国工程院院士（参见本报2011年6月3日报道《工程院是什么院》）。")]),_._v(" "),v("p",[_._v("“2007年，铁道部已经推荐了何华武参选工程院，如果再推荐张曙光，两人同时选上的机会非常小。”中科院技术科学学部院士潘际銮分析，“还有一个原因，工程院搞铁路的比较多，有人对他知根知底，中科院这边大家都对他不熟悉。”")]),_._v(" "),v("p",[_._v("“工程院里对他的底细是了解的，有搞铁路的专家，他是怕，才选的中科院。”2013年9月23日，工程院院士秦伯益向南方周末记者透露。")]),_._v(" "),v("p",[_._v("“张曙光反对过中华之星项目，这事情当时引起了很大争议。这个项目的总设计师刘友梅，就是工程院的院士。”铁道部科学研究院一位知情人告诉南方周末记者。")]),_._v(" "),v("p",[_._v("王梦恕告诉南方周末记者，2009年何华武当选工程院院士时他参加饭局，“饭桌上，我很诚恳地讲，你们不要再报张曙光了，丢了铁道部的人”。")]),_._v(" "),v("p",[v("strong",[_._v("包装术")])]),_._v(" "),v("p",[_._v("得益于公职，张曙光可以与多位中科院技术工程部的院士进行更为广泛持续的工作接触。")]),_._v(" "),v("p",[_._v("2007年可谓张曙光的学术年。在这一年里，他不仅成为了中科院院士增选的有效候选人，甚少著书立说的他还一口气出版了两本专著，发表了5篇论文。")]),_._v(" "),v("p",[_._v("这些学术著述，与铁道部的推荐材料一同成为张曙光参选的重要砝码。尤其是《铁路高速列车应用基础理论与工程技术》（以下简称《高速列车技术》）与《超大型工程系统集成理论与实践》两书，共计119万字，既有理论也含实践，涉及机械、力学、电传动技术等多个领域和学科。")]),_._v(" "),v("p",[_._v("几乎同时，一封来自铁道部工作人员的举报信寄到了中科院。信中，张曙光两本专著被指为捉刀代笔之作。而代笔之事，后来已被媒体调查证实。")]),_._v(" "),v("p",[_._v("2013年9月16日，南方周末记者看到了这封举报信。举报人向南方周末记者出示了《高速列车技术》“编写组专家通讯录”，名单共30人，其中13人来自北京交通大学，7人来自西南交通大学，3人来自同济大学，3人来自铁科院，3人来自铁道部，1人来自南车集团四方股份公司。")]),_._v(" "),v("p",[_._v("这份名单中既有大学教授，也有来自生产一线的工程师，他们的专业包括车辆工程、热能动力、电力电子与电力传动等8个学科。写作时间则分为两个阶段，第一阶段从2007年2月26日到3月8日，组织突击编写；第二阶段从3月9日到3月14日，进行清样校订。")]),_._v(" "),v("p",[_._v("值得一提的是，名单中所涉的北京交通大学和西南交通大学，属于铁道部所管的部属高校；而张曙光的多篇论文，联合署名中也多有两校师生的身影。")]),_._v(" "),v("p",[_._v("这也并不是张曙光第一次对自己进行包装。")]),_._v(" "),v("p",[_._v("2007年3月，中国铁路进入第六次大提速试车阶段，清华大学的50名教授在校长的带队下，参与了胶济线的试车，由张曙光全程陪同，并在目的地，由南车青岛四方机车车辆有限公司带领众教授进行参观，以一次使用PPT的演讲结束。")]),_._v(" "),v("p",[_._v("“他的演讲内容从机械系统到动力系统到空气动力学，都有介绍，效果非常好。”参与参观的中科院院士潘际銮回忆，他当时不明白张曙光的意图，对张曙光的学术素养颇有好感。")]),_._v(" "),v("p",[_._v("该行程综合学术演讲、生产参观、现场体验于一体，张曙光巧妙完成了一次自我展示。但据《中国科学院院士增选工作中院士行为规范》中第五条明确规定，“院士候选人或其所在单位及其他相关者不得以学术交流、考察、鉴定、答辩、评审、评奖等名目进行影响增选工作公正性的活动。”")]),_._v(" "),v("p",[_._v("9月23日，工程院院士秦伯益在接受南方周末记者电话采访时表示，这种变相的公关活动，在两院院士评选前后，屡见不鲜。")]),_._v(" "),v("p",[_._v("“直接拜票是低档次的做法，大多时候会更巧妙，比如单位出面邀请你去参加活动，然后候选人就跑过来说找你汇报汇报。一般来说，只要我知道有院士候选人在这个单位，我就会拒绝出席这样的活动。”")]),_._v(" "),v("p",[_._v("“我是不相信哪个院士没有被公关过。”秦伯益直言，被公关是不可避免的，关键是被公关以后要站在国家层面去进行判断。")]),_._v(" "),v("p",[_._v("得益于公职，张曙光可以与多位中科院技术工程部的院士进行更为广泛持续的工作接触。")]),_._v(" "),v("p",[_._v("卢强回忆，参观胶济线时，列车时速达到250公里。作为空气动力学的专家，他告诉张曙光，列车的时速在超过200公里时，相对于空气阻力，轮轨的摩擦已经可以忽略不计。")]),_._v(" "),v("p",[_._v("“我问铁路部门研不研究空气动力学的问题，他说不研究。我说不研究，很多电能就白白浪费了，他马上就说，‘我也想到了这个问题’。他的意思是，我们英雄所见略同。”")]),_._v(" "),v("p",[_._v("张曙光当场表示请卢强牵头组织一支队伍。卢强回京找到了北大和北航的两位前任校长，“十天后，铁道部和课题组签订了项目”。")]),_._v(" "),v("p",[_._v("张曙光的效率和态度给卢强留下了印象。“北航的老校长告诉我，北航之前试图向铁道部提出项目，但不被理睬。”")]),_._v(" "),v("p",[_._v("让卢强更为意外的是，在他们提交一期报告后，张曙光马上把这些成果应用到京津城际高铁列车上，并把课题组的专家请去体验。")]),_._v(" "),v("p",[_._v("“我们的科研成果这么快就用上了，北大的老校长很开心。”卢强说。")]),_._v(" "),v("p",[_._v("据南方周末记者查证，2007年前后，清华大学有9个项目被列入“铁道部科技研究开发计划”。除卢强院士主持的“采用空气动力学措施作为高速列车辅助手段预研”项目外，还有朱静院士主持的“动车组高速车轴研制”、潘际銮院士主持的“京津高速线钢轨闪光对焊及热处理工艺的研究”、温诗铸院士主持的“齿轮箱润滑技术及选用标准研制”。")]),_._v(" "),v("p",[_._v("上述四名院士，同属于中科院技术科学部，对张曙光有直接投票权。")]),_._v(" "),v("p",[_._v("有舆论对此进行质疑，认为这属于一种变相的贿选与利益交换。卢强对这种说法嗤之以鼻。他承认，对于科研工作者而言，没有什么比自己的研究能够被应用更高兴的事情了，“那就像你看到自己的小孩长大一样”。")]),_._v(" "),v("p",[_._v("卢强甚至向南方周末记者公布了课题组的经费，“清华、北大、北航三个学校每所分了才不到50万；当时国内甚至没有空气动力的软件，我们是去美国租来用的。”可供参照的是，卢强所主持的973项目，经费达3500万元。")]),_._v(" "),v("p",[_._v("同时，卢强也坦率地承认这些合作不可避免地成为了张曙光的展示机会。“‘他抓工作的效率是比较高的，是比较有魄力的’，这就是当时的印象。”")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/y1.ifengimg.com_haina_2013_39_53af0799264337a.jpeg@660x440.jpg",alt:""}})]),_._v(" "),v("p",[_._v("2007年之前，张曙光在学界毫无名声。但随着仕途亨通，他开始成为一颗“学术新星”。张曙光在动车上视察。 （CFP/图）")]),_._v(" "),v("p",[v("strong",[_._v("合谋者")])]),_._v(" "),v("p",[_._v("得知中科院要调查张曙光代笔事件，铁科院相关部门特意进行了“模拟调查”，提前演练中科院调查时，专家们应该如何“应对”。一名专家拒绝配合，随即被铁科院公费派出国外数月，直到中科院增选活动结束才回国。")]),_._v(" "),v("p",[_._v("2007年8月29日，中国科学院公布了院士增选初步候选人名单的公告。有效候选人只留下145人。张曙光过关。")]),_._v(" "),v("p",[_._v("但那封举报信是悬在他头顶的达摩克利斯之剑。据《中国科学院院士增选工作中院士候选人行为守则》规定，“‘被推荐人附件材料’的提供者要对材料的真实性负责……不得作虚假陈述，不得提供关于研究成果等方面的不实信息。”")]),_._v(" "),v("p",[_._v("张曙光的代笔事件一旦被戳穿，按照上述规定，张曙光不仅会被终止评审资格，并且会被取消下一次的被推荐资格。")]),_._v(" "),v("p",[_._v("事实上的严重性远甚于此。在学术界，一个人涉及学术造假，往往意味着学术生命的终结，因为这是“学术品格问题”。")]),_._v(" "),v("p",[_._v("张曙光亦因为这一封举报信成为这一届增选活动中的“明星”，潘际銮和卢强都向南方周末记者表示，他们当了这么多年的院士，候选人受到如此严重的指控还是头一回碰到。")]),_._v(" "),v("p",[_._v("另一边厢，铁科院得知中科院要对此事进行调查时，如临大敌。据铁科院一位专家介绍，事前，相关部门特意进行了“模拟调查”，即提前演练中科院调查时，专家们应该如何“应对”。该专家拒绝配合，随即被铁科院公费派出国外数月，直到中科院增选活动结束才回国。")]),_._v(" "),v("p",[_._v("最终，中科院采取了公函询问的方式。在最终投票前的会议上，中科院领导宣读了铁道部的两封回函。")]),_._v(" "),v("p",[_._v("“第一封是铁道部人事部门的回函，称铁道部经查无（举报）此人；第二封是铁道部就张曙光代笔事件的回应，非常积极地肯定这两本书就是张写的。”卢强向南方周末记者回忆了事件的调查结果。按照规定，对于匿名和不实举报，中科院不予受理。")]),_._v(" "),v("p",[_._v("张曙光所获得的背书并不能完全打消院士们对他的疑惑。“有的院士说，举报人化名，可能是因为害怕被打击报复。”卢强说。")]),_._v(" "),v("p",[_._v("另一个调查的办法被想了出来，“如果书是他写的，那请他把草稿拿出来。”潘际銮回忆，这个要求最终没有得到张曙光本人的回应。")]),_._v(" "),v("p",[_._v("事情因此而陷入僵局。“现代科学隔行如隔山，加之科学都是研究谁都不知道的事情。”秦伯益介绍，在一个学部里，有能力对候选人材料进行完全消化理解的并不多。所以许多院士并没有办法形成一个明晰的判断，大部分的人，只能够依靠常识，对张曙光是否能够突击完成如此高强度的著书任务而感到怀疑。")]),_._v(" "),v("p",[_._v("按照惯例，技术科学部按学科领域分成材料组、工程一组、工程二组等五个小组，每3到5个人组成一个团队，对候选人的资料进行研读，然后集体产生一份小组意见。")]),_._v(" "),v("p",[_._v("卢强告诉南方周末记者，张曙光所在的工程一组，铁道方面的专家只有一个，那就双院士、西南交通大学教授沈志云。放眼全国，沈志云都是顶尖的铁路专家，被称为“中国高铁之父”。")]),_._v(" "),v("p",[_._v("无独有偶，被称为“中国高铁第一人”的张曙光，恰是沈志云门下的在职博士生，当时还未毕业。")]),_._v(" "),v("p",[_._v("更巧的是，被指控捉刀的《铁路高速列车应用基础理论与工程技术》，作序人就是沈志云。")]),_._v(" "),v("p",[_._v("沈志云在序中对张曙光提出了高度赞扬，“张曙光同志的这本专著首次系统总结和归纳了中国高速铁路理论和技术创新的最新成果，是当今中国高速铁路理论、技术发展的代表之作，同时丰富和发展了世界高速铁路的学术研究成果，代表了中国高速铁路对世界铁路发展的贡献。”")]),_._v(" "),v("p",[_._v("但甚为奇怪的是，该书后来被证实从2007年2月才开始组织编写，但沈志云作序落款时间是2006年12月。不知沈先生对该书的评价缘何得出。")]),_._v(" "),v("p",[_._v("张曙光是沈志云的在读博士生这件事，也引起了不小的轰动。“这个是很罕见的，你博士还没有毕业，说明你还不具备博士水平，你怎么能够来选院士呢？”潘际銮表示，2007年是最后一次参与增选院士的投票（2009年因年满八十岁成为资深院士，不参与投票），但“在职博士当候选人”的事情是他头一回遇到，留下深刻印象。")]),_._v(" "),v("p",[_._v("多位拥有给张曙光投票权的院士告诉南方周末记者，正因为不是所有人都有能力研读候选人的材料，那同领域专家的意见就显得至关重要。")]),_._v(" "),v("p",[_._v("“我会下问过沈志云，‘你的意见怎么样？’沈志云回答我，‘张曙光是我的学生，我不好多说什么。’”卢强向南方周末记者强调，这是会下议论的内容，而会上讨论的内容是严禁公开的。")]),_._v(" "),v("p",[_._v("2007年12月27日，中科院公布增选投票结果，张曙光落选。")]),_._v(" "),v("p",[_._v("2009年，张曙光再一次向中科院院士发起冲击，一路成为有效候选人、初步候选人，进入最终票选环节，并再次落选。据张在法庭上供述称，他“只差一票”，离进入最高学术殿堂仅一步之遥。2009年中科院技术科学部共有110名院士，按照其中78名院士有投票权计，张曙光共拿到了51张票。有院士将其看成是学术尊严的胜利，但这只能称之为惨胜。")])])}),[],!1,null,null,null);v.default=n.exports}}]);