(window.webpackJsonp=window.webpackJsonp||[]).push([[13],{375:function(n,r,t){"use strict";t.r(r);var v=t(14),_=Object(v.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  |  |  |  |"),r("br"),n._v(" "),r("strong",[n._v("唐山的交大记忆（上）")]),n._v(" **"),r("br"),n._v("\n唐山的交大记忆（下）**")]),n._v(" "),r("hr"),n._v(" "),r("p",[n._v("**"),r("br"),n._v("\n回首来时路"),r("br"),n._v("\n——《唐山的交大记忆》观后感访谈"),r("br"),n._v("\n文／皂紫菡 王也 2015-11-05 （来源：校庆办公室）"),r("br"),n._v("\n**"),r("br"),n._v("\n近日，央视纪录片《唐山的交大记忆》在我校师生中悄然传播开来，影响十分广泛。百年峥嵘岁月，十八次辗转搬迁，一步一个脚印走到今天犀浦—九里—峨眉三足鼎立的时代，新老交大人又会借此契机触发多少感慨？为此，我们采访了几位交大师生校友，请他们谈谈观看了这部纪录片后的感想。"),r("br"),n._v("\n当我们一提到《唐山的交大记忆》这部纪录片时，本就是唐山人的徐文生老师难掩心中的激动之情。他回忆道，当时的唐山交大在唐山人心中占据十分神圣的地位，是承载了许多老一辈学子的梦想的学堂。而他本人则是在1978年高考填报志愿时，怀揣着一份崇敬之情选择了西南交大，最后留在学校工作至今。话里话外，我们还能清楚地感受到他的骄傲和自豪。谈到这部纪录片，徐老师更加感慨，他向我们描述了抗日战争时期命途多舛的唐山交大发展历程，痛心疾首于全国大规模院系调整后实力大幅度削弱的交大。徐老师特别强调了加强学风建设的重要性。最后，他为母校即将到来的120岁生日献上了自己的祝福——以先辈为榜样，从我做起，脚踏实地，振兴交大。"),r("br"),n._v("\n“历史无法改变，而正是这样的经历让交大拥有了深厚的文化底蕴，作为后辈，我们的职责就是珍惜现在不易的幸福生活，不忘先辈，为了学校的发展贡献出自己的一份力量，尽管绵薄，却是必不可缺。”土木学院辅导员黄雪娇这样对我们说道。竢实扬华的精神应当一直被传承下去，每个交大学子都应该脚踏实地，响应国家新时代的号召，从我做起，努力拼搏，发扬先进思想。"),r("br"),n._v("\n参加了今年暑期唐山三下乡活动的校园大使团团员侯云庚对这部纪录片也有很深的感触：作为一个交大学生，同时也是大使团的一员，他的感受就是一种惋惜。一是惋惜曾经的校园已在历史的长河中烟消云散，二是惋惜曾经的一流学府几经波折才艰难走到今天。他坦言，这部纪录片更多地像一部科普片，它记录了这座早已从唐山消失，但是颠沛流离至今都没有灭亡的大学的故事，也讲述了它为中国作出的巨大贡献。侯同学最后希望能将老唐院的故事以更多形式记录下来，并一代代传承下去。"),r("br"),n._v("\n纵观一路南迁，作为交大人，我们仍不忘故土。对如今身处天府之国的西南交通大学来说，远在北方的唐山永远是它记忆的源头。昔日的一堂弦歌，曾经的桃李芬芳，早已熔铸成文化之魂，传承在每一个交大人心间。")]),n._v(" "),r("table",[r("thead",[r("tr",[r("th")])]),n._v(" "),r("tbody")])])}),[],!1,null,null,null);r.default=_.exports}}]);