(window.webpackJsonp=window.webpackJsonp||[]).push([[141],{505:function(_,v,p){"use strict";p.r(v);var i=p(14),t=Object(i.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img2.ph.126.net_jJl44aLqikcZ2_rYlNNn_g==_6632047430561252405.jpg",alt:""}}),_._v('【原文作者简介】黄寿恒、字镜堂，1896年出生，1914年毕业于唐山交大前身唐山工业专门学校土木工程科，1916年赴美留学，获麻省理工学院土木工程学士及航空工程硕士学位。黄老1923年回母校任数学教授直至退休，为著名的"唐山五老"之一，1968年去世。')]),_._v(" "),v("hr"),_._v(" "),v("p",[_._v("【编者按】黄老擅长诗文，曾手录所作诗词并装订成册，名曰《师子夏》。1940年在贵州平越纪念校庆时，黄老写了一首五言长律，回顾他自1910年起与交大结缘30年的历程：")]),_._v(" "),v("p",[_._v("四岁专科制，规模轫始崇，")]),_._v(" "),v("p",[_._v("声名腾冀北，成绩肇华中。")]),_._v(" "),v("p",[_._v("寝馈思当日，菁我振国风，")]),_._v(" "),v("p",[_._v("分攻路矿学，俱习测量工。")]),_._v(" "),v("p",[_._v("听讲叹幽邃，磋磨语曲通，")]),_._v(" "),v("p",[_._v("心知殊少少，毕业遂匆匆。")]),_._v(" "),v("p",[_._v("九载漂游倦，重来杏李丛，")]),_._v(" "),v("p",[_._v("庭栽犹半熟，师友甚和融。")]),_._v(" "),v("p",[_._v("设备年增益，讲论岁不同，")]),_._v(" "),v("p",[_._v("芳菲齐结实，培养喜收功。")]),_._v(" "),v("p",[_._v("洙泗坛仍在，弦歌诵忽终，")]),_._v(" "),v("p",[_._v("东隅嗟已失，桑梓恨无穷。")]),_._v(" "),v("p",[_._v("倭寇猾华夏，蝦夷据学宫，")]),_._v(" "),v("p",[_._v("图书抛散佚，仪器掷西东。")]),_._v(" "),v("p",[_._v("庭柳迎风绿，池荷过雨红，")]),_._v(" "),v("p",[_._v("驱车由此去，回首罪天公。")]),_._v(" "),v("p",[_._v("复课湘潭畔，避戎黔岭中，")]),_._v(" "),v("p",[_._v("精神异宿昔，物质竟虛空。")]),_._v(" "),v("p",[_._v("建国工程大，同门发愤雄，")]),_._v(" "),v("p",[_._v("匹夫知义愤，岂敢息微躬。")]),_._v(" "),v("p",[_._v('湖南湘潭是唐山交大在抗战时期辗转播迁九年中的第一站，至今已经近80年，交大南迁也使得湘潭有大学的历史前推了20年。本文为黄老70年前所作，记述"复课湘潭畔"的那段往事，是关于交大校史的一篇经典之作。文中提及的"唐山四少"之一许元启教授1920年代曾在沪上亚细亚火油公司供职，将公司为其所购7000元养老金悉数捐出作复校之用。另一位被称为"中国铁路巨擘"的校友杜镇远先生，除了自己慷慨捐资之外，作为北京校友会交际股主任干事，他还致函各地校友，呼吁对母校南迁给予帮助。黄老的两位公子"棠棣之花"，跟随父母南迁时还是少年，在战争年代先后考入交大读书，远隔重洋的兄弟二人也已去年秋天相继谢世。敬录此文，是为纪念。(\n'),v("strong",[_._v("编辑")]),_._v(" ：范明)")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/mmbiz.qlogo.cn_mmbiz_jpg_cgdcv5MCXUaFibxTzq6QqOX7Nebia1iaWtueFvOKS7ZBDw5cOibpArzAwR0wicnAJ2lWa1Dlwfib8d2aeUw37ml7bcQQ_0_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),v("p",[v("strong",[_._v("湘潭复校经过纪事"),v("br"),_._v("\n原文作者／黄寿恒 编辑／范明")])]),_._v(" "),v("p",[v("strong",[_._v("复校经过")])]),_._v(" "),v("p",[_._v('"七·七"骤起，校园沦陷，师生校友咸不忍见本院之历史中断，急谋复学，校友奔走，解囊相助，是年十二月，复校湘潭。')]),_._v(" "),v("p",[_._v("本院院址唐山，当津榆冲要。以往每受战争影响，然仅课务稍有停顿而已，尚无其他损失。自“九·一八”暴日内侵，铁蹄西犯，本院之感受威胁，遂日甚一日。民国二十二年春(1933年)暂迁上海，假交大沪校房舍上课，结束该学期课程。塘沽协定后，唐山院舍收回，于十二月复课。斯时冀东政情复杂，二十四年秋，环境愈益恶化，赖院长孙鸿哲应付得宜，秉其刚德，贤愚共钦，国旗巍然飘扬于本院中央，供冀东全境国民之瞻仰，而本院乃如一块净士，师生于中闭户读书，设备建筑仍得与时增加，新办之矿科亦日臻巩固渐具规模焉。然人怀戒心，假期中亦各留校。迨二十六年日本以文治派近卫组阁，意其能阻武人之横行也，师生乃多于暑期中归里者。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/mmbiz.qlogo.cn_mmbiz_jpg_cgdcv5MCXUaFibxTzq6QqOX7Nebia1iaWtuiaO2f7oeibWR0bcKnj1arJ2nNc2I1LnfhgFw4ribgK1xO7YRUUY5kl5pA_0_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),v("p",[_._v("(上图：交大唐院主校门，下图：校园内飘扬的中华民国国旗)")]),_._v(" "),v("p",[_._v("本院向隶交通部、铁道部，二十六年夏改隶教育部，令未到院而“七七”事变骤起。时院长鸿哲劳郁致病，卧病北平已逾两月。教员学生或在野实习，或回家归省，颇形散漫。而自十八年重行合组交通大学以后，二十年设专任校长，校长黎照寰常驻上海，仓卒无以应变，日寇旋入占院舍，师生四散，本院沦陷矣。")]),_._v(" "),v("p",[_._v("八月(1937年)中日战事延及上海，黎校长乃请南昌市长龚学代为觅寻房屋，以便将交大唐院、平院以及沪院分别或一并迁赣，并于八月二十三日函本院教授黄寿恒由九江迳往南昌，察勘所借房屋是否合用。黄教授察看后于九月二日报告校长，谓市内可借之房屋足供一院之用，同时建议设法借用市外梅岭高中军训地点，作为全校迁赣之临时校址。嗣校长与上海同人谈论，以为南昌地点不甚安全，暂不进行。上海各校旋因战事紧张迁入租界上课。本院则几经在沪教授顾宜孙、李斐英、许元启等与黎校长谈论，未能获得确实办法。")]),_._v(" "),v("p",[_._v("本院开办以来，向著成绩，为校友所爱护，教授又多久任，对于本院自极关怀。九月以后，学生渐集都市，群谋继续其学业，而又不愿向他校借读。师生校友咸不忍见本院之历史中断，同志间书信往还，而复校之运动于以开始。")]),_._v(" "),v("p",[_._v("教授许元启、校友许照在沪，自始即与各方通讯，商讨复课，十月初更与教授李斐英、顾宜孙，学生代表戴根发及校友吴鸿照、郭守先、孙立己等多人集会，议决组织上海及南昌两办事处，筹备本院在赣开学。上海由许照、顾宜孙，戴根发负责，南昌由李中襄(校友)、黄寿恒、许元启负责。许元启教授遂即先行溯江西上，另由校友吴敬恒、过养默及顾教授等转达黎校长，说明需要，恳请饬办。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/mmbiz.qlogo.cn_mmbiz_jpg_cgdcv5MCXUaFibxTzq6QqOX7Nebia1iaWtutwyPkk6n0jnicVmH3n301Zo5s7ibJm7CmKyibWMhZia2s8U1dhAk0uhQiaQ_0_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),v("p",[_._v("(民国年间交大唐院部分教授)")]),_._v(" "),v("p",[_._v("十月杪，许教授抵九江与黄寿恒教授交换意见后，以校友李中襄适在南昌洽得省立工业专科学校原校址，许教授遂先往南昌。先是黄教授闻在赣开学之议，驰书浙赣、湘黔、湘桂各铁路校友，商借测量仪器。书到湘潭，适在湘学生推选代表王小鲁、王秉文等往见校友，推动复校。校友裴益祥、薛兆枢等乃开会商讨，以为军事倥偬之际，本院必须自动筹备开课，以免荒废。遂由湘黔铁路校友会函促黄教授迳往湘潭，筹备开课，并谓如教授不易齐集，湘潭有校友多人愿尽代课义务。黄教授见其计划具体，遂迅以转达许教授与上海顾、李教授及唐山各教授。时校友候家源适过南昌，黄教授乃应其电邀前往与会。")]),_._v(" "),v("p",[_._v("十一月三日，校友侯家源、李中襄及黄、许二教授会于南昌，检讨(一)孙院长于十二月十三在平病故，负责无人：(二)教授之集中；(三)经费；(四)南昌湘潭两地之比较诸问题后，遂一面由校友代表电呈教育部、铁道部，恳请维持本院，明令在赣复课，拨予经费，并聘任院长以专责成。一面由李校友、黄许二教授于四日依照原议，组织本院南昌办事处，通知学生迅即登记，并电促滞在唐山之朱泰信教授设法速来。黄教授以湿疾未愈返浔。筹备开学及洽校址诸事多由许教授李校友办理。")]),_._v(" "),v("p",[_._v("是时战事紧急，上海旋失守，南昌亦感震动。各铁路校友杜镇远等来函，均主张先行复课。湘黔路校友且为在湘潭代觅校址，其在川陕豫鄂湘粤各地学生，则以闻在湘潭可有办法，纷纷函请在湘复课。时上海学生代表吴天佑等六人驶抵南昌办理登记，遂由南昌办事处登报通知，定于十二月十五日在湘潭开学。黄许二教授随即率领学生于十二月一日兼程驶往湘潭，当夜到达。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/mmbiz.qlogo.cn_mmbiz_jpg_cgdcv5MCXUaFibxTzq6QqOX7Nebia1iaWtumBvTPmWnV4vKCUMD86vFykNic42ibKibKkgpiaNl1Yib3YCgJAsjYhXX4NA_0_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),v("p",[_._v("(湘潭钱家巷临时校舍)")]),_._v(" "),v("p",[_._v("十二月三日，本院教授校友代表举行联席会议于湘潭，即租定钱家巷西式住宅一所作课室，并借得窑湾礼拜堂一处作学生宿舍，敦聘富有学问经验人士充当临时教授。经校友薛兆枢、庆承道、张鸿逵、裴益祥等诚恳代洽，其先后担任课目义务供职者，有李钟美、吴汝康，余育德、唐民、徐功懋先生等五人，王君礼、裴益祥、薛兆枢、黄寿益、汪菊潜、庆承道、王世祯、邓淦祥、吴鸿训、李温平校友等十人，他们授课始终不懈，至堪钦敬，亦以吾院以往所表现之成绩，有以招致有心者之爱护也。至于临时费用，则由校友发起捐募复校基金，于是积极筹备开学。上海学生戴根发等多人，获浙赣路校友王节尧等帮助，兴奋赶到。朱泰信教授自十一月初在唐山闻电向罗、伍教授征求意见后即行设法南来，沿途与教授校友接洽，于十二月十四日到达湘潭。十二月十五日，本院在湘潭钱家巷临时校址，如期举行开学典礼，十六日各年级正式上课。惜矿冶系高年级课目无人教授，未能开班。此本院在湘潭复课时之最初情形也。")]),_._v(" "),v("p",[_._v("复课之日，教授范治纶已起程由汉口来湘潭，旋即到校，师生鼓舞。时有教育部迁在长沙办公之说，黄、朱、许三教授乃往长沙接洽，仅获见高等教育司长黄建中，未得要领。于是经与校友侯家源等会商后，一面加紧捐募复校基金，由黄教授前往衡阳向该地校友说明复校经过；一面推举朱泰信教授为本院教授代表，前往汉口向教育部、交通部请求遴选院长，拨予经费。至院中大小事务，则由许教授计划经理，并井井有条，而校友师生之合作有足助者。")]),_._v(" "),v("p",[_._v("二十七年一月初，朱教授在汉口联合校友茅以升、倪钟澄等向部方剀切陈说，既而获得谅解。二月奉黎校长来电，聘罗忠忱教授为本院院长，师生为之欢忭。复以罗教授滞在唐山一时不能前来，便由黎校长阳电敦请茅以升校友代理。茅教授于二月十一日在师生校友欢迎中到院就职。茅院长于处理邀请教员诸事后，即往汉口接洽经费，更与湖南大学商定两校矿冶系合作及该系学生借地上课办法，至三月杪回院。适在院奉部令，交通大学北平管理学院暂行并入本院，设铁道管理系，乃续招新生，更在湘乡县杨家滩租赁校舍。经筹备后于五月二十三日迁在杨家滩上课，新生七十七人同时前往。时教授学生已相继齐集，罗院长与教授伍镜湖旋亦由唐山来院。罗院长耽于任教，辞不就一职，遂由黎校长聘茅代理院长任本院院长，又以校长在沪，文件来往不便，呈由教育部令本院直接向部行文，于是茅院长多方罗致矿冶教授以便开班。同年秋长沙大火，本院奉令西迁，道经桂林，遭敌机轰炸，损失甚重，赖茅院长经营擘划，始得于二十八年二月迁抵贵州平越。土木，矿冶、及管理各年级全体上课。本院复校至是可谓完成。虽设备非复旧观，而久任教员则多数留校焉。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/mmbiz.qlogo.cn_mmbiz_jpg_cgdcv5MCXUaFibxTzq6QqOX7Nebia1iaWtuia3NF6j3dicFZA5clCuTdzlRm2rpQVO18q3fAnSclte4Kx01Ih5Oicicibg_0_wx_fmt=jpeg.jpg",alt:""}})]),_._v(" "),v("p",[_._v("(湘潭旧时风光)")]),_._v(" "),v("p",[_._v("本院沦陷之初，时值孙院长病重，师生四散，卒难集中，而经费又丝毫无着，教育停顿者近半年。卒赖校友之力，得于二十六年十二月十五日在湘潭复课。向部呼吁而外，或担任授课，或代邀教员，一一义务供职，更于战乱艰难之中踊跃捐输，以供母校之用。共捐集一万二千余元，超出预期额一倍。惟款系陆续汇来，计迄二月十一日院长就职时止，仅垫支校用约二千元。迄三月底止，亦仅共垫用四千余元，当时师生之刻苦可知。维时师生环围竹桌，言笑会餐，学生更匍匐地铺，勤勉制图，课程进行一如平日。坚忍耐苦之合作精神，至今回忆犹觉可贵。")]),_._v(" "),v("p",[_._v("今时逾八载，茅院长及朱许诸君又已离校，而抗战复员后，教授有在陈之忧，全院兴无煤之叹。工厂空虚，设备荡然，前途艰辛更有甚于昔日者。爰编记复校经过，亦已见困难之终须克服，而愉快不在享受，乃由努力焉耳。惟讹误遗漏之处在所不免，事关院史，祈阅者指正。至当时推动奔走募捐者有：侯家源、杜镇远、吴土恩、徐世雄、裴益祥、吴祥棋、连浚、史翼、邵福宸，王节尧、邵鼎汾、宋福祺、江为三、查良铸、李俨、王之镐、邵从桑、冯雄、薛兆枢、张竟成校友等，记忆所及，顺以附载。")]),_._v(" "),v("p",[_._v("三十五年十二月十日")]),_._v(" "),v("p",[_._v("黄寿恒识于唐山母校")]),_._v(" "),v("p",[v("strong",[_._v("资料来源")]),_._v(" ：西南交大《校史资料选辑》第一辑 (1996年)，选自《唐院月刊》第四期 (1946年)。文中图片为编者所添加，均来自网络。")])])}),[],!1,null,null,null);v.default=t.exports}}]);