(window.webpackJsonp=window.webpackJsonp||[]).push([[181],{545:function(p,t,_){"use strict";_.r(t);var v=_(14),s=Object(v.a)({},(function(){var p=this,t=p._self._c;return t("ContentSlotsDistributor",{attrs:{"slot-key":p.$parent.slotKey}},[t("hr"),p._v(" "),t("p",[p._v("【上海校友会供稿】")]),p._v(" "),t("p",[p._v("交融四海通天下，思源致远共方圆。2013年5月18日，第七届交通大学全球校友商界领袖峰会在北京交通大学隆重开幕。本届峰会由交通大学北美校友会和北京交通大学主办，上海交通大学、西安交通大学、西南交通大学、新竹交通大学协办。上海交通大学、西安交通大学、西南交通大学、北京交通大学和新竹交通大学五所交大校领导，海峡两岸、世界各地的300多名交大商界校友齐聚一堂，共襄盛举。")]),p._v(" "),t("p",[t("img",{attrs:{src:"/pic/img0.ph.126.net_WmyyQzAmi0ITec9FOLh46w==_6597314957750467980.jpg",alt:""}})]),p._v(" "),t("p",[p._v("国家发改委城市和小城镇改革发展中心主任李铁，世界银行前首席经济学家兼高级副行长、全国政协常委、北京大学国家发展研究院名誉院长、北京交通大学中国产业安全研究中心学术委员会主任林毅夫，交通大学北美校友会会长马开桂，上海交通大学党委书记马德秀、副校长徐飞，西安交通大学副校长卢天健，西南交通大学校长陈春阳、副校长蒲云，北京交通大学党委书记曹国永、校长宁滨、党委副书记高艳、副校长刘军，新竹交通大学校长吴妍华、副校长黄志彬等校领导参加了此次盛会。")]),p._v(" "),t("p",[t("img",{attrs:{src:"/pic/img2.ph.126.net_cad15sAvQPWQxxFH6WObzQ==_3171660037675752764.jpg",alt:""}})]),p._v(" "),t("p",[p._v("5月18日上午，大会开幕式在北京交通大学天佑会堂举行。北京交通大学党委书记曹国永致欢迎词，国家发改委城市和小城镇改革发展中心主任李铁与交通大学北美校友会会长马开桂先后致辞，对大会的成功举办表示祝贺。商界校友领袖代表、全国工商联副主席李河君，联电荣誉副董事长宣明智，中国电信技术创新中心主任毕奇先后做精彩发言，表达了商界校友对各母校的真挚感谢和美好祝愿。")]),p._v(" "),t("p",[p._v("紧接开幕式，举行了以“构建协同创新机制，实现校企共同发展”为主题的五所交大校长论坛。本次论坛由中国教育电视台新闻中心首席主持人荆慕瑶主持。伴随2012年“高等学校创新能力提升计划”（“2011计划”）的实施，协同创新正式被提升到国家战略的地位。上海交通大学党委书记马德秀、西安交通大学副校长卢天健、我校校长陈春阳、北京交通大学校长宁滨、台湾新竹交通大学校长吴妍华在论坛上共同探讨“2011计划”实施以来，高校作为科技第一生产力和人才第一资源的重要结合点，通过创新发展观念，服务国家重大需求、实现校企合作共赢的创新理念和具体实践。")]),p._v(" "),t("p",[t("img",{attrs:{src:"/pic/img2.ph.126.net_vqyLZ67DWAsibHQsAdQgTg==_1671398411807955857.jpg",alt:""}})]),p._v(" "),t("p",[p._v("此次峰会之前，适逢由西南交通大学和北京交通大学联合牵头，中南大学重点参与的轨道交通安全协同创新中心已首批被认定为14个2012年度“2011协同创新中心”之一，我校校长陈春阳、北京交通大学校长宁滨特别分享了两校在协同创新、联合申报中的成功经验。")]),p._v(" "),t("p",[t("img",{attrs:{src:"/pic/img2.ph.126.net_qQjsyBUcpRVQy51neIjVuQ==_291608075972320751.jpg",alt:""}})]),p._v(" "),t("p",[p._v("校长论坛结束后，世界银行前首席经济学家兼高级副行长、全国政协常委林毅夫为全场带来精彩学术报告。他分析了近年来的世界经济形势，针对中国的经济发展前景和巨大潜力作了充分阐述。")]),p._v(" "),t("p",[t("img",{attrs:{src:"/pic/img0.ph.126.net_23dUuq4UrQYR0yshUkwSpw==_3274398404175112632.jpg",alt:""}})]),p._v(" "),t("p",[p._v("当天下午，举办了“稳中求变、深化转型：大数据时代的科技金融创新”、“新能源、新方向，全面引领绿色发展”、“区域协调，以人为本，城镇化背景下交通运输行业发展新局面”、“高端\n高效 高速\n聚焦生物产业和新信息技术的多元化成长”等四个主题论坛，我校校友广州中安科技发展有限公司董事长卫中安、广西自治区发改委副主任廖小波、中铁西北科学研究院有限公司总经理朱本珍作为分论坛特邀嘉宾探讨了相关领域的前沿技术与发展等问题。")]),p._v(" "),t("p",[p._v("与此同时，举行了“交通大学战略发展研讨会”，与会各校领导就五所母校的交流合作与发展进行了商讨。会后，各校领导试乘由北京交通大学参与研发的电动汽车，正式启动了北京交通大学“百辆电动汽车示范运行”项目。随后，五校校领导来到芳华园为12年前植下的交通大学“同根树”培土浇水，表达对交通大学再续辉煌的美好祝愿。")]),p._v(" "),t("p",[t("img",{attrs:{src:"/pic/img2.ph.126.net_CVHhtEX3y6leSmcmMbdAPw==_6598222054843674179.jpg",alt:""}})]),p._v(" "),t("p",[p._v("各母校校友见面会也在各分会场举行。我校蒲云副校长与我校参加峰会的校友进行了亲切座谈。蒲云副校长用生动的事例、精彩的讲解向在座校友展示了母校发展近况，赢得了校友们的阵阵掌声。大家纷纷就各自关心的问题与母校领导进行了深入探讨。在座校友期待能与母校开展协作创新、共同发展，祝愿母校早日实现建设具有交通特色的多学科协调发展的高水平研究型大学的发展目标。我校校友工作办公室主任战凤主持座谈。")]),p._v(" "),t("p",[t("img",{attrs:{src:"/pic/img1.ph.126.net_EItoz5tMq_sSMoxP0pargQ==_6597815235541616965.jpg",alt:""}})]),p._v(" "),t("p",[p._v("5月16日至17日，还举行了为期两天的“交通大学商界领袖大讲堂”大型校友讲座。受邀主讲嘉宾、我校校友广西建工集团董事长金宁运、博文国际株式会社董事长刘修博面向在校学生分别作了题为“努力与作为”、“谈谈外语教育、职业培训及中日关系”主题讲座，通过讲述各自的奋斗历程、创业体会和人生感悟，帮助在校大学生更深入地了解社会和行业发展，解答学生成才、求职和择业等方面的困惑，激励大学生努力学习成长成才，获得了很好的反响。")]),p._v(" "),t("p",[p._v("交通大学全球校友商界领袖峰会由交通大学北美校友会与五所交通大学共同倡议举办，每年由北美校友会与一所交通大学轮流主办，是海峡两岸五所交大联袂打造的一大品牌活动，自2003年以来，峰会已成功举办了六届，其中2009年由我校主办。交通大学全球校友商界领袖峰会是海峡两岸五所交大交流联谊的重要契机，为五所交大同根同源的重要见证，为促进国际间和地区间校友的交流合作做出了卓越贡献，也为海峡两岸高校及校友间的交流搭建了有效平台。")])])}),[],!1,null,null,null);t.default=s.exports}}]);