(window.webpackJsonp=window.webpackJsonp||[]).push([[195],{557:function(t,n,_){"use strict";_.r(n);var r=_(14),e=Object(r.a)({},(function(){var t=this,n=t._self._c;return n("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[n("hr"),t._v(" "),n("p",[t._v("【注】劳远琇教授系我校桥梁专家劳远昌教授胞姐。"),n("br"),t._v("\n|  |  |  |  |"),n("br"),t._v("\n沉痛悼念劳远琇教授"),n("br"),t._v("\n（来源：http://www.cjo-eye.com/index.php?c=defaults&m=news&id=313）")]),t._v(" "),n("p",[t._v("我国杰出的临床医学家、医学科学家和教育家，中国现代神经视野学奠基人，北京协和医院眼科劳远琇教授因病医治无效，于2013年3月24日在北京逝世，享年94岁。")]),t._v(" "),n("p",[n("img",{attrs:{src:"/pic/img1.ph.126.net_K1yNxR_uI1Hv9nDF8cpWXQ==_6597714080471671327.jpg",alt:""}})]),t._v(" "),n("p",[t._v("劳远琇教授1919年出生于湖南长沙一个中学教师家庭。1944年毕业于湘雅医学院，获医学博士学位。1949年获得美国大学妇女协会奖学金及中美文化基金会奖学金赴美留学，毕业于宾夕法尼亚大学研究生院。抗美援朝期间，她历尽艰辛，克服重重困难回到北京，成为新中国成立后，北京协和医院第一位全职眼科医生。"),n("br"),t._v("\n劳远琇教授在眼科的临床工作、基础研究及医学教育领域辛勤耕耘60余年，涉猎广泛，贡献卓著，建树颇丰，曾多年兼任国家部委、眼科学会的重要职务，主持或参与过多项科研工作，发表了数十篇具有国际领先水平的论著，在医学界特别是眼科界享有很高的声誉。1954年在协和医院创建全国唯一的“神经视野学”专业组，多年来为国家培养了大批视野专业人才；1978年编著并修订再版的《临床视野学》一书在全国卫生医药科学大会上获奖，填补了我国这个领域的空白；1990年获国家教委颁发的“为高校教育及科技事业工作四十年成绩显著”奖状；1991年她参加的医院“激素分泌性垂体瘤的临床与基础研究”工作，获卫生部科技进步一等奖，1992年获国家科技进步一等奖；自1991年起享受国务院特殊津贴；2006年获协和医院颁发的“卓越贡献奖”；2009年获协和医院颁发的“杰出贡献奖”。"),n("br"),t._v("\n在长达六十余年的从医生涯中，劳远琇教授不仅因其深厚的学术造诣令人高山仰止，同时也以其高尚的人格魅力让人由衷地敬佩与折服，她的学生、她的同事、她的下级乃至所有与她有过接触、交往的人，对她都有着一致的评价：和蔼可亲、平易近人、淡泊名利、心胸宽广。劳远琇教授的一生，“其仁宽也。爱国利民，敬业乐群，流芳百世；斯惠广矣！行医布药，救死扶伤，造福四方”。"),n("br"),t._v("\n劳远琇教授用平凡的脚步，走出了不悔的人生！劳教授一路走好！")]),t._v(" "),n("p",[t._v("中国医学科学院北京协和医学院 北京协和医院眼科")]),t._v(" "),n("table",[n("thead",[n("tr",[n("th")])]),t._v(" "),n("tbody")])])}),[],!1,null,null,null);n.default=e.exports}}]);