(window.webpackJsonp=window.webpackJsonp||[]).push([[233],{596:function(t,n,r){"use strict";r.r(n);var e=r(14),s=Object(e.a)({},(function(){var t=this,n=t._self._c;return n("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[n("hr"),t._v(" "),n("p",[t._v("|  |  |  |  |  **"),n("br"),t._v("\n“我们的青春我们的厂”"),n("br"),t._v("\n图文／燕赵都市报冀东版记者闫漪 李金花"),n("br"),t._v("\n（来源:"),n("a",{attrs:{href:"http://ts.yanzhen.yzdsb.com.cn/thread-40370-1-1.html",target:"_blank",rel:"noopener noreferrer"}},[t._v("http://ts.yanzhen.yzdsb.com.cn/thread-40370-1-1.html"),n("OutboundLink")],1),t._v("）"),n("br"),t._v("\n**")]),t._v(" "),n("p",[n("img",{attrs:{src:"http://www.yzdsb.com.cn/pic/0/11/27/24/11272410_138154.jpg",alt:""}})]),t._v(" "),n("p",[n("img",{attrs:{src:"http://www.yzdsb.com.cn/pic/0/11/27/24/11272411_908047.jpg",alt:""}})]),t._v(" "),n("p",[t._v("从中国之最到世界之最，唐车人经历了百余年的努力和数辈人的探索，无数人为它的发展献出了青春。"),n("br"),t._v("\n原工厂副总工程师、新厂建设指挥部总指挥———现年77岁的金涵淼将人生中最美好的30多年留给了唐车。接受记者采访时，金老特意穿了红得耀眼的上衣，衬着银白的头发，以最特别的方式诠释了唐车的整个历史：古远而又透着勃勃生机。"),n("br"),t._v("\n1969年11月，金涵淼进入唐车工作，直到1998年退休。毕业于唐山铁道学院铁道建筑系的他曾在哈尔滨铁路局工作过，并很快就成为了南厂的技术骨干。作为唐山机车车辆厂的高级工程师。"),n("br"),t._v("\n1976年，唐山大地震摧毁了开滦、唐车等百年老企。1979年至1990年，国家将唐车异地重建纳入“六五”、“七五”规划，在距离原厂址以北30公里的丰润区，投资3.5亿元建设一个占地面积达123万平方米的新厂。在新厂规划设计和建设中，金涵淼主持完成了全场123万平方米的总图、运输施工图的设计，并组织实施。1986年，新唐车一期工程竣工———客车新造业务全部迁到新厂生产。"),n("br"),t._v("\n“新厂建设的同时，老厂生产急需恢复锅炉房，我当时不仅要参与新厂筹建、规划，还要在老长原锅炉房废墟上搞设计，白天画设计图，晚上在家搞结构设计，一个多月的时间完成设计，为老厂生产生活供热供暖创造条件！”年逾古稀的金涵淼老人对此自豪不已。1990年，新唐车二期工程全部竣工并通过国家验收，这个重点工程经过国家审计署审计获得“A”级信誉，生活3小区荣获全国银质奖，而金涵淼作为审计建设者代表在中南海受到了党中央国务院领导同志的亲切接见。\n对于金老来说，唐车给了他实现梦想的舞台，也是他一生的牵挂。即使在退休后的十多年里，金老依然是唐车工程技术问题的“智囊”。在金老看来，唐车早已成了他生命的一部分。"),n("br"),t._v("\n（感谢网友老陶成局提供报道信息）")]),t._v(" "),n("table",[n("thead",[n("tr",[n("th")])]),t._v(" "),n("tbody")])])}),[],!1,null,null,null);n.default=s.exports}}]);