(window.webpackJsonp=window.webpackJsonp||[]).push([[243],{607:function(_,v,p){"use strict";p.r(v);var t=p(14),r=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[_._v("|  |  |  |  |")]),_._v(" "),v("p",[_._v("茅以升设计的“桥坚强”超寿命 就是不倒")]),_._v(" "),v("p",[v("strong",[_._v("来源／2013-02-09互联网资讯")])]),_._v(" "),v("p",[v("strong",[_._v("作者：王学进")])]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img8.ph.126.net_P9oKvBLzKB55KXAmUKWyRw==_6597769056052404418.jpg",alt:""}})]),_._v(" "),v("p",[_._v("茅以升设计的钱江大桥耗资160万美元，设计寿命50年，已经超期24年，74年就是不倒！")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img8.ph.126.net_ilrVw64ExJ7oFYMNDUvxIA==_6598158283168411638.jpg",alt:""}})]),_._v(" "),v("p",[_._v("1997年1月28日投资高达6个亿的钱江三桥通车。14年后，2011年7月发生引桥坍塌事故。")]),_._v(" "),v("p",[_._v("迄今为止，杭州钱塘江上已经建成了9座大桥，一座比一座建得漂亮，但论质量，谁也不敢打包票，后建的桥一定比它们的老大哥———茅以升主持设计的钱江大桥牢固。这些天，随着钱江三桥发生引桥坍塌事故后，网友将关注的目光聚焦到钱江大桥身上，发现大桥建成74年来任凭风吹浪打，岿然不动，被网民热捧为“桥坚强”。网友发帖问：茅以升修的这座桥，设计寿命50年，已经超期24年，就是不倒，连大修都没修过，这是神马道理?(2011年7月24日《新京报》)")]),_._v(" "),v("p",[_._v("论设计能力、施工技术、材料质量，茅以升他们建钱江大桥时根本无法与今天相提并论，至于投资，钱江大桥耗资160万美元(未炸毁之前的建桥资金)，而钱江三桥投资高达6个亿，两者更没有可比性，但建造质量何以差距如此之大呢?答案已经由一网友给出了：过去的人实在，按部就班，科学施工，不搞献礼工程，不搞大跃进，不沽名钓誉，不偷工减料，所建工程焉得不牢固?")]),_._v(" "),v("p",[_._v("此话说对了一半，其实这中间还隐含着另一层更深刻的含义，恰如网友所说的那样：我觉得道德才是第一生产力，道德是社会运行的“万有引力”，它看似不存在，实际上无处不在!它对社会的作用力总和任何其他力量不可相比!诚哉斯言!")]),_._v(" "),v("p",[_._v("钱江大桥之所以能在风雨中屹立74年，就因为建桥者们用他们高尚的道德情操、无与伦比的敬业精神和强烈的爱国主义精神为大桥奠定了精神基础和灵魂底色。桥身不倒，首先是桥魂犹在。我查阅了有关钱江大桥的建设资料，震惊于以茅以升为代表的建设者们克服种种困难，采用“射水法”、“沉箱法”、“浮远法”等，解决了建桥中的一个个技术难题的创新精神，更敬佩茅以升一丝不苟的工作精神：茅以升对建桥的每一道工序，都极尽苛刻，大到钢梁的架设，小到每一颗螺丝钉都有严格的检查程序。")]),_._v(" "),v("p",[_._v("2008年7月21日《衢州日报》曾报道过时任钱江大桥监工的姜时俊的事迹，他1935年大学毕业后，被学校直接安排到钱塘江大桥工程处，在茅以升的直接领导下担任监工一职，具体负责技术管理工作。因为工作出色，廉洁自律，深受茅以升赏识。抗战爆发后，姜时俊辗转桂、湘、黔、粤，先后主持了湘桂铁路、桂穗公路、湘桂公路等国内交通主干线的建设，从未有渎职事件发生。据此可知，当时的建设者们是何等敬业和廉洁，哪像如今，钱江三桥建成不久，三桥总指挥、副总指挥因受贿就双双落马了。这就是道德的力量!建桥人一旦缺失了道德，哪怕技术再先进，也建不成钱江大桥这样的“桥坚强”!")]),_._v(" "),v("p",[_._v("如今，茅以升1975年赠送给浙江省档案馆的钱塘江大桥工程技术档案，已于2000年被列入全国首批48件《中国档案文献遗产名录》之一，在为今人、后人提供珍贵的建桥史料和经验，我希望今天的建桥者们不但能从中汲取技术养料，更能从中收获老一辈建桥者们公而忘私、公正廉明、敬业奉献的道德力量和精神品格，将建桥事业当做百年大计而非政绩工程来从事经营。(齐鲁晚报)")]),_._v(" "),v("p",[_._v("【附1】 "),v("strong",[_._v("茅以升生平 ：")])]),_._v(" "),v("p",[_._v("(1896年1月9日-1989年11月12日)，字唐臣，江苏镇江人，结构工程师，桥梁工程专家。中国近代桥梁事业的先驱、铁道科技事业的开拓者，同时也是中国土力学的开拓者、科普工作者。")]),_._v(" "),v("p",[_._v("简历")]),_._v(" "),v("p",[_._v("1916年毕业于唐山工业专门学校(今西南交通大学)。参加清华学堂(清华大学前身)留美官费研究生考试，以第一名录取留洋;")]),_._v(" "),v("p",[_._v("1917年获美国康乃尔大学硕士学位(桥梁专业);")]),_._v(" "),v("p",[_._v("1919年获美国卡耐基理工学院(现为卡内基梅隆大学)博士学位，是该校的第一位博士。")]),_._v(" "),v("p",[_._v("1920年回国后至1949年，曾任国立东南大学教授、工科主任、南京河海工科大学校长、北洋工学院院长及北洋大学(今天津大学)校长、交通大学唐山工程学院院长;1930年至1931年任江苏水利局局长，1934年至1937年任浙江省钱塘江桥工程处处长(直到1949年中华民国政府迁至台湾止)，在自然条件比较复杂的钱塘江上主持设计、组织修建了一座全长1453米，基础深达47.8米的双层公路铁路两用钱塘江大桥。大桥于1937年9月26日建成通车，这是中国人自己设计和施工的第一座现代钢铁大桥，是中国桥梁工程史上一座不朽的丰碑。")]),_._v(" "),v("p",[_._v("1937年12月23日，为了阻止日军攻打杭州，茅以升亲自参与了炸桥。抗日战争胜利以后，茅以升又受命组织修复大桥，1948年3月，大桥修复通车;")]),_._v(" "),v("p",[_._v("1942年至1943年任交通部桥梁设计工程处处长;")]),_._v(" "),v("p",[_._v("1943年至1949年任中国桥梁公司总经理;")]),_._v(" "),v("p",[_._v("1943年当选中华民国民教育育部部聘教授;")]),_._v(" "),v("p",[_._v("1948年当选中华民国中央研究院院士;")]),_._v(" "),v("p",[_._v("1949年10月1日中华人民共和国建国，1949年至1952年任中国交通大学、北方交通大学校长;")]),_._v(" "),v("p",[_._v("1951年至1981年任铁道技术研究所所长、铁道科学研究院院长;")]),_._v(" "),v("p",[_._v("1955年选聘为国家科学院院士(学部委员);")]),_._v(" "),v("p",[_._v("1959年在北京十大建筑的建设中，担任人民大会堂结构审查组组长，并被总理周恩来指定为设计方案审定人;")]),_._v(" "),v("p",[_._v("1955至1957年主持设计了武汉长江大桥;")]),_._v(" "),v("p",[_._v("1977年主持设计了重庆石板坡长江大桥;")]),_._v(" "),v("p",[_._v("1982年当选美国国家工程院外籍院士。")]),_._v(" "),v("p",[_._v("1989年11月12日去世，享年93岁。")]),_._v(" "),v("p",[_._v("【附2】 "),v("strong",[_._v("姜时俊生平")]),_._v(" ：")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://history.seu.edu.cn/picture/article/49/7e4438a7-5b45-4131-b6f4-ebdb06434610/02f32a8c-8bb7-48ed-9739-b8a948cece8e.jpg",alt:""}})]),_._v(" "),v("p",[_._v("姜时俊，1911年6月9日出生在浙江省西南部的江山县新塘边镇新街村一个农民家庭。早年刻苦勤学，博览群书，尤其爱好文史诗词。")]),_._v(" "),v("p",[_._v("1928年从衢州省立八中（初中）毕业，报考浙江省立七中（今金华一中）高中文科，却被录取为理科新生。1931年高中毕业，先后去上海、杭州报考中央大学土木工程系和浙江大学电机系，均被录取。")]),_._v(" "),v("p",[_._v("1931年9月，他赴南京中央大学（今东南大学）就读，立下誓愿：“联袂同学白下城，龙盘虎踞举世名。秦淮寂寂千年事，扬子茫茫万里行。遥望云天怀壮志，静思报国毓专精。兴衰自古由人力，甘作开山筑路兵。”")]),_._v(" "),v("p",[_._v("1935年夏，中央大学毕业。留学美国、获博士学位。时任南京大学教授的茅以升、罗英对他们说：你们的职业有三种选择，一是国民党国防部要人，到那里去每月360块大洋（银元）；二是我俩以后不教书了，去建桥，若跟我们去建钱塘江大桥，每月60块大洋；三是自谋出路。姜时俊抛弃高官厚禄经校方介绍，选择到钱塘江大桥工程处。在著名桥梁专家茅以升等前辈的直接领导下，任施工员。从此，走上道路建筑的艰辛之路，实现自己“甘作开山筑路兵”的誓愿。")]),_._v(" "),v("p",[_._v("钱塘江大桥是著名桥梁专家、中国20世纪桥梁之父茅以升为首的中国道桥专家自己设计、自己组织施工的我国第一座公路铁路两用桥。正桥钢梁16孔，总计重量4165吨，每节钢梁260多吨，其时，没有起重机械，如何把巨大的钢梁，装上几十米高的桥墩，茅以升把此课题交给了姜时俊。姜时俊组织有关技术人员，共同研究，通过查阅钱塘江水文资料，应用钱塘江潮汐涨落规律，把钢梁准确放置在船架上。施工人员各就各位，潮水涨起，将船体定位，抢在潮水回落前，把全部钢梁安装完毕。在当时没有巨型起重机械的情况下，把如此重型钢梁安装到位，确属不易。江山市档案馆至今保存着姜时俊其时记述的，钱塘江大桥施工过程的档案材料，客观、真实地记录大桥设计施工过程。载体为纸质档案、图纸档案、照片档案，字体为中文、英文。其主要内容有大桥钢梁安装施工记录，施工报告；钢筋混凝土沉箱设计计算材料；工程摄影照片、图片。其中大桥钢梁施工记录、施工报告，是当时姜时俊直接负责正桥钢梁安装监工时，按记录撰写的。它详细记载了钢梁自1936年1月4日开始布置安装工场，到1937年9月20日竣工的全过程。4165吨重的正桥钢梁，按16孔安装的先后次序，清楚地记载着桥梁材料场的安排，材料的卸车、整理、点验、修理，钢梁铆合时遇到的困难，栈道的建筑，钢梁的移动升高，并重点记录了如何艰难地运用钱塘江复杂多变的水位，采用浮船安装的经过，各方面的配合等，关键之处，配以照片加以介绍。这些档案生动地再现70多年前大桥建设者，在没有现代化安装设备的情况下，克服千难万阻发挥人的聪明才智，利用自然的力量，创造人间奇迹的全过程。大桥建成后姜时俊赋诗一首。诗曰：“板桩围堰建成功，铁打江山堪称雄。“三怪”齐临同作祟，龙王收进水晶宫。”（诗中的三怪是指洪水、怒潮、流沙）这首诗，是当年钱塘江修建大桥时恶劣施工条件的真实写照。")]),_._v(" "),v("p",[_._v("抗日战争爆发后，姜时俊辗转桂、湘、黔、粤等省施工、修路。抗战胜利后，调至国民政府交通部第八区公路工程管理局（在北平），后又调到昆明交通部第四区公路工程管理局，任工程师兼技术组长；1948年调昆明至洛川公路工程处，任副总工程师。1949年12月云南和平解放，先任碧河公路工程处副处长兼桥工总队长，后继调中央人民政府交通部第一设计分院（在重庆），任副总工程师。在西南各省边疆地区徒步奔波，勘测设计国防、边防公路5000余公里。西双版纳的热带林区，横断山脉的陡坡峡谷，青藏高原上二郎山、拉萨、日喀则，到喜马拉雅山麓的定日和聂拉木，到处都留下他的足迹和汗水。")]),_._v(" "),v("p",[_._v("1958年年初，姜时俊奉调东北沈阳，任交通部第四设计分院副总工程师；同年秋又随建制支援内蒙古建设，任内蒙古自治区交通厅总工程师。此一职达25年之久，1983年后改任顾问，直至1987年退休。任职期间，他曾遍走全区公路，改善路面，设计新的公路线，勘定区内下城湾、包头、喇嘛湾、乌海四座黄河公路大桥。每造一桥，他都亲临第一线，在工地上与施工人员同吃同住，督促检查工程进度和质量。")]),_._v(" "),v("p",[_._v("1961至1980年近20年间，姜时俊先后10次被国家交通部抽调，参加我国援外路桥建设专家组，担任项目专家组组长，总工程师。足迹远涉尼泊尔、巴基斯坦、坦桑尼亚、赞比亚、南也门、北也门、马达加斯加等10多个国家。")]),_._v(" "),v("p",[_._v("姜时俊于1933年参加中国工程师协会；1936年参加中国土木工程学会，解放后曾任中国土木工程学会第三届理事。1978年中国公路学会成立，他被推为第一、二届理事。1990年中国公路学会授予他荣誉理事称号。")]),_._v(" "),v("p",[_._v("2003年11月24日，为交通事业奔波一生的姜总，心脏停止了跳动。")]),_._v(" "),v("p",[_._v("（【附2】系本博主编摘自《中国档案》杂志 2010年第三期 作者：姜志书）")]),_._v(" "),v("table",[v("thead",[v("tr",[v("th")])]),_._v(" "),v("tbody")])])}),[],!1,null,null,null);v.default=r.exports}}]);