(window.webpackJsonp=window.webpackJsonp||[]).push([[252],{617:function(a,r,n){"use strict";n.r(r);var t=n(14),s=Object(t.a)({},(function(){var a=this,r=a._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":a.$parent.slotKey}},[r("hr"),a._v(" "),r("p",[a._v("**春风十里翡翠屏"),r("br"),a._v("\n——Stockholm的国家城市公园"),r("br"),a._v("\n图文／范明"),r("br"),a._v("\n**")]),a._v(" "),r("p",[a._v("位于Stockholm中心地带的Kunliga Nationalstadsparken于1995年正式命名，又称为Ekoparken\n(环保公园)，是世界上第一个、也是瑞典唯一的国家城市公园。整个公园从西北至东南绵延十公里、总面积达27平方公里，由绿地、森林、山丘、海湾、湖泊、岛屿组成，皇家宫殿、历史建筑、博物馆园座落其间。公园里的动植物资源十分丰富，有100多种鸟类、大约1000种蝴蝶、1200多种甲壳虫，还有800多种各类花卉。虽然公园刚刚命名11年，但历史却很悠久。大约纪元前2300-1800年间的石器时代末期，这片土地从海洋里升起，Ulriksdal出土的石斧即是人类在此狩猎和捕鱼的遗迹。纪元前1800-600年的青铜时代，海岸比现在高15米左右，此地曾发现这一时期的人类墓穴。直到纪元前400年至公元1050年的铁器时代，人类才开始在这里永久居住并从事农耕生产。而在公元800-1050年间的Viking北欧海盗时代，整个大Stockholm人口激增，这一地区已经出现了十多个村舍和农庄。13世纪中期Stockholm成为商业中心并由老城向周边扩展，中世纪的几百年中这里陆续出现了一些教堂。16世纪初期Gustav\nVasa实行宗教改革，这一带随即成为皇家领地及猎场，18和19世纪间兴建了多处宫殿、城堡和皇家园林。山水相依、风景如画，一个国际大都会里集中了如此之多的自然景观和文化遗存，在全世界恐怕都不多见，真是斯京心脏一块碧绿晶莹的翡翠。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/211d7aee9224abd2d562fa92df7610ae.jpg",alt:""}})]),a._v(" "),r("p",[a._v("**"),r("br"),a._v("\n**")]),a._v(" "),r("p",[r("strong",[a._v('波罗的海海湾旁的"皇家禁苑"')])]),a._v(" "),r("p",[a._v("Edsviken和Brunnsviken是两条伸\nStockholm近郊和城内的波罗的海海湾，前者长7.7公里、后者长3.5公里。在这亦湖亦海的水域，夏天清波荡漾可泛舟、冬天冰冻三尺可滑湖。1988年之后这两条海湾之间的32公顷土地成为自然保护区，其中包括瑞典最北面的山毛榉林、最大的史前墓葬群以及古老的界碑。从17世纪开始，在两条海湾旁陆续建造了一系列皇家宫殿和园林，除了王储Victoria一家的居所Haga\nslott之外，所有宫殿园林一律对公众开放。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/4ba46223c2816316fe704f34957e00a8.jpg",alt:""}})]),a._v(" "),r("p",[a._v('Edsviken湾旁的Ulriksdals slott\n(图一)是一座始建于1638-1645年间的皇家宫殿和园林，得名于17世纪号称"小拿破仑"的瑞典国王Karl\nXII的弟弟Ulrik王子，18世纪中叶宫殿改建成为现在的样子。宫殿建成后深得皇亲贵胄和上流社会青睐，1650年Kristina女王在这里开启她的加冕仪式，院子里的皇家马厩现在是陈列女王当年加冕马车的博物馆，18世纪后期此地又成为国王Gustav\nIII大宴宾客的场所。1986年开始每年五月至十月宫殿对外开放，世界自然基金会WWF的瑞典总部就设在宫殿南翼。文艺复兴和巴洛克混搭风的宫廷礼拜堂(图二)建于1863-1865年间，如今已成为全瑞典最受欢迎的婚礼教堂之一。北侧的橘园博物馆(图三)始建于1662-1664年间、18世纪初改建，早年宫廷花园里种植了许多柑橘属果树，冬季就移入这座房子。自1988年起这里成为国家博物馆的一部分，展出18-20世纪瑞典雕塑家的作品。木结构的宫廷餐厅(图四)建于1868年，这里的美食佳肴对于斯京人民颇具吸引力，经常举办从婚宴到圣诞餐等各种宴席。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/9568886cad909d36d417fa51feb8a4ce.jpg",alt:""}})]),a._v(" "),r("p",[a._v('Ulriksdals\nslott林荫路旁的椴树已有600余年历史(图一)，宫殿外的英式花园始建于1801年，典型的巴洛克风格。夏日里鲜花盛开，常常看到一对对举行婚礼的新人，十分养眼。19世纪后期开始花园里陆续放置一些瑞典著名雕塑家的作品，例如图二中Per\nHenrik Lundgren的"撒网的摩尔人" (1845年)以及图三中依稀可见的Carl Milles的"两头野猪" (1929年)。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/63fa8b1fa4c98676c8e26d4783102731.jpg",alt:""}})]),a._v(" "),r("p",[a._v('位于Brunnsviken上游西岸的哈加公园Hagaparken是根据国王Gustav\nIII的旨意，于1780-1797年间始建的英式皇家园林，所以别名为Gustav公园。后来又多次整修改建或重建，1935年Hagaparken和Ulriksdals\nslott一同列入瑞典国家文化历史建筑保护名单。图一是名为Gustav III\npaviljong的Haga小皇宫，始建于1787-1792年间，是那一时期Gusta\n风格的典型建筑，即融合了法国路易十六及洛可可风的瑞典版。图二是始建于1802-1805年间的哈加宫Haga slott，现任国王Karl XVI\nGustav和他的姐姐们都在这里出生，现在是Victoria王储一家四口的居所。Gustav III原计划在公园内建造一座游乐宫 Haga\n大皇宫，但由于1792年国王被暗杀使得工程终止，现在只剩下一片遗址。Ulriksdal和Hagaparke\n都是开放型的大公园，人们可以自由自在地在海边和绿地上漫步、休憩、野餐、玩耍，遛娃遛狗的诸君没准还会与王储夫妇不期而遇。唯一"游人止步"、壁垒森严的地方就是皇家禁苑Haga\nslott，到了夜晚偌大的公园只有王储一家及保安和侍者在此地居住，皇家其实也有不为人知的孤独。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/9d4146f2b68364703291edfb8fb59e84.jpg",alt:""}})]),a._v(" "),r("p",[a._v("Hagaparken公园内建造了各种异国风情的亭台楼阁，左上图是始建于1786-1788年间的土耳其便亭，是 Gustav\nIII在Hagaparken兴建的第一个建筑，他有时会与自己最亲近的朋友在这个亭子里议事。右上图是建于1787年的八角形中国亭，亭子中央曾放置一张八边形桌子，也许与中国的阴阳八卦有关。这两个亭子位于公园最南部，靠近皇家陵园。中图是始建于1787-1790年间的蓝色黄条铜帐篷，是严格对称的巴洛克风格建筑，现在这里是咖啡厅。左下图是铝制的银帐篷内部，铜帐篷前方是面积达六万平方米的大草坪。右下图是始建于1790年的回音殿，这里是当年Gustav\nIII夏季室外用餐的地方。Hagaparken的多座老建筑都是瑞典建筑师Fredrik Magnus Piper、Christoffer\nGj?rwell和深得Gustav III喜爱的法国建筑师Louis Jean Desprez等人设计的。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/0dcca67b8545b09d70a9248bf90c0560.jpg",alt:""}})]),a._v(" "),r("p",[a._v('Brunnsviken湾下游的Bellevueparken公园是建于1786年的英式花园，Bellevue在法语中是"美景"的意思，与Hagaparken的设计师同为Fredrik\nMagnus Piper。园内峰回路转、曲径通幽，遍布山石树木和绿植花卉。公园后来成为国王 Gustav III\n的夏季休闲地，1879年在这里举办了瑞典第一次越野滑雪比赛。Gustav\nIII原计划环Brunnsviken湾修建一圈英式花园，虽然没有最后完成，但还是为后人留下了诸多美景。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/3625a8abd20bb8a7002a1d30e2f9e99b.jpg",alt:""}})]),a._v(" "),r("p",[a._v("**"),r("br"),a._v("\n**")]),a._v(" "),r("p",[r("strong",[a._v("生机盎然的动物园北岛 NORRA DJURG?RDEN")])]),a._v(" "),r("p",[a._v("国家城市公园的主要部分动物园岛Djurg?rden分成南北二岛，岛上最早的人类活动遗迹可追溯到铁器时代。这里从前是皇家领地，有各种野物出没，Djurg?rden于1579年得名于国王Johan\nIII。1646年女王Kristina 在Djurg?rden西南部的半岛兴建了第一批房屋，作为水手的医院和住宅。18世纪国王Gustav\nIII时代Djurg?rden成为市民的休闲娱乐场所，随后在岛上修建了一些私家豪宅及外国使馆，1868年1月1日Djurg?rden成为斯京城市的一部分。Stockholm的最大魅力大约就在于与大自然的贴近，Djurg?rden北岛生机盎然、充满野趣。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/a91b53901c8dab6a0b80e0c2b3f473c6.jpg",alt:""}})]),a._v(" "),r("p",[a._v("Bergianska\ntr?dg?rden植物园起源于17世纪中叶，1885年搬到Brunnsviken湾东岸的Frescati现址，建于20世纪初的高塔以瑞典植物学家Veit\nWittrock的名字命名(图一)。植物园的主建筑是1995年落成的钢管结构的Edvard\nAnderson温室(图三)，还有一个1900年左右为种植王莲建造的Victoria温室。植物园里还有建于19世纪充满异国风情的意大利平台和日式小花园，是一个获取知识、启发灵感、休闲娱乐的好去处。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/bbf745297590a642a79004135605fb06.jpg",alt:""}})]),a._v(" "),r("p",[a._v("植物园沿着Brunnsviken湾而建，四季景色优美宜人。在植物园的花园里种有整个北半球的植物9000余种，包括各种乔木、灌木、花卉、果蔬、香料及药用植物。枝头新叶悄悄绿，园中花朵迟迟开。水上的皮划艇，岸边的白天鹅，春天在我们的眼睛里。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/cb9707b2f8dda829df574f38b792be23.jpg",alt:""}})]),a._v(" "),r("p",[a._v("Edvard\nAnderson温室和Victoria温室中遍布来自世界各地的四季植物、奇花异草，并根据不同地域分门别类，在热带区域还有如咖啡树、茶树、可可树及捕虫植物。人们就像从弥漫着薰衣草香味的地中海区域、经由南非和澳洲西南部开花的灌木丛、直到加州的沙漠地带和潮湿的热带雨林漫游了一周。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/1008c5d22219b741d9ad9e6346f58f58.jpg",alt:""}})]),a._v(" "),r("p",[a._v('瑞典的两所著名大学——成立于1878年的Stockholm大学(图一)和成立于1827年的KTH皇家理工学院(图二)的主校园以及主要的学生宿舍区也坐落在国家城市公园中。Stockholm大学对面、Bergianska\ntr?dg?rden植物园旁边是成立于1739年的KVA瑞典皇家科学院(图三)，这里是每年评选和宣布诺贝尔物理学、化学、经济学奖的地方。皇家理工学院主校园是一色的民族浪漫主义风格红砖建筑，近旁是1912年Stockholm夏季奥运会的主会场Stadion(图四)，据说这是最后一次颁发纯金金牌的奥运会。也是在这次奥运会上，现代奥林匹克之父顾拜旦发表了著名长诗《体育颂》，并首次举办了文学、音乐、绘画、雕塑、建筑"缪斯五项"比赛。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/0738e1b4edf7695e6cb0597e9e040c40.jpg",alt:""}})]),a._v(" "),r("p",[a._v('Orn?sbj?rk是Hans Gustaf Hiordt于1767年在瑞典Dalarna省Lilla\nOrn?s发现的一种桦树，从而得名。与普通桦树不同的是，这种桦树的叶子细长多裂，大约是瑞典唯一的原生树种。植物分类学家的鼻祖1781年Carl von\nLinné生前很想得到几棵树苗，但一直未能如愿。1781年Linné的儿子命名了Orn?sbj?rk的拉丁文学名Betula pendula\nDalecarlica，1985年On?sbj?rk被选为瑞典国树。这种桦树的数量很少，母树枝条在Frescati栽培成功，并移植到瑞典各地\n…...，其中最著名的两棵位于Stockholm大学学生会"绿房子"门前(图三)。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/d31e0539864faa55cfed5ff92a350cc4.jpg",alt:""}})]),a._v(" "),r("p",[a._v('瑞典第一间公立博物馆——新艺术运动及卡罗琳斯卡巴洛克风格的国家自然历史博物馆新馆NRM\n(中图)开放至今年正好一百周年，瑞典对于自然界动植物标本的收藏可上溯到250多年前的Carl von Linne\n时代。这间博物馆是皇家科学院于1786年宣布开张的，1819年国王Carl XIV Johan\n降旨决定由国家资助。这位国王曾是拿破仑手下的将军，他将法国的美食文化带入瑞典，从此本地人才开始食用森林中的蘑菇，瑞典语"牛肝菌"(左下图)就以他的名字命名。由于皇家和民间的赞助捐赠，藏品日渐丰富，二万平方米的新馆于1916年落成，这里的餐馆都以"化石"命名(右下图)。NRM是假日周末斯京人民带娃休闲的好去处，除了穹顶的IMAX影院外，所有展馆免费对公众开放。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/263f77d6ee285630fd6d6ff7917c94f6.jpg",alt:""}})]),a._v(" "),r("p",[a._v("从动物园北岛至南岛约20公里长的地段是17、18世纪的皇家狩猎场，动物园岛Djurg?rden也因此得名。这里有深入斯京腹地的大片原始森林，当年可以捕获到各种大型野物，18世纪中叶狩猎规模达到最高峰。后来由于国王Adolf\nFredrik对郊游的兴趣浓过狩猎，因此逐渐拆除围栏。到了19世纪中后期，这一带也就成为平民们远足、骑行、垂钓、听鸟、滑雪、四季休闲的自然公园了。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/57ccc93eafbf3f99bfb83534c2314177.jpg",alt:""}})]),a._v(" "),r("p",[a._v('Stora skuggan 意为"林荫地"，图1-3是这里及附近列入瑞典国家文化历史建筑保护名单的老房子：1. 建于1680年代的国王Karl XI的钓鱼屋\nFiskartorpet，这个小屋现在成了冬季滑雪者的休息中心；2. Gustav III的御用建筑师Louis Jean\nDesprez设计的英式别墅；3. 同一时期建造的八面楼； 4. 钓鱼屋附近建于1902年的滑雪跳台。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/6e59b1ea0f4b8afbe2c6ab0eb41821e8.jpg",alt:""}})]),a._v(" "),r("p",[a._v('Stora\nvargjakten意为"猎狼场"，是早年的四大皇家狩猎场之一。这里有古老的松杉林以及北部欧洲最老的橡树林，有些大树已经超过500岁，当然早已无狼，秋色宜人。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/e5811a4bca49330e0feb9a0b409fd0d5.jpg",alt:""}})]),a._v(" "),r("p",[a._v("**"),r("br"),a._v("\n**")]),a._v(" "),r("p",[r("strong",[a._v("多姿多彩的动物园南岛")]),a._v(" "),r("strong",[a._v("S?DRA DJURG?RDEN")])]),a._v(" "),r("p",[a._v("Djurg?rdsbrunnskanalen是连接波罗的海海湾Lilla\nV?rtan和瑞典第三大湖梅拉伦湖湖湾Djurg?rdsbrunnsviken的一条运河，根据国王Karl XIV\nJohan的动议，用了近十年时间于1834年建成。运河长约一公里、深约二米、宽近十米，岸边种植了两排高大的橡树和枫树，四季风光秀丽，机动车禁行，是散步、骑行、放舟的天堂。图一是运河西岸滨湖大道旁建于19世纪中后期的各种建筑风格的高档住宅楼。图三是运河最南端的Djurg?rdsbron大桥，这是一座建于19世纪末的三拱铁桥，拱高三米，桥头的四个雕塑是北欧众神，原型取自瑞典著名作家和诗人Viktor\nRydberg的神话故事。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/fa80db6f272634e1f95165e3883895aa.jpg",alt:""}})]),a._v(" "),r("p",[a._v("动物园南岛靠近斯京市中心，岛上有许多历史建筑和主题公园，是斯京居民最喜爱的消闲场所并深受旅游者青睐的地方。图一是建于1889-1907年间的北欧博物馆，建筑风格为受荷兰影响的丹麦文化复兴建筑，而非瑞典传统风格，是瑞典最大的文化历史博物馆，专门展出有关瑞典近代(1520年起)至今的文化史和民族志展品。图二是1891年落成的瑞典民俗和生态博物园Skansen，是全世界第二座户外博物馆，园内分布着140多所斯堪的纳维亚半岛的各式民居，最早的可追溯到14世纪，展现了19世纪北欧市镇的完整面貌，Skansen的动物园还展出了多种斯堪的纳维亚动物。图三是19世纪上半叶的国王Karl\nXIV Johan的度假行宫Rosendals slott。图四是大型室外游乐场Gr?na\nlund，18世纪这里就开始成为娱乐场所，1883年正式命名开业，是瑞典最早的室外游乐场，至今仍为孩子们所喜爱。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/d344d6e3e4a4eb9427fedaaf55f49450.jpg",alt:""}})]),a._v(" "),r("p",[a._v("瑞典近代工业化之后最大的一件盛事就是1897年为庆祝国王Oscar\nII登基25周年在动物园南岛举办的Stockholm博览会，至今已经近120年了。博览会为时近五个月，也是在北欧举行的第四次博览会，共有3722位参展商与会，盛况空前。在这次博览会上使用了电影和照相等媒体新技术，开幕式全程摄影——包括Oscar\nII的讲话影像都可在互联网上找到，一百多年来世界的变化真是天翻地覆。南岛西部原有的一些著名建筑Djurg?rdsbron大桥和Skansen博物园等都被用于这次博览会，还新建了百余所展馆和展厅，主设计师是Ferdinand\nBoberg，参与设计的还有Fredrik Dahlberg和Gustaf\nWikman等著名建筑师。但是大部分博览会建筑都已经不存在了，图片依次为：餐馆Villa Godthem、植物展览馆Biologiska\nmuseet、狩猎展馆J?garhyddan、南方展馆Sk?nska gruvan、Villa Lusthusporten——博览会期间用作新闻和警察中心。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/8d69318ed30cb64e94bbaa2f52ac1e12.jpg",alt:""}})]),a._v(" "),r("p",[a._v('沿着Djurg?rdsbrunnskanalen\n运河边行走，有一条"王储Victoria和驸马爷Daniel的爱情小路"。这条长390米的小路是2010年王储夫妇大婚时，世界自然基金会WWF送给二人的贺礼，2012年8月23日剪彩。这条小路环绕湿地Isbladsk?rret，湿地里有各种动植物，还有斯京最著名的鸟岛，小路沿途都是描述湿地动植物的知识标牌。说到驸马爷，也是一位"凤凰男"，与王储二人十年爱情长跑方修成正果，如今也是俩娃的爹妈了。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/38dd8865af9937a6fb17f0f28d8a6e69.jpg",alt:""}})]),a._v(" "),r("p",[a._v('Waldemarsudde是一个三面环水的半岛，18、19世纪时这里曾经是炼油厂。19世纪末国王Oscar\nII和王后Sofia的幼子Eugen王子买下了这片土地，建造了自己的私宅和花园并常常在半岛上作画。Eugen王子是瑞典近现代著名画家和艺术家，他搬到半岛上居住后，Stockholm\n的昼夜变化、四季风光常常入画。王子故居现在是一个美术博物馆，左上图是瑞典雕塑家Liss\nEriksson的作品"伴侣"，图二的画板只要付费即可作画。五月里春意盎然，这么好的天气，大概全斯京人民都出动了。"木欣欣以向荣，泉涓涓而始流"，五月新绿和十月金秋是一年中最好的季节，人生亦如是。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/6c7fffdb6700d242f8ae18dd56c9ca5b.jpg",alt:""}})]),a._v(" "),r("p",[a._v("斯京晚春处处盛开的郁金香。中间是Waldemarsudde半岛上植物学大神Carl von Linné青年时代的雕像，是著名雕塑家Carl\nEldh的作品，半岛花园里有很好看的花卉。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/8ed2f48ad342aead64ceda4ff4895785.jpg",alt:""}})]),a._v(" "),r("p",[a._v("动物园南岛的绝美秋色，人生不过是四季流转、送往迎来。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/75ce5013b535d33fdcf2591ffca6a55c.jpg",alt:""}})]),a._v(" "),r("p",[a._v("**"),r("br"),a._v("\n**")]),a._v(" "),r("p",[r("strong",[a._v("斯京要塞：舰船岛Skeppsholmen和城堡岛Kastellholmen")])]),a._v(" "),r("p",[a._v('舰船岛和城堡岛位于Stockholm\n市中心波罗的海入口的战略位置，是扼守皇宫门户前水域的要塞，因此传统上是军事、特别是海军基地。连接舰船岛和斯京其余部分的第一座桥梁是一座名为"小岛桥"的木桥，由瑞典海军兴建于1638-1640年，当时海军军营迁到了舰船岛，如今还能看到旧时鱼雷工厂的遗址。现在的铁桥更名为"舰船桥"，于1861年落成，是瑞典第一座铸铁桥，桥上的两枚镀金皇冠(上二图)，象征昔日的辉煌。舰船岛的标志是停泊在南岸的老帆船af\nChapman\n(中图)，1888年建造，现在是一家青年旅社。左下图是1842年落成的舰船岛教堂，是一座仿照梵蒂冈的新古典主义风格建筑，2001年改成文化中心。右下图的石碑是为纪念1878-1880年间瑞典人首次环亚洲航海所立，关于航海的标志在岛上比比皆是。')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/cdd42c086f3f53ac773e58d56e584fe2.jpg",alt:""}})]),a._v(" "),r("p",[a._v("舰船岛现已成为斯京的博物馆岛，岛上的现代美术馆(Moderna\nMuseet)据称是欧洲最好的几间现代艺术博物馆之一。这里定期举办各位名家的作品展，并收藏了包括毕加索(图一)、达利(图二)等宇宙级大师的原作及当今世界各种流派的艺术作品。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/05a9c5bb1649ec3d4bb7824a39988cb0.jpg",alt:""}})]),a._v(" "),r("p",[a._v("城堡岛上的小城堡(左上图)建于1846-1848年间，它包括一个圆形的红砖塔楼。在城堡顶部飘扬着瑞典国旗，每天日出升起、日落降下，表明国家处于和平之中。右上图的红砖板岩顶塔式楼房是城堡岛上建于1882年的皇家溜冰馆和划船俱乐部，19世纪末上流社会青年男女中的时尚社交场所。")]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/ae1a786b8ecb1916c09f8ca1536e6fc6.jpg",alt:""}})]),a._v(" "),r("p",[a._v('波罗的海海水虽然没有地中海那么蓝，但斯京湖海与法南蔚蓝海岸最大的区别可能就是这里的水域上各类公共游轮更多，私家游艇也没有那么豪——很低奢。在这里住了许多年，但有些地方也是头一次去，比如图一的Beckholmen——有300多年历史的斯京最老的码头之一。在这里即使整个半岛都是私人领地，但周边总要留出一条公共道路给游人观赏景色，十分亲民。图二是酒类博物馆的各种招贴画，居然还有乙醇\nCH3CH2OH 的分子结构式，"何以解忧，唯有杜康"。图三是城里随处可见的自行车租借点，绿色出行，低碳生活，车上的瑞典语意为"I love\nStockholm"！')]),a._v(" "),r("p",[r("img",{attrs:{src:"/pic/18d890b1b8fc23d96213839164c4c52a.jpg",alt:""}})]),a._v(" "),r("hr")])}),[],!1,null,null,null);r.default=s.exports}}]);