(window.webpackJsonp=window.webpackJsonp||[]).push([[256],{623:function(_,v,p){"use strict";p.r(v);var t=p(14),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[_._v("【唐院春秋编者按】下文中所述主角——蔡孟骥，乃我交大唐山工学院矿冶系1941届平越毕业生，一生命途多舛，遭受极不公正的对待，难能可贵的是，他不忘所学，逆境中仍然以能为民为地方造益为荣，值得我后辈向他深深鞠躬致敬！")]),_._v(" "),v("hr"),_._v(" "),v("p",[_._v("**一个劳改犯发现铁矿的故事［引用］"),v("br"),_._v("\n作者／和弦社人"),v("br"),_._v("\n**")]),_._v(" "),v("p",[_._v("十多年前，有一次我探亲回家。同村的一位长辈问我在哪里工作？我回答说在浙江嘉兴。长辈听后很高兴，说嘉兴是个好地方，素称鱼米之乡，还说他有一位远房亲戚也在嘉兴，但已多年不走动了，叫我返回时，一定要帮他带封书信。"),v("br"),_._v("\n长辈的远房亲戚当时住在嘉兴市荷花堤的一条小弄堂里，进门很暗，要从壁陡的楼梯爬上二楼。居室不大，甚为整洁。长辈的亲戚已是八十高龄的老人，精神很好。听说是老家来的人，非常高兴。几句寒暄过后，老人就打开了话匣了，好像是见到多年不见的故已。从谈话中，慢慢了解到老人的一些情况。")]),_._v(" "),v("p",[_._v("老人一家从爷爷起就一直在西安谋生。当时，老人的爷爷生意做得很大，在西安市中心就有几十亩地的家产，有\n“蔡百万”之号称。由于战乱，至老人父亲时，家业开始走下坡路。老人40年代毕业于唐山交通大学矿冶系，就业于杭州。老人的弟弟在国民党军队中任职，官至将军级。解放后，由于家庭原因被人诬陷为反革命，自己不仅坐了30年的牢，而且还连累到家庭，长子被逼害致死，次子和女儿受尽歧视与欺负，真是家破人亡。面对家庭的不幸，老人显得极其坦然。")]),_._v(" "),v("p",[_._v("坐牢的30年期间，老人做过很多活，其中，对绍兴漓渚铁矿的发现过程感到特别的骄傲，而且谈得也很仔细，我听后也十分感动，答应将他发现的过程记录下来，转交给漓渚铁矿公司或有关部门，请他们关注这一事实。时至老人过世，这一事情尚未有眉目，可能是因为铁矿的发现不能归功于“劳改犯”，或许不相信“劳改犯”能有这个作为，总之，我没有完成老人的心愿。")]),_._v(" "),v("p",[_._v("今天，我将此事写入博客中，也算是对老人的一种思念，一丝歉意吧！老人的尊姓大名：蔡孟骥。")]),_._v(" "),v("p",[_._v("（注：此文曾于2007年5月发在新浪博客上）")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img6.ph.126.net_FfMOIT1ejbeIObjcb9thjA==_6597806439447220586.jpg",alt:""}})]),_._v(" "),v("p",[_._v("蔡孟骥老师与夫人杨倍坚")]),_._v(" "),v("p",[v("strong",[_._v("附：《绍兴漓渚铁矿是如何发现的？》一文")])]),_._v(" "),v("p",[_._v("一次偶然的机会，与一位八十高龄的老者谈话，从杭州的十景谈到诸暨城市的变化，又从诸暨谈到绍兴的兰亭，这引发了老者约半个世纪前的回忆，兴致勃勃地讲述了以下的往事：")]),_._v(" "),v("p",[_._v("1953年初春，根据群众的报矿，我（蔡孟骥）偕同杭州劳管局工业科张科长两人，到诸暨锣鼓山看了云母矿的露头，又去枫桥虎魄岭寻找铜矿，然后计划经绍兴去嵊县含天佩山勘察煤矿。")]),_._v(" "),v("p",[_._v("由枫桥去绍兴的途中，在谢家桥镇一家小饭店打尖，我一边吃面条，一边注视着路旁的一块石头，于是放下饭碗，取出背包里的矿锄蹲下来看那块黑皮西瓜似的石头，是嵌在路中无数石头的一块，用矿锤敲下一小块，显出银灰色的金属光，我惊喜地呼叫：“张科长，快来看，这是磁铁矿”。喊声惊动了周围的人，在围观的群众中有人说：“这种石头我们那边多得很”。")]),_._v(" "),v("p",[_._v("这位农民是住在兰亭附近的皇游塘（据说乾隆皇帝曾亲临此处），从谢家桥过去不足十华里，于是请该青年农民带路直奔皇游塘，将要进村时，发现路旁有座关帝庙，是所没有香火的破庙，没有人，门空关着，沿着庙的墙脚上山，不到一百米，就发现一块巨大的黑石头，没有表土覆盖，用锤打下一小块，果然是磁铁矿，张科长决定暂时不去嵊县，先在这里查勘。")]),_._v(" "),v("p",[_._v("在兰亭边上不到300米处有座石灰窑厂，是劳改单位的，我们就住在那里，第二天张科长向石灰厂借用两名会打炮眼的人，去关帝庙后的露头打了一炮，果然是富矿，采了样准备带回杭州化验。")]),_._v(" "),v("p",[_._v("关帝庙旁的炮声，惊动了村子里的百姓，许多男女老少跑来围观，见到我采集的的矿石样品，一些人说：“十年前，日本鬼子在苦竹岭也开采过这种石头”。问他们苦竹岭在哪里？回答说：“翻过山就到，不到十里路”。")]),_._v(" "),v("p",[_._v("当晚，委托石灰厂干部将所采的矿石标本从邮局先寄去杭州化验，我和张科长第二天一早就去了苦竹，从苦竹岭下来，先抵达上苦竹村，走进一家农户，见有一位老汉在吸旱烟，向他打听十年前日本人开矿的事，老汉十分热情地接待了我们，述说了当年有十几个工人在沙漏山开矿并运走了几万斤石头的事。")]),_._v(" "),v("p",[_._v("在老汉家搭伙吃了中饭，下午老汉叫儿子领我们上山去看原来开采过的宕口，路虽只有一千多米，但是非常难走，灌木严，柴草盛，他手持柴刀，披荆斩棘，好不容易总算到了沙漏山麓，山高约三百米，山脚下有一处凹进的地方就是当年开采过的宕口，掘进部分没有草木覆盖，但有积水深约0.3米，于是脱了鞋袜，仔细察看，果然都是银灰色的磁铁矿，取了样品，心中十分惊喜这个发现。")]),_._v(" "),v("p",[_._v("当晚就住在老百姓的家里，并邀请了几位长者谈话，了解一些有关情况，其中有两位青年自告奋勇说明日作向导陪我们上山。")]),_._v(" "),v("p",[_._v("原来此地处于绍兴、诸暨两县交接处，苦竹岭就是交界，山的最高处约300米，有一条路过苦竹岭可通往眉池，另有一条砍柴的小路可翻过山到西矿，除此以外，满山都是茂密的柴草，老虎、豹子常有出没，冬天积雪时，可以猎得野兔、麂子等，果然，就在我们费力地寻找露头过程中，就不止一次地看到老虎的粪便和爪迹，杜鹃花开遍了山阳，有红有白，还有一种黄色的花向导告诉我们叫“闹羊”，动物吃了会中毒甚至死亡。")]),_._v(" "),v("p",[_._v("整个上午沿沙漏山向东南方向前进，爬上爬下，找矿石露头，勘察岩石走向和性质，至中午到了孙家山，没有可满意的发现。下午仍继续前进，绕过孙家山向西北是紫铜山，山上的泥土和石头都是名符其实的古铜色，采集了通样而归。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img7.ph.126.net_xwDP2h2xI46hynLCfy7NSA==_6598097810028408517.jpg",alt:""}})]),_._v(" "),v("p",[_._v("回到杭州，看到由浙大化验的皇游塘样品的报告单，含铁量竞高达百分之五十七点四，于是立刻把沙漏山样品送去化验，不久报告单带来令人惊喜、兴奋的信息，含铁量是百分之六十八点九。")]),_._v(" "),v("p",[_._v("经上级领导研究决定，先派遣四十人进驻苦竹，用毛竹、稻草搭建临时房屋，清除山阳覆被，由我布置探槽二十一条，探井四十个，从1954年4月开始，全面探矿，从姚公埠山直至紫铜山。")]),_._v(" "),v("p",[_._v("两个月后，初步探矿告一段落，探槽中有14条见矿，这14条探槽都分布在沙漏山、孙家山、紫铜山，探井中有分布在沙漏山、孙家山、紫铜山一带的31个探井，其中有27个见矿，井深一般为3-5米，最深的一个为8米。")]),_._v(" "),v("p",[_._v("8月就调来第一批共两千人，作全面开采的准备工作，以后陆续增加到五千人，此为东矿。我用三个月时间，独自一人住在杭州圣湖旅馆里，完成了《绍兴漓渚铁矿露天阶段开采计划技术设计书》呈上级部门审批，然后我又回到苦竹村，眼前呈现的情况实在令人吃惊。")]),_._v(" "),v("p",[_._v("山下开出一大片平地，一排排简易房整齐排列，山上的柴草已全都剥离，密密麻麻的人群在山上忙碌着，经纬仪、平板仪已进行平面及等高线测量，由于剥离表土层时，捡出来许多块石，都堆放在山脚下。")]),_._v(" "),v("p",[_._v("这时我必须首先测定阶段运输线路，写出当月的掘进计划，开办选矿培训班，成立化验室，工作千头万绪，的确忙了一阵子，直到第二年方才勘察西矿，1955年末，东西两个矿区全面开采。")]),_._v(" "),v("p",[_._v("至于矿车线，重力自动放矿斜坡，破碎场，选矿场，下壮水运码头，东西矿区到下壮的小火车线路、矿区地形图，大爆破等等都是后话不提。")])])}),[],!1,null,null,null);v.default=n.exports}}]);