(window.webpackJsonp=window.webpackJsonp||[]).push([[269],{630:function(n,r,t){"use strict";t.r(r);var v=t(14),_=Object(v.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  |"),r("br"),n._v("\n**我校档案馆已找到1896年创立学校的奏折"),r("br"),n._v("\n** "),r("strong",[n._v("文／转贴四川经济日报讯")])]),n._v(" "),r("p",[n._v("四川经济日报讯：昨日记者从西南交大获悉，近日在国家第一历史档案馆发现的一个折片，揭开了该校前身之谜，同时也意味着找到了学校116年前的“出生证”。"),r("br"),n._v("\n众所周知，西南交通大学时一所有着百年历史的高校，至今在学校的档案馆里保存着该校1900年首届毕业生张孝基的毕业证书。这张毕业证书是国内高校存世的最早毕业证书，其价值十分珍贵。但是西南交大的前身具体何时建校？是由何部门批准的？这些关键问题仍悬而未决。"),r("br"),n._v("\n经过近两年的广泛搜求，西南交大档案馆人员终于找到了线索，发现时任直隶总督兼北洋大臣王文韶在日记中有上奏设立铁路学堂的记载。但日记中并未记录折片内容，是否批准，日记中也未再提及，故无法确证该“铁路学堂”是否就是“山海关北洋铁路官学堂”。数次在唐山、山海关、天津等地查找，亦无所获。"),r("br"),n._v("\n近日，在国家第一历史档案馆终于发现了一个折片，揭开了谜底。"),r("br"),n._v("\n王文韶折片名为《奏为拟设立铁路学堂所需经费在火车脚价等项下酌加应用事》，藏在“宫中朱批奏折”类档案中，时间为光绪二十二年九月二十三日（公历\n1896年10月29日）。全折虽然只有311个字。但对于西南交通大学而已，字字如今，它意味着终于找到了学校的“出生证”。"),r("br"),n._v("\n折子首先谈及学堂设立的背景和意义。王文韶创设我校的主张是修建铁路的主张已得到普遍肯定，铁路不断延伸，铁路事业蓬勃发展，铁路人才急缺的大背景下提出的。其次学堂如何办理的设想。王文韶计划将学堂定址于山海关北洋官铁路局旁，招生80名，聘请洋文正副教习三人、汉文教习二人并分班教学，学制为三年，毕业后按照其学习情况予以分配。最后讲学堂经费的来源问题。折后“该衙门知道”，是朝廷的批示，批准了王文韶的上奏请求。因此，这份文件完整了记录了清朝一个省级官员向朝廷请示并获同意的过程。"),r("br"),n._v("\n据悉，山海关铁路官学堂就是西南交通大学的前身，建于1896年。"),r("br"),n._v("\n折子全文如下："),r("br"),n._v("\n**再津关自设铁路以来，征兵运械，通商便民，成效昭著。现在接修关外工程，津卢一路亦经奉旨兴办，近又议建卢汉铁路，从此风气大开，实为富强之业。唯造桥、造路、制造车辆及行车司机等事，借材于泰西各国，殊非久违之图。经臣饬，据铁路总局道员吴懋鼎、张鸿顺等公同商，酌拟就山海关局房设立铁路学堂，招选学生八十名。派洋文正副教习三人，汉文教习二人，分班教授，三年为期。学成后量材器使，仍随时募补足额。将来广建铁路，可期取用不穷，较之借材异国，所省实多，且可免要挟居奇之患。所需学堂经费，岁约银一万余两，即在火车脚价项下，按照向章，客货每银一两，酌加三分；开平矿局运煤脚价，酌加二分，以资应用，不另请拨款项。俟经费充足，再行推广办理。除咨总理各国事务衙门、户部查照外，理合附片陈明，伏乞圣鉴，谨奏。"),r("br"),n._v("\n光绪二十二年九月二十三日直隶总督王文韶 **")]),n._v(" "),r("table",[r("thead",[r("tr",[r("th")])]),n._v(" "),r("tbody")])])}),[],!1,null,null,null);r.default=_.exports}}]);