(window.webpackJsonp=window.webpackJsonp||[]).push([[270],{633:function(n,r,b){"use strict";b.r(r);var v=b(14),_=Object(v.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  |  |  |  |  **"),r("br"),n._v("\n追忆父亲何志先（一）"),r("br"),n._v("\n尊 师 篇"),r("br"),n._v("\n文／何晓亮"),r("br"),n._v("\n**"),r("br"),n._v("\n每当11月末，我都会想起父亲，虽然他离开我们己近19年了。但每当想起他的养育之恩，我还是久久不能入眠。"),r("br"),n._v("\n从我得到《西南交通大学校史大事记》，《竢实扬华自强不息》两卷书籍和朋友今年初给我1966年文化大革命初期的一本《何志先的大字报选编》\n。这三本书我反复翻阅，它们使我更加了解了父亲在唐院时的工作情况，同时我也更加佩服他在那时的环境下敢于这样说和做的胆量。"),r("br"),n._v("\n我先给大家讲一讲何志先1958年12月从铁道部调到唐山铁道学院来的时代背景。1957年10月钱应麟从铁道部部长助理的位子上调到唐山铁道学院任院长和党委书记。他来到唐院后首先处理的就是1957年7月己被打成右派的副院长白铁岩的问题，"),r("br"),n._v("\n1958年7月铁道部决定白铁岩调离唐山铁道学院。"),r("br"),n._v("\n1953年白铁岩到校后主抓的是教学和科硏工作，在1957年7月被打成右派后教学和科硏工作就无人主管，学校很多教学工作出現混乱的局面，所以钱院长就去铁道部要人来接任这一角色。"),r("br"),n._v("\n何志先在1947年10月至1949年5月在东北铁路学院，干过教育科长. 教育处处长等职务。"),r("br"),n._v("\n1952年调进铁道部计划局任局长助理，局长就是钱应麟。而1958年铁道部教育局局长孟华又在1947年东北铁路学院领导过我父亲。这些都是决定何志先来唐山铁道学院的条件。"),r("br"),n._v("\n1958年×月决定下达后，何志先首先就是学习党的关于知识分子的有关政策。1956年1月，中共中央召开了关于知识分子问题的会议。这是新中国成立后第一次召开的大规模的知识分子问题会议，周恩来代表中共中央响亮地宣布：“我国的知识界的面貌在过去六年来己发生了根本的变化。他们中间的绝大部分己经成为国家工作人员，己经为社会主义服务，己经是工人阶级的一部分”。"),r("br"),n._v("\n当他来到唐山铁道学院时，首先认识到学校真正的主人是教师，沒有教师就不可能有学校的存在。所以来校后首先要尊重教师，关心他们。唐院是有着几十年历史的老学校。有着一批为学校服务了几十年的老教授。"),r("br"),n._v("\n所以他和钱院长商议借着5月15日校庆的日子在校友厅由钱应麟主持召开在校工作20年以上的老教师、老工人及党政干部座谈会。引人注目的是，老唐院的“五老”中的罗忠忱、黄寿恒、顾宜孙等被邀请出席座谈会。钱院长诚恳地称他们为“老前辈”，肯定了他们为学校作出的贡献，希望他们“在工作上多指教”，这也是多年没有过的。这些温暖如春的语言温暖了长时间总是接受批判的知识分子冷却了的心。（摘自校史下卷123页）"),r("br"),n._v("\n下面我再从大字报选编中找出四件事来说一下何志先对五老的照顾。"),r("br"),n._v("\n（1）何志先几次责成总务部门给罗忠忱、伍镜湖布置“客厅”。并且说：他们要什么，只要有就给什么，学校觧决不了的，要向市里反映。他还唯恐不能落实，几次责成院长办公室，催促总务部门。（大字报选编笫33页）"),r("br"),n._v("\n（2）他经常指示总务部门为学者、权威、袓师爷“送礼”，大米、白面、猪、牛羊肉、水果、细菜应有尽有。说什么：“办学要靠这些人，他们要什么给什么。”（笫35页）［注：在三年困难时期］"),r("br"),n._v("\n（3）大肆挥霍国家钱物，给袓师爷祝寿（大字报笫15页）：指1963年6月25日院行政、工会联合邀请全院副教授以上教师及老职工在校友厅为伍镜湖老教授庆祝80寿辰。祝寿会由工会主席梁道三同志主持，顾稀院长首先向伍老教授致词祝贺，并赞颂了他一生为教育事业的贡献，很多教师也表示了热忱的祝贺。（校史大事迹209页）"),r("br"),n._v("\n（4）何非常崇拜资产阶级“教授”，为一个八年不工作的“教授”\n办丧礼，不顾同志们的反对，公然主持成立了治丧委员会。（大字报29页）［注：经了解是原教文学的一位教授，唐院的校徽是选用了他写的字。］"),r("br"),n._v("\n正是何志先来校后，尊重学校的优良传统。尊重那些为学校教育做出贡献的这些老先辈，才得到各位在职的教师们的认可，也才有了问题就去找何副院长帮忙的事情。（以后再讲）"),r("br"),n._v("\n再过几天就是父亲去世19年的忌日，谨以此篇开场白来表达我对他的怀念！")]),n._v(" "),r("table",[r("thead",[r("tr",[r("th")])]),n._v(" "),r("tbody")])])}),[],!1,null,null,null);r.default=_.exports}}]);