(window.webpackJsonp=window.webpackJsonp||[]).push([[297],{659:function(_,v,p){"use strict";p.r(v);var t=p(14),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[_._v("|  |  |  |  |")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_UK2yLIkrf7m1it61oTc_nw==_1453536779734980769.jpg",alt:""}})]),_._v(" "),v("p",[v("strong",[_._v("得不偿失的大炼钢铁运动"),v("br"),_._v("\n图文／孺子牛")])]),_._v(" "),v("p",[_._v("1958年是中国人民永远难以忘怀的火红年代。总路线、大跃进、人民公社被称为“三面红旗”在中国的大地上高高飘扬。是1070这个数字触发了人们神经中的兴奋点，还是共产主义的曙光激发了人们对幸福生活的向往？那时人人都超越了常规的思维，透支了潜在的体力。虽然后来由于对事物发展规律的违背，使我们尝受了苦果，但那时人们的热忱和虔诚充分彰显的是对社会主义与共产主义的热望。我院的这段历史仅仅是整个中国在社会主义进程中一个微乎其微的缩影罢了。")]),_._v(" "),v("p",[_._v("1957年11月，毛泽东主席提出我国要在15年左右时间，在钢铁等主要工业品的产量方面赶上和超过英国的口号。")]),_._v(" "),v("p",[_._v("1958年8月17日，中共中央在北戴河召开政治局扩大会议，通过《全党全民为生产1070万吨钢而奋斗》的决议。")]),_._v(" "),v("p",[_._v("1070万吨钢是1957年我国钢产量535万吨钢的两倍，当时全国的钢产量仅完成450万吨，在仅剩下的四个月时间里要炼出620万吨钢。显然凭借现有的钢铁企业无论如何是不能完成这个任务的。所以，党中央号召全党、全民动员起来，要求各级党委第一书记挂帅，大搞群众运动，大办土法炼钢。在全国城市和农村开展空前规模的大炼钢铁运动，为生产1070万吨钢而奋斗。")]),_._v(" "),v("p",[_._v("1958年10月24日，我院党委第二书记顾稀同志（1957年10月8日，国务院任命铁道部部长助理钱应麟为我院院长兼党委第一书记，顾稀同志改任副院长兼党委第二书记）在全院干部会上传达了省委召开的钢铁生产紧急会议的精神：省委给我院下达了在12月10日以前完成800吨钢和1500吨铁的任务，我们力争完成全国钢产量的万分之一1070吨钢，产铁2000吨。决定从下星期开始全院停课开始大炼钢铁，要在学校内建两个炼铁炼钢基地。“一炼”建在学校的西操场上，需建一个炼铁厂，一个炼钢车间，并建1.5米3的土炉60个，50公斤到200公斤小转炉4座，要求在11月10日建成，预计到12月10日产钢720吨，产铁1000吨；“二炼”建在学校的一分部，需建6.5米3和13米3小高炉各两座，500公斤转炉三座，500公斤和1吨的电炉各一座，预计11月底，4座高炉和1个转炉可投产，到12月10日可产钢300吨，产铁500吨。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img5.ph.126.net_qoo6A8DjcYwljdVk6DuIsw==_6597695388772408605.jpg",alt:""}})]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img6.ph.126.net_-3cw-aAifUWKGM4klWx_Wg==_6597245688518033546.jpg",alt:""}})]),_._v(" "),v("p",[_._v("![](/pic/img4.bimg.126.net_photo_RUUgzFxMILQorYvlH-\nFZcA==_1480558377499243864.jpg)")]),_._v(" "),v("p",[_._v("一炼指挥部由孟庆源同志负责，二炼指挥部由许世拯、李国珩、王德恒负责。两个炼铁炼钢基地的基建任务由各系包干。")]),_._v(" "),v("p",[_._v("叮叮当 叮叮当，")]),_._v(" "),v("p",[_._v("千军万马奔战场。")]),_._v(" "),v("p",[_._v("星夜打起钢铁仗，")]),_._v(" "),v("p",[_._v("“1070”放豪光。")]),_._v(" "),v("p",[_._v("叮叮当 叮叮当，")]),_._v(" "),v("p",[_._v("锤下碎石如山岗。")]),_._v(" "),v("p",[_._v("熔炼钢水汇成海，")]),_._v(" "),v("p",[_._v("淹死美国老豺狼。")]),_._v(" "),v("p",[_._v("—— 王玉仓 《夜战钢铁厂》")]),_._v(" "),v("p",[_._v("这是当年为了配合大炼钢铁创办的《鏖战新闻》快报上刊登的一首学生写的诗歌。正如诗歌中描写的那样，从10月25日开始，我院千军万马奔向了大炼钢铁的战场，我们美丽的校园就变成了一个炼钢厂。教师、职工、学生停止了所有的工作和学习，为1070放豪光。")]),_._v(" "),v("p",[_._v("炼钢、炼铁首先需要解决建炉用的耐火砖问题。24日下午，党委一声令下，上千名师生排成一字长龙阵，从十余里外的砖窑把一块块耐火砖传送到学校直到夜晚，完成了5100块砖的运输任务。")]),_._v(" "),v("p",[_._v("从10月25日开始，校内运砖、运矿石的人群、车辆，川流不息；铁道系的任务是建立20座土高炉，占“一炼”高炉数量的三分之一，总计需要23万块砖，运输任务非常艰巨。全系师生们用木板背，用扁担挑，用自行车驮，用马车拉，后来借来十几辆手推车才提高了效率。为了解决运输问题，铁道系师生仅用了6天时间就建成了一条为炼钢基地运送原材料的铁路专用线。")]),_._v(" "),v("p",[_._v("正如领袖所教导：“只要有了人，什么人间奇迹都可以创造出来。”时间仅仅过了24个小时，由越南留学生参加建成的3号土高炉就炼出了我院的第一炉铁。学校特别将3号炉命名为中越友谊炉。")]),_._v(" "),v("p",[_._v("由全体团委委员在团委办公室门前修建的共青团1号炉，经过9小时的奋战，终于在10月31日凌晨1点炼出了我院的第一炉低碳钢。")]),_._v(" "),v("p",[_._v("10月27日晚，顾稀同志代表党委常委，决定11月10日到16日为全院第一个高产卫星周。要求全院总动员，争取钢日产量达到20吨，铁日产量达到40吨。在会上顾稀同志还宣布了为完成钢铁生产任务而设立的机构和人员名单。")]),_._v(" "),v("p",[_._v("党委生产指挥部下设：")]),_._v(" "),v("p",[_._v("1、炼钢办公室：主任张德中（正） 李国珩 梁继增")]),_._v(" "),v("p",[_._v("一炼指挥部负责人：梁继增")]),_._v(" "),v("p",[_._v("二炼指挥部负责人：李国珩")]),_._v(" "),v("p",[_._v("2、炼铁办公室：主任万少萍（正） 王德恒 孟庆源")]),_._v(" "),v("p",[_._v("一炼指挥部负责人：孟庆源")]),_._v(" "),v("p",[_._v("二炼指挥部负责人：王德恒")]),_._v(" "),v("p",[_._v("3、技术室：主任高家驹（正） 陆大宏 陶佑卿等")]),_._v(" "),v("p",[_._v("4、材料供应组：组长罗文志（正） 王元良 邢春连等")]),_._v(" "),v("p",[_._v("5、设备组：组长许世拯（正） 回力永 白家棣")]),_._v(" "),v("p",[_._v("6、运输组：组长李全圣（正） 张敬昭 任立棠")]),_._v(" "),v("p",[_._v("7、基建组：组长冯孝全（正） 胡敬庵 于润森")]),_._v(" "),v("p",[_._v("8、人事劳动组：组长张贵生（正） 王志明 古连华")]),_._v(" "),v("p",[_._v("9、宣传组：组长李泳（正） 朱玲 顾文汉 邓介曾")]),_._v(" "),v("p",[_._v("各系（职工）设立分指挥部。")]),_._v(" "),v("p",[_._v("在各级领导的带领下，全院师生员工，包括家属齐上阵，夜以继日地奋战在小土炉前、炼钢炉旁。当夜幕降临，学校西操场炼铁炉群的火光映红了半边天。在一个个不眠之夜，干部、教授和学生，守炉餐，伴炉眠，不讲条件，不讲报酬，有一分热，发一分光。")]),_._v(" "),v("p",[_._v("这是电气系女学生的一份决心书，现在读起来似乎有些稚气，但它的的确确是当时全体群众心态的真实写照：")]),_._v(" "),v("p",[v("strong",[_._v("决心书")])]),_._v(" "),v("p",[_._v("听到党总支正式批准，61号炉全部交给女同学承包的消息时，我们兴奋地几乎跳起来了。这是党对我们的信任，是党给予我们的光荣。保证节约原料，提高产量和质量，出色地完成任务，让铁水流成银河川，并把自己炼得像钢铁一样。")]),_._v(" "),v("p",[_._v("我们炉上炉下共有39个战士，人数并不比其他炉多。有人说：“女同学不行，力气小。”我们的回答是“力气虽小，干劲可大”。还有说：“你们操作经验少。”我们的回答是“虚心学习，认真操作，熟能生巧”。电气女一号炉是全院第一块花木兰阵地，我们人人都要像花木兰一样，排除万难，坚守阵地，让胜利的红旗永远飘在我们的阵地上，并要和男战友们比高低。")]),_._v(" "),v("p",[_._v("——电气女一号炉战士的雄心")]),_._v(" "),v("p",[_._v("尽管群众的热情高、干劲大，但按照时间的推移和学院的生产现状，省委交给我院的钢铁生产任务远远无法完成。")]),_._v(" "),v("p",[_._v("在这种情况下，学院党委制订了一个又一个钢铁高产周、高产日的生产计划，以不断激励师生员工，期待奇迹的出现。")]),_._v(" "),v("p",[_._v("但因时间已接近年底，长期停课也非长久之计。于是在11月27日和12月17日，铁道部和唐山市委两次给唐院调整了生产任务，最终以产钢350吨、产铁690吨一锤定音。")]),_._v(" "),v("p",[_._v("12月24日，学院26号炉产铁9203公斤，21号炉产铁3859公斤，放出全唐山市最大的一颗卫星。也就是在这一日，我院以总产铁881.534吨，完成交给任务的120.75%和产钢361.021吨，完成任务的103.1%的成绩为学院大炼钢铁划上了句号。")]),_._v(" "),v("p",[_._v("大炼钢铁的任务在全院师生的共同奋斗中算是勉强完成了。可是在这样的完成过程中和完成任务后，学院付出了何其巨大的代价？从经济上来讲，经成本核算，学校实际亏损867104.33元，虽然后来铁道部拨了20万作为学校亏损的补偿，学校仍亏损近67万元，造成了人力、财力和物力的极大浪费。从教学上来讲，停课两个多月，教学秩序打乱了，学生的学业受到了影响，教学质量严重下降。")]),_._v(" "),v("p",[_._v("而国家于1958年12月19日宣布的数据表明：我国提前12天完成钢产量翻番的任务，钢产量为1080万吨，生铁产量为1369万吨。实际上合格的钢只有800万吨，所炼的300多万吨土钢，416万吨土铁根本无法使用，据估计全国在大炼钢铁中的损失约200亿元，至于间接损失则无法计算。在大炼钢铁运动中，为了解决燃料，很多树木被砍伐；为了解决原材料，很多群众家中的铁器均被收走（当时叫“捐献”）；我校的老职工还记得当年学校的铁校门被换成了木门，幸好铁校门被保护起来，否则就会被砸成铁块送进化铁炉。")]),_._v(" "),v("p",[_._v("上个世纪60年代，我校西操场的草丛中，随地都能找到大炼钢铁时遗留下的铁砣砣、钢砣砣，日晒雨淋，锈迹斑斑，它们是这场荒诞闹剧的见证者和经历者。而真正经历过大炼钢铁运动的老人们回忆起这段历史时，他们只能苦笑地说：“那是一个激情燃烧的岁月，那是一个荒唐的岁月，那是一个留给一生深刻记忆的岁月。”")]),_._v(" "),v("p",[_._v("因为当大炼钢铁的浓烟消失后，紧接着带给中国人民的就是一场大饥饿，由于这场运动造成了工农业比例的严重失调，国家的经济陷入最困难的境地。当然，也不排除个别地区出现了自然灾害以及苏联背信弃义地撤走专家等因素的影响。但刘少奇主席在1962年召开的7000人大会上，对1960年出现的困难认为是由于“三分天灾，七分人祸”而造成，这种评价是今天历史学家认为比较客观的。")]),_._v(" "),v("p",[_._v("如今，大炼钢铁运动已经成为了共和国历史上一段很值得深思的难忘的回忆。它留给我们的沉痛教训，深刻启示，决不是“得不偿失”四个字就可以概括的。")]),_._v(" "),v("hr")])}),[],!1,null,null,null);v.default=n.exports}}]);