(window.webpackJsonp=window.webpackJsonp||[]).push([[331],{692:function(n,r,t){"use strict";t.r(r);var v=t(14),_=Object(v.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  |  |  |  |  **"),r("br"),n._v("\n母校向何方？"),r("br"),n._v("\n文／周文正（铁道1967届）"),r("br"),n._v("\n**"),r("br"),n._v("\n纵观唐院六十三年之历史，余以为只有：遭摧残、折腾、再遭摧残、重新折腾，重遭摧残。学这仿那，最后是邯郸学步，不会走路久矣。"),r("br"),n._v("\n解放初期，全盘仿苏（余用“仿”字，未用“学”字。夫学者，即使学点皮毛，尚有可取之处。“仿”者，简单模仿，猴子仿人依然是猴子，八哥学舌，终为鸟类）。齐白石大师云：“仿我者死，学我者生”即是此理。所谓“院系调整”，数刀砍下，唐山交大至少四肢已折其三（水利、冶金、采矿等不属吾校矣。）堂堂交大只能称学院，虽很久以后又改称大学，君不见当今国内大学数量远多于学院数量，能同日而语吗？各位校友心中自明了。精华去大半，元气大伤，可谓久久难以复原。"),r("br"),n._v("\n57年反右，精英又伤几何？接着“大跃进”、吃不饱、到处抓阶级斗争，再折腾，屡遭摧残。"),r("br"),n._v("\n“文革”乃大革文化命，学校、教授首当其冲，又遭摧残。到后来干脆大学停办，既已停办，校何以为校？师何以为师？唐院师长自有坚强意志，故自戕者较少，虽多少师长遭受摧残，损失大好年华，毕竟留得青山在。"),r("br"),n._v("\n七二迁校，自是人为大折腾，此乃最高当权者对所谓“战争”形势完全错误判断所为。试想：如学校遭受战争袭击，则祖国大部分重要部门已不复存在。观人类战争史，鲜见有学校首先遭受战争摧残之例，但逃不过人为自残，所谓摧残，定是自残！学校一迁，师资、仪器设备、图书、生源、对外交流……无一不遭殃。又大伤元气。有人可能问：抗战时，学校亦屡迁。但余以为，抗战迁校，师生斗志愈迁愈勇，意志愈迁愈坚。与七二迁校完全是两种不同的概念，不同的后果！"),r("br"),n._v("\n好不容易盼来文革结束，百废待兴，可以开放了，可以改革了，唐院振兴之日来矣。诸君不要被表面现象所遮目。现在学习规模比以前大了不知多少，师生数量比以前多了不知多少，高级职称不知多了多少，大楼数量比以前多了不知多少，设备也今非昔比……君可曾记得清华大学老校长梅贻琦先生所说“大学者，非大楼之谓也，乃有大师之谓也”的著名论述。所谓大师者，原本并无歧义，可如今有些大师需打引号，或作为调侃、讽刺、挖苦之用，早已随着信仰、诚信、道德、人生价值、文凭、学位、论文、货币的贬值而贬值矣。大学贩卖学历、学位、职称，早已人皆知之，论文抄袭，更是平常小事一件（余并非攻击大学，媒体揭露这种事还少吗？）"),r("br"),n._v("\n学校拼命扩招，拼命增设不知所云的专业和课程，就在笔者写此文的今天（2012.7.25扬子晚报B1版）的报纸上揭示有些学校开设下列选修课：厦门大学设“爬树课”，大连海运学院设“海盗研究课”，浙江纺织服装学院设“微博与社交课”，中南大学设“名侦探柯南与化学探秘课”，广东外语外贸大学设“爱情心理学课”，中国地质大学设“舞龙课”，南京医科大学设“足球裁判法规课”。是我落伍了还是社会进步了？当今学校这些举措，大多与金钱有关，唐院（西南交大）也不例外，早已把“唐山精神”（老校友邹良严先生语）、“唐院学风”抛掉了。"),r("br"),n._v("\n在清华大学百年活动中介绍了29位著名校友情况、南京大学110周年活动中介绍的著名校友情况，余看到，绝大部分精英是在以民国为纪年的时代培养出来的，最近这几十年好像鲜有举世闻名的精英。就唐院而言，余虽孤陋寡闻，但也知出了如下精英：茅以升、唐振绪、竺可桢、杨杏佛、顾稀、罗忠忱、伍镜湖、顾宜孙、李斐英、黄寿恒、陈茂康、林炳贤、范治纶、罗河、朱泰信、许元启、李汶、黄万里、林同炎、林同骅、何杰、方俊、汪菊潜、周惠久、李俨、刘恢先、王抵、杜庆萱、郭可詹、张维、严恺、姚桐斌、张万久、曹建猷、沈志云、杨耀乾、钱冬生、孙竹生、孙揆、劳远昌、高渠清、孙训方、徐家增、史家宜、杜镇远、张沛兹、徐采栋、肖纪美、陈能宽、张馥葵、刘文华、李温平、刘大中……至于近几十年的人物，不是一点没有，只不知能否与上述人物相比？校友G.M云：“自从唐院从峨眉搬迁至成都后，交大已无精神一说，大学没有精神，也只是行尸走肉罢了。”余深以为然，只是首句宜改为“唐院从唐山迁峨眉再迁成都后”，或可进一步改为“唐院从院系调整后”。尽管新人在不断涌现，但终究逃不过钱学森先生的世纪之问！余无语！"),r("br"),n._v("\n当年学校力争“211”时，不少海外老学长初不知“211”为何物，知道后，不少人失声痛哭，“东方康奈尔”竟沦落到需十分努力才能“211”达标。后来又有“985”更使人迷惘。唐院何日复兴？何时涅槃重生？何年可翘首迎接？有校友《荷花池赐联》云：“百年交大，憾别唐山，憾意难销，狂书三千憾字，字字泣血；十万学子，心系母校，愁容未解，疾饮十觞愁酒，觞觞有泪。”但愿校友之血和泪，碧血成花，浊泪迎春，余翘首以待，切莫如陆游临终时的《示儿》诗成为：“唐院腾飞天下日，家祭勿忘告乃翁”。"),r("br"),n._v("\n虽然，余前述自迁校以来，如何如何。但这毕竟是“末”，不是“本”，根本原因乃国情所致！南方科大的校长朱清时说过，中国大学要改革，第一就是要去行政化，第二就是要实现大学自治，教授治校。当年周恩来授意中国科学院成立历史研究所。决定请陈寅恪当《中古史研究所》所长，郭沫若当《远古史研究所》所长；吴宓当《近古史研究所》所长。但被陈寅恪拒绝了，他说：我是学者，要有“自由之思想，独立之精神”，是不宗奉马列主义的！在文革时，500年才出一个教授，被称为“教授的教授”\n陈寅恪被整死了。北京邮电大学经济管理学院的第一任院长阚凯力曾说：大学是什么？“自由之思想，独立之精神”。随着陈寅恪逝去，中国大学的“自由之思想，独立之精神”的唯一根苗也不复存在矣。阚凯力还说：“现在中国大学变成了一个赚钱的系统，学校领导都是官，分什么副部级的、局级的。这样下来，中国的大学就是两个字的目标：一个是权、一个是钱。合法的腐败和灰色的腐败，做的全都是卖文凭的生意，这就是中国。整个大学教育，乃至研究生的硕士、博士教育就是这个样子。钱学森的‘世纪之问’是：‘为什么中国的大学里培养不出大师？’靠这样的大学培养什么大师，简直是扯淡。我们的大学已经变成了养猪场，养猪卖钱，完全失去了大学的精神。”"),r("br"),n._v("\n尤其可笑并十分可怕的是改革开放以来，有人喊出了教育产业化的口号。但是，全世界有靠教育赚钱的吗？没有！教育本身是一项社会公益事业，是以促进社会的发展为目标。"),r("br"),n._v("\n说到目标，不管是学校还是老师、学生，若以金钱为目标，一切都完了，一切都结束了。清华大学只能出政界高级人士或聪明的商人了，无论如何，他们难以被称为大师。清华如是，遑论唐院！"),r("br"),n._v("\n就中国而言，只搞经济体制改革，不搞政治体制改革，可以在短期内把经济搞上去，但长久不了，只能更多地出贪官污吏，更多的“二代”携更多金钱到外国。就母校而言，在大的体制不变的情况下，谈什么“高招”建议，谈什么“二本”，谈什么“生源”问题，好像意义不大。庸老有云：“窃以为，议论一人、一事或一个学校的发展变化，或可按照古人所谓‘天时、地利、人和’三个层面论之：天时者，大环境也，以学校言，当指教育体制乃至国家体制，我国除港、台外，目前教育体制之利、弊如何，专家、学者的评论可谓已是汗牛充栋，即使在《唐院春秋》上也有若干此类文章发表，可毋庸再议矣。”此诚高论也，实论也。不从实际出发，不从根本出发，“竢实扬华”空矣，若无“实际”之“实”，就无“果实”之“实”，即使久“竢”，何以扬华？余又无语，故就此打住。")]),n._v(" "),r("p",[n._v("周文正 唐院铁道系 1967届")]),n._v(" "),r("table",[r("thead",[r("tr",[r("th")])]),n._v(" "),r("tbody")])])}),[],!1,null,null,null);r.default=_.exports}}]);