(window.webpackJsonp=window.webpackJsonp||[]).push([[385],{747:function(n,r,v){"use strict";v.r(r);var _=v(14),b=Object(_.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|"),r("br"),n._v("\n**"),r("br"),n._v("\n西南交通大学校史馆校史文物、实物征集办法"),r("br"),n._v("\n(征求意见稿)"),r("br"),n._v("\n**"),r("br"),n._v("\n自1896年山海关铁路官学堂创立至今，西南交通大学已跨越三个世纪，走过116个春秋，是中国近代建校最早的高等学府之一，素有“中国铁路工程师的摇篮”和“东方康奈尔”之称。在曲折坎坷而又卓越辉煌的办学历程中，学校形成了“竢实扬华，自强不息”的交大精神和“精勤求学，敦笃励志，果毅力行，忠恕任事”的校训，为近代中国的交通和高等教育事业的发展做出了重要的贡献，蜚声中外。"),r("br"),n._v("\n为了缅怀先人、垂范后学、弘扬传统、彰显辉煌，学校决定筹办一个永久性的校史展览馆。该馆将于近期内动工，馆舍面积达三千平方米。为了丰富校史展览内容，更全面的展现学校百年的建设与发展，学校特向广大教职员工、校友及其亲属以及社会各界人士广泛征集各类校史文物、实物。")]),n._v(" "),r("p",[n._v("** 征集范围**"),r("br"),n._v("\n1、国家领导人、社会知名人士及重要外宾来校参观、视察、访问或讲演时的照片、影像、题词、合影，各个时期新闻媒体对学校重大事件的报道，学校各类重大活动收到的有价值的贺信、贺电和礼品等。"),r("br"),n._v("\n2、西南交通大学各历史阶段，做出过突出成绩和贡献的校领导、专家学者、知名人士、杰出校友的著作、手稿、笔记、书信、题词以及奖状、奖品、证书、证章、聘书、任命书、委任状和有纪念意义的教学、生活用品等。"),r("br"),n._v("\n3、学校各历史时期具有历史价值或纪念意义的出版物、印刷品、票据及证件（如早期的毕业证、学生证等）、毕业照片、纪念册、徽标、印章及其他实物等。"),r("br"),n._v("\n4、西南交通大学各历史阶段，在党团建设、教学科研、学生活动、校园文化、后勤服务、基础设施建设以及革命运动、各种重大事件、重要活动中形成的有纪念意义、有保存、研究、展览价值的文字材料、图表、照片、音像等文物或实物。"),r("br"),n._v("\n5、其他有校史研究和展览价值的文物或实物。")]),n._v(" "),r("p",[r("strong",[n._v("征集办法")]),r("br"),n._v("\n1、学校提倡无偿捐赠，热诚希望全校教职员工、校友及其亲属、学生以及社会各界关心西南交大的人士，能将自己收藏的与交大校史有关的各类文物、实物捐赠给校史馆，以供展览与研究。"),r("br"),n._v("\n2、愿意将西南交通大学校史文物或实物出让给校史馆，可将原件的照片及其说明寄（送）学校。对个别贵重物品，经过鉴定、确认后，协商双方可以接受的价格。重要文物或实物，持有者可亲自送交给西南交大，或通知学校派专人去取。送来学校者视情况适当补助交通费。"),r("br"),n._v("\n3、若捐献原件有困难，可提供复制件；或者提供原件由档案馆负责复制，然后将原件安全归还本人。"),r("br"),n._v("\n3、由有关专家、领导和工作人员，组成征集校史文物、实物鉴定小组，负责对真伪及价值的鉴定。经过鉴定，达到文物标准者，可颁发给三千到五千人民币奖励金，有特别重要价值的，可颁发更高额奖励金。虽不属文物，但有研究、展览等价值的校史实物，经鉴定后，亦可颁发奖励金。"),r("br"),n._v("\n4、对捐赠的校史文物、实物及复制品，学校档案馆将登记造册、建立档案、按照文物管理的规程予以保护珍存，择要展出，并向捐献者颁发纪念证书，展出时亦将注明捐赠者身份。"),r("br"),n._v("\n5、欢迎向交大校史馆推荐、提供校史文物、实物之所有者或所在地，并进行沟通，协助征集。对于热心支持、帮助交大征集校史文物、实物者，学校将颁发给一定价值的纪念品。"),r("br"),n._v("\n6、有关征集校史文物、实物的咨询、协商、鉴定等具体事宜，由档案馆校史办具体负责。")]),n._v(" "),r("p",[r("strong",[n._v("捐赠地点")]),r("br"),n._v("\n西南交通大学档案馆校史办"),r("br"),n._v("\n地址：成都市高新区西部园区西南交通大学犀浦校区图书馆一楼档案A128室、二楼A216、A214室"),r("br"),n._v("\n邮编：611756"),r("br"),n._v("\n联系人：张雪永（028-66366040）"),r("br"),n._v("\n张阿支、崔啸晨（028-66367481）"),r("br"),n._v("\n韩琴英（028-66367483）"),r("br"),n._v("\n电子邮箱：zhangxueyong1973@163.com,489367597@qq.com"),r("br"),n._v("\n传真：028-66366040")]),n._v(" "),r("p",[n._v("二零一二年三月二十八日")]),n._v(" "),r("table",[r("thead",[r("tr",[r("th")])]),n._v(" "),r("tbody")])])}),[],!1,null,null,null);r.default=b.exports}}]);