(window.webpackJsonp=window.webpackJsonp||[]).push([[406],{769:function(_,v,t){"use strict";t.r(v);var p=t(14),n=Object(p.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[_._v("|  |  |  |  |  **"),v("br"),_._v("\n范文田：《交通大学唐山工程学院院歌》解读有感"),v("br"),_._v("\n２００６年初（原文载于西南交大网站）"),v("br"),_._v("\n**")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img2.ph.126.net_Boo64s1WyD1PwBZ-0x8wdw==_6631820931166509161.jpg",alt:""}})]),_._v(" "),v("p",[_._v("上个世纪30年代初，在确定我校“精勤求学，敦笃励志，果毅力行，忠恕任事”十六字校训的同时，在1934年前后，又拟定了《交通大学唐山工程学院院歌》，其歌词如下：")]),_._v(" "),v("p",[v("strong",[_._v("翳唐山，灵秀钟，我学院，声誉隆，灌输文化尚交通。")])]),_._v(" "),v("p",[v("strong",[_._v("习矿冶，土木工，窥学术，贯西中，相期同造最高峰。")])]),_._v(" "),v("p",[v("strong",[_._v("璀兮如金在镕！灿兮如玉相攻！桃秾李郁广座被春风。")])]),_._v(" "),v("p",[v("strong",[_._v("宜诚果，宜勤朴，基础坚，事功崇，文轨车书致大同")]),_._v(" 。")]),_._v(" "),v("p",[_._v("据有关资料记载，这一歌词的作者是“五四”爱国学生运动期间，曾任我校国文及历史教员的国民党元老吴稚晖先生（1865—1953）。他又名敬恒，江苏武进人。早年留学日本，1905年加入同盟会，曾任国民党中央监察委员、国民政府委员等职。著有《吴稚晖先生全集》。1919年在我校任教期间，曾与爱国学生一道创建“唐山注音字母传习所”，开展平民教育，提倡白话文。他还为我校最早出版的学术刊物《唐山工业专门学校杂志》（1919）、《交大唐院季刊》等亲笔题写刊名。")]),_._v(" "),v("p",[_._v("上述院歌在歌词中，首句“翳唐山，灵秀钟”，“翳”，隐翳，隐藏之意。上个世纪二、三十年代，唐山还是一个规模不大的城市，学校又建在郊区，故称隐翳唐山。“钟”，汇聚之意。“灵秀钟”即汇集了全国名师及优秀学子。“我学院，声誉隆”，反映了我校办学三十余年所取得的辉煌成就。1916年，我校在全国高校作业展览会上名列第一。教育部给我校颁发优等奖状和“竢实扬华”匾额以资鼓励。同年，我校应届毕业生茅以升，以特优的成绩考取美国康乃尔大学研究生，该校作出决定，以后唐山工业专门学校的毕业生可以免试入学攻读研究生，因而我校名声大振。“灌输文化尚交通”，阐明了当时我校遵循孙中山先生遗愿，“以研究高深学术，养成交通建设专才”为办学宗旨。“习矿冶，土木工”，我校当时设置采矿冶金、土木工程两大学科门类的专业。“窥学术，贯西中，相期同造最高峰”，反映了全校师生潜心钻研科学文化知识，学贯中西，相互勉励，共同攀登科学最高峰的积极向上的精神状态。“璀兮如金在镕，灿兮如玉相攻”，用镕金琢玉赞扬我校严谨治学，严格要求，千锤百炼，精琢细磨培育人才的优良校风。“桃秾李郁广座被（同披）春风”，桃李在此处指培养的学生。《资治通鉴·唐则天皇后久视元年》载：狄仁杰曾举荐姚元崇等数十人。后来，多成为名臣。有人对狄仁杰说：“天下桃李，悉在公门矣”。后以桃李满天下比喻教育培养的学生很多，遍布各地。这句话是说在春风沐浴之下，桃李芬芳，枝繁叶茂。比喻在学校、师长的辛勤耕耘、春风化雨，谆谆教诲下，广大的学子健康成长，硕果累累。“宜诚果，宜勤朴”教导学生做人要诚实守信，做事要勇敢果断，学习要刻苦勤奋、生活要俭约朴素。“基础坚，事功崇。”告诫学生在校学习，一定要打好坚实的基础，毕业后在事业上才能做出令人崇敬的功绩。最后一句“文轨车书郅大同”，“文轨车书”取秦始皇统一中国后，书同文、车同轨的典故。“郅”，登上之意。辛亥革命成功以后，在帝国主义操纵下，全国陷入军阀混战。1928年北伐成功，1931年日本又制造“九、一八”事变，侵占我国东北。所以，这一句是说全体同学与全国人民一道，热切盼望实现全国统一，共同奔向大同社会。")]),_._v(" "),v("p",[_._v("这首院歌歌词，当时，就以其含义深奥而情切，内容丰富且具特色，词句高雅而深得学校师生和广大校友的喜爱和好评。后经谱曲，唱遍唐山、湘潭、平越校园。1947年5月，抗战胜利回到唐山后第一次院庆大会上，校方在重申沿用原交大十六字校训的同时，将歌词略加修改后仍将此歌定为院歌。（当时学校名称市国立唐山工学院）")]),_._v(" "),v("p",[_._v("1991年，曾负笈平越的1943届校友，趁庆祝母校95周年校庆之际，返校寻梦，上百名白发苍苍的师兄、师姐，在聚会上齐声高唱老唐院校歌。老校友王德丰先生，会后以《洞仙歌》填词，真实反映了当时的动人情景，充分表达了老校友对校歌的眷恋和对母校的热爱与期盼。其词曰：")]),_._v(" "),v("p",[v("strong",[_._v("洞 仙 歌")])]),_._v(" "),v("p",[v("strong",[_._v("沉雄肃穆，“翳唐山灵秀”，此调不闻已悠久。")])]),_._v(" "),v("p",[v("strong",[_._v("念波翻山海，雾漫峰峦，尤难忘平越杨滩时候。")])]),_._v(" "),v("p",[v("strong",[_._v("欣逢金会聚，师友凋零，惟此歌声怅依旧。")])]),_._v(" "),v("p",[v("strong",[_._v("莫道不消魂，上百翁妪齐声唱，气冲牛斗。")])]),_._v(" "),v("p",[v("strong",[_._v("要唱得，“唐山不会亡”！纵绝唱，百年树人期后 。")])]),_._v(" "),v("p",[v("strong",[_._v("[注]")])]),_._v(" "),v("p",[_._v("1 峰峦：意指唐山。")]),_._v(" "),v("p",[_._v("2金会：50年后的聚会。")]),_._v(" "),v("p",[_._v("3\n“唐山不会亡”：抗日战争期间，我校师生被迫从杨家滩迁往贵州平越途中，在桂林遭日寇飞机轰炸，行李、衣物、图书仪器损失殆尽。后到广西两江，茅以升院长鼓励大家说：中国不会亡！唐山（交大）不会亡！我们一定要学好本领，报效国家，国家强大，才不再受日本帝国主义欺侮。在这首词中，“唐山不会亡”是校友对学校的期待，要继承唐山交大的优良传统、唐山交大精神，把学校办好。不无遗憾的是，往事如烟。这首校歌，在抗日战争时期，成为母校仍能聚而不散，弦歌不辍，并且取得辉煌成就的强大精神支柱。而如今，这首校歌已经被人们淡忘了。能唱这首校歌者已如凤毛麟角，不禁令人汗颜。诚然，歌词中的某些词句已经跟不上形势的发展而需修正。但是，校歌和十六字校训一样，都是母校百余年来优良传统中的最宝贵部分，也是母校抗击灾患和谋求发展的生命力的底蕴所在。它对于稳定学校的风格和水准，具有至关重要的作用。对于这种宝贵的优良传统和学校精神，应予珍视、继承和发扬光大！值此庆祝母校110周年校庆之际，特撰此短文已资纪念。限于水平，文中不妥之处，尚期指正。")]),_._v(" "),v("p",[_._v("【作者简介】：范文田——我校土木学院教授。1946年考入我校土木系，1951年毕业留校任教至今。本文写于2006年初。")]),_._v(" "),v("table",[v("thead",[v("tr",[v("th")])]),_._v(" "),v("tbody")])])}),[],!1,null,null,null);v.default=n.exports}}]);