(window.webpackJsonp=window.webpackJsonp||[]).push([[409],{771:function(t,n,r){"use strict";r.r(n);var v=r(14),_=Object(v.a)({},(function(){var t=this,n=t._self._c;return n("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[n("hr"),t._v(" "),n("p",[t._v("|  |"),n("br"),t._v("\n与张万久教授相处的最后时光"),n("br"),t._v("\n文/ 桥66届校友 魏庆昌")]),t._v(" "),n("p",[t._v("1966年2月，我因患十二指肠溃疡未去西南工地参加毕业实习，而留在学校张万久教授身边，协助他整理薄壁空心墩的实验数据。张教授很关心我的身体和学业，并专门安排徐文焕老师为我一个人开了《弹性力学》课，我把实验数据整理好绘成曲线后一个阶段一个阶段地向张教授汇报，张教授对学术的研究非常专业；也非常认真，并经常耐心教授和指导我们，在他身边的几个月里，知识有了很多长进，对这位蜚声中外的桥梁专家也有了更多的了解。"),n("br"),t._v("\n1966年6月，随着文化大革命的开展，斗争的锋芒渐渐地指向了国内知名知识分子。一天晚上我在食堂打饭，遇见了张教授在看大字报，我很担心地问了张教授：“为什么建国后的政治斗争都首先向知识分子开刀？”张教授苦笑的回答：“这是运动，由不得个人。”随着运动的扩大，张教授先后在峨眉报国寺被批斗，戴高帽子游街。当时我心里很难受，由于张教授对我的关心厚爱，我这个即将毕业的学生也被人贴了大字报，骂我是教授的宠儿，白专的典型。"),n("br"),t._v("\n后来大串连开始了，学生们也先后离校外出串连，一天晚上我很晚去报国寺食堂打饭，正好和张教授一起吃饭，我表达了希望张教授多多保重的意向，当时我觉得他还希望回唐山，运动结束后再把手上的研究课题弄完。"),n("br"),t._v("\n在1968年8月，我即将离校，有意到教授院去看看张教授，每次都没见着，但在9月7日下午，我下意识地走到张教授的住所，幸好看见他取奶回来，我怀着深深地感激向他告别，这时高音喇叭里又传来<人民日报>的一篇评论：文化大革命是毛主席的伟大创造，反修防修的的最好方法，今后要不断地搞下去.......这时，我看到张教授的脸色煞白，表情痛苦。与他告别后，看到张教授步履十分缓慢地走了回去，当时我已意识到这位年事已高的专家已对未来大失所望。\n等文革结束后，张教授被解放，他却毅然决然地选择了卧轨而去，这样一位赫赫有名的学者，为国家桥梁专业做出了这么大贡献的专家，就这样含恨而去，真是让人悲愤难平。我为失去这样一位贤师益友而痛心！为我们国家失去这样一位栋梁而惋惜！"),n("br"),t._v("\n英国前首相撒切尔夫人说过：“人类最大的悲剧是毁灭了自己创造的文明”。但愿今后我们的国家再也不要发生像《文革》这样的悲剧。")]),t._v(" "),n("p",[t._v("原桥61-2班学生 魏庆昌 2012.2.2")]),t._v(" "),n("table",[n("thead",[n("tr",[n("th")])]),t._v(" "),n("tbody")])])}),[],!1,null,null,null);n.default=_.exports}}]);