(window.webpackJsonp=window.webpackJsonp||[]).push([[449],{811:function(_,v,p){"use strict";p.r(v);var t=p(14),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("hr"),_._v(" "),v("p",[v("strong",[_._v("向孙中山临时大总统授印的赵士北"),v("br"),_._v("\n作者：梁锦唐")])]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img1.ph.126.net_m_A6kZ84iNwtFeFjhKTORw==_6631652705888913901.jpg",alt:""}})]),_._v(" "),v("p",[v("strong",[_._v("唐山工业专门学校校长赵士北")])]),_._v(" "),v("p",[_._v("赵士北\n，字孔南，号于朔，清同治十年(1871年)生，广东新会县古井镇霞路乡五福里人。新会县位于广东省中南部，在珠江三角洲西部，如今属于江门地区，改称江门市新会区。其中原新会、台山、开平、恩平四县俗称“四邑”，这里是华侨之乡，曾出过很多知名人士。如中国近代著名思想家、教育家、戊戌变法的领导人梁启超，著名的国学大师、曾任辅仁大学和北京师范大学校长的陈垣等。澳门特别行政区长官崔世安，被众多青年人崇拜的影视明星和歌星刘德华、谭咏麟、容祖儿以及著名作家张抗抗的祖籍也都是广东新会。")]),_._v(" "),v("p",[_._v("在清光绪年间，这里的很多乡民纷纷赴海外谋生，赵士北就是在清光绪十年(1884年)，随乡人漂洋过海来到美国。先后在美国接受了小学、中学教育，并考入哥伦比亚大学，获得法学博士学位。")]),_._v(" "),v("p",[_._v("光绪二十二年(1896年)，赵士北经人介绍，认识了来美洲宣传革命的孙中山，并积极参与反清活动，后加入中国同盟会，成为了孙中山的挚友。赵士北在美国与德籍美国人Mariebok结婚，（Mariebok中文译为白薇熙），白薇熙毕业于康奈尔大学，获医学博士学位。她随赵士北来到中国后，曾任北京协和医院院长（但笔者在协和医院官方网站上没有找到这方面的记载）。")]),_._v(" "),v("p",[_._v("1899年12月9日，赵士北与白薇熙的女儿赵丽莲在美国纽约出生。光绪三十三年(1907年)，赵士北回国，在江西省从事教育工作。")]),_._v(" "),v("p",[_._v("赵丽莲随父回国后，又被送往德国莱比锡音乐学院(院长伯恩思坦是她的舅父)学习音乐，1919年获硕士学位。赵丽莲归国后，与北洋政府国务总理唐绍仪的侄子唐荣祚结婚。曾任唐山交大校长的梁如浩（1905年5月至7月任唐山铁路学堂总办），其儿子娶了唐绍仪的女儿，所以这唐山交大的前后两任校长也沾上了亲戚关系。赵士北、梁如浩、唐绍仪都是幼年留美，况且他们又都是广东老乡，所以梁、赵二人能任唐山交大的校长，估计与此多少有些关系。")]),_._v(" "),v("p",[_._v("1911年12月16日全国17省南京代表会召开，赵士北作为江西省代表参加会议，在会上被推选为临时议会议长。1912年元旦，中华民国临时政府在南京成立，孙中山被选为临时大总统，在举行就职典礼上，赵士北以临时议会议长的身份向孙中山临时大总统授印。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img1.ph.126.net_6RGOJA3E7aBbe5rXCk0X7w==_6632735724840027775.jpg",alt:""}})]),_._v(" "),v("p",[v("strong",[_._v("上图中华民国临时政府成立会议代表合影")])]),_._v(" "),v("p",[v("strong",[_._v("前排左起第五人是孙中山，左四是时仼陆军部长的黄兴，左三是教育部长蔡元培，")])]),_._v(" "),v("p",[v("strong",[_._v("左六是参议院议长赵士北，左八是总统府秘书胡汉民")])]),_._v(" "),v("p",[_._v("1912年1月28日，赵士北被广东选区选为南京临时参议院议员，同年4月转为北京中华民国临时参议院议员。1912年7月，赵士北来到唐山铁路学校任校长。（1913年9月，根据中华民国《普通教育暂行办法》唐山铁路学校更名唐山工业专门学校。）")]),_._v(" "),v("p",[_._v("赵士北先生来到唐山铁路学校任校长仅两个月，没有想到又与老朋友孙中山在唐山相见。1912年8月，孙中山先生应袁世凯一再邀请专程赴北京共商国是。孙中山先生在与袁世凯会谈期间，接受了袁世凯授予的“筹划全国铁路全权”之头衔，视察了北京、天津、河北、山西、山东等地的铁路，还特地去京张线进行了实地调查。据《西南交通大学史》记载，1912年9月24日早，孙中山乘坐火车来到唐山，先去唐山铁路工厂视察，当天下午孙中山来到了与唐山铁路工厂仅一路之隔的唐山铁路学校。唐山铁路学校的学生们得知孙中山先生要来唐山视察的消息，还专门用英文编写了一首歌曲《中山先生万岁》，大家就是唱着这首歌曲列队前往车站欢迎孙中山先生的。唐山铁路学校1913届学生余明徳在一篇回忆文章《想当年》中，曾描述了当时的场景。他在文章中还专门提到：“那时的新校长是赵士北博士，听说他是孙先生的老朋友。”")]),_._v(" "),v("p",[_._v("在赵士北任唐山铁路学校校长期间，主持制定了《唐山铁路学校规则》，明确了办学的目标，严格了学校的各项规章制度，强调了对学生德育的培养，并将学生的操行成绩列入学生成绩的评定。除此之外，在课程设置上，除安排土木工程类外，还涉及了机械和电气学科，明确规定了学生的实习时间。这些措施提高了学校的教学质量，其效果在1916年3月教育部举办的全国专门以上学校成绩展览会上得到了体现。")]),_._v(" "),v("p",[_._v("1916年3月15日至4月14日，北洋政府教育部在北京举办了全国专门以上学校成绩展览会，全国报送选品参加展览的高等学校共71所。展览会闭幕后，教育部遴选各科专家对各校展品分别审查，评定分数。由赵士北任校长的唐山工业专门学校获94分，在全国各高校中名列榜首，为此，教育部给学校颁发了特等奖状，当年12月，还由教育总长范源濂颁奖匾额一方，上书4个金色大字：竢实扬华。这是对赵士北任校长期间工作的最好评价了。")]),_._v(" "),v("p",[_._v("1916年参加全国学习成绩展览会，唐山工业专门学校选送的展品是1915届学生王节尧和1916届学生茅以升的作业，当时，他们俩人的成绩均为全年级第一名。学校颁发给茅以升的毕业证书上，校长的大名就是赵士北。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img2.ph.126.net_0LYyEBL9sPcANAmLyT5ZKQ==_2592947485478762006.jpg",alt:""}})]),_._v(" "),v("p",[v("strong",[_._v("由赵士北校长签发的茅以升毕业证书")])]),_._v(" "),v("p",[_._v('1916年8月17日，赵士北校长奉调离任，改任粤汉铁路管理局局长。\n1917年8月，孙中山反对北洋政府废除旧约法，南下开展护法活动，在广州组织军政府。赵士北随孙中山先生来到广州，任军政府司法部司长。他在司法部任职期间，极力主张实行欧美国家的"三权分立"制，提出"司法无党"、“司法独立”等观点。但当时这种理念违背国民党政府推行的"以党治国"的国策，所以，1919年他辞去司法部职务，专任广东省高等法院首席推事。后来，他辞去政府的职务回到家乡江门，在江门旧仓巷开设庆新律师事务所，担任执业律师。')]),_._v(" "),v("p",[_._v('1928年6月，北伐军进驻北京，国民党政府宣布南北统一\n，并迁都南京。同年11月，赵士北被选为国民政府立法院立法委员。1931年“九一八”事变爆发，赵士北针对日本帝国主义的侵略行径，以立法委员身份向报界发表宣言，提醒政府要提高警惕，团结一切力量抵抗日军的侵略；同时要积极通过外交途径斡旋，通过"国联"，努力防止战争的扩大；在国内发行救国公债，充实军备做好战争准备；最后要争取外国的援助。赵士北这些建议，直到卢沟桥事变后，才在政府的决策中得到体现。1936年秋，赵士北调任广州市地方法院院长。在任职期间，他积极推进地方的法制建设，1937年5月，他得知家乡古井发生械斗，便于下旬借回乡扫墓祭祖之机，宴请全乡乡绅族老130余人，席间以法院院长身份发表演说，对乡人进行法制教育。他告诉家乡的百姓在国难当头之时，应该团结对外，国家才有希望。')]),_._v(" "),v("p",[_._v("1938年10月，广州沦陷，赵士北辞职赴香港闲居。日军占领香港后，曾诱他充当汉奸，他严词拒绝，旋为日军拘捕，于1944年病逝狱中。")]),_._v(" "),v("p",[_._v('赵士北性情耿直，爱国忧民。孙中山曾亲题"大道之行，天下为公"八字为赠。赵士北博士在任唐山铁路学校校长之前，曾有过为孙中山临时大总统授玺之经历，而在1918年曾任唐山工业专门学校国文教师的吴稚晖，在1946年蒋介石当选中华民国第一任总统时，曾以制宪代表主席的身份，将《中华民国宪法》递交给蒋介石总统。')]),_._v(" "),v("p",[_._v("除此之外，据史料记载\n1926年，国共合作的北伐战争开始，在北伐誓师大会上，是吴稚晖将孙中山的遗像和党旗授给时任北伐军总司令蒋介石，并致词激励全体将士。在中国近代史上，这两件重要事件的当事人都曾在唐山交大任过职，说起来也是学校校史上应该特别值得记载的。校长是给孙大总统授印的，教师是给蒋总统授旗的，这样的学校在全国高校中也是独一无二的。")]),_._v(" "),v("p",[_._v("这里也要特别提一下赵士北的女儿赵丽莲，她的性格和父亲一样，她从德国学习回国后，先后在广东女子师范学校\n、北京女子师范学校、北京大学等院校教授音乐和英语。在抗日战争期间，她带着三个孩子留在北平，在一个中学任教。日寇占领北平后，要她担任广播电台英文播音员，她严词拒绝。日寇将她逮捕，逼其就范，但她决不屈服，后来日寇不得不将她释放。太平洋战争爆发后，赵丽莲又被日寇拘禁于集中营达半年之久，在友人帮助下才出狱。")]),_._v(" "),v("p",[_._v("抗日战争胜利后，赵丽莲致力于英语教学，在北平开办了英文补习学校，专门教授英语\n，后来又到北平广播电台通过电台进行英语广播教学，开创了在中国进行英语广播教学的先河。")]),_._v(" "),v("p",[_._v("1948年8月，赵丽莲应原北平女子文理学院院长许寿裳之邀，赴台湾任教。她先后执教于台湾师范学校和台湾大学外文系，并在台北“中国广播公司”主持“空中英语教室”节目，同时在“中华电视台”开辟“鹅妈妈(赵丽莲)教室”节目，教授儿童学英语，一直坚持了40年之久。")]),_._v(" "),v("p",[_._v("赵丽莲退休后，仍分别在台湾文化学院、政治大学、台湾清华大学、台湾交通大学、“中央”警官学校等任教。她85岁高龄时，仍在家中开设“儿童英语教育班”教授儿童，后来不幸身患血癌。当社会各界得知这一消息后，纷纷为她治病捐款，但她将400万元(台币)\n捐款转而捐给台湾大学校友会的文化基金会，作为推广英语基础教育使用。")]),_._v(" "),v("p",[_._v("1998年6月2日，赵丽莲病逝于台湾，终年90岁，宋美龄女士为其敬献了花圈。在宝岛台湾，赵丽莲的名字比赵士北的名字被更多的人们所熟知。")]),_._v(" "),v("hr")])}),[],!1,null,null,null);v.default=n.exports}}]);