(window.webpackJsonp=window.webpackJsonp||[]).push([[497],{866:function(n,r,t){"use strict";t.r(r);var b=t(14),v=Object(b.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("p",[n._v("|"),r("br"),n._v("\n**校庆感言"),r("br"),n._v("\n文／庸 老"),r("br"),n._v("\n**"),r("br"),n._v("\n大约是4月上旬，就在上海电视中，见有报道上海交大庆祝115周年校庆的消息。可能是开幕式吧，场面看来相当隆重，科技部长陈竺亲临庆典，还有数十位校友院士被授予“终身荣誉奖”等活动。"),r("br"),n._v("\n由此想到：同是交大，同是校龄115年，不知我校在今年校庆日将有何活动？无论如何，作为一个耄耋老人，对于“跨越了三个世纪”（上述新闻报道用语）的母校，值此校庆之际，也要衷心祝贺！"),r("br"),n._v("\n说到交通大学，有趣的是，记得在上述新闻报道中，有一段话，不知出自何人，大意是：《易经》曰：“天地交而万物通，上下交而其志同”。由此而有了“交通”这个名字。意思是不错，只是我们知道，上海交大最早名为南洋公学，而唐山交大最早名为铁路学堂，只是在1921年都归属北洋政府交通部后，才改为上海和唐山两个交通大学。因此校名来历引用《易经》，未免有借重之嫌也。"),r("br"),n._v("\n不过，用《易经》这两句活来说明两个交大在115年里的成就，的确也有道理，特别是在前几十年。人所共知，许多著名的科学家、工程师，都出自这两个交大，而更多交大出身的工程师、专家们也在我国的交通以及其它工程、科研领域做出了贡献。所以能如此，推其原因，无非是自有交大以来，即形成一个传统，正如上引《易经》所言：“上下交而其志同”，即教师和学生志同道合：教师认真教授；学生则刻苦读书，务求成才。此一优良传统，师、生们代代恪守，才俊之士，层出迭现，“俟实扬华”，遂使交大名满天下焉。"),r("br"),n._v("\n然而，近些年来，令我辈老人困惑不解的是：对高等教育的议论、批评、乃至抨击的言论，屡见于网络、报刊，其范围自解放初学习苏联直至今之改革开放，无不涉及。甚至在不久前的清华校庆时，前总理朱熔基也对当前的教育状况予以批评，并说“教育中长期规划”是“空话”，引起了热烈反映。"),r("br"),n._v("\n关于前几十年的情况如何姑置不论，对近些年来的高教状态，人们所诟病的似乎主要在于学校行政化，再就是教师无心教学而一意追名逐利，学生也无意学习而心有旁骛也。其例甚多，爆出的丑闻也屡见不鲜。何以产生这些问题？其深层原因何在以及如何才能解决之？我校状态又如何？还望有关方面以及校内热心人士深切关注焉！"),r("br"),n._v("\n在上面所说新闻报道中所引的《易经》文字出自《泰?彖》。《泰》是个吉卦，内容强调的是“天地交而万物通，……君子道长，小人道消。”后两句意思是正气压倒了邪气，君子之道将大行也。但卦中也提出了警告：“无平不陂，无往不复。”这是后人经常引用的警句。陂（po），不平坦也。即在发展途中，总会遇到麻烦。更糟的发展是由《泰》而变为其后的《否》，其卦与《泰》正相反，否（pi），恶也，其《彖》云：“天地不交而万物不通也……。小人道长，君子道消也。”古老的《易》，其于吾人也有启示意义欤？"),r("br"),n._v("\n值此学校115周年华诞之际，发此感言，其有不经之处，还望读者批评指正焉！")]),n._v(" "),r("hr")])}),[],!1,null,null,null);r.default=v.exports}}]);