(window.webpackJsonp=window.webpackJsonp||[]).push([[501],{859:function(n,r,v){"use strict";v.r(r);var _=v(14),t=Object(_.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  **"),r("br"),n._v("\n一级学科规划提要之"),r("br"),n._v("\n31 公共管理"),r("br"),n._v("\n（征求意见稿）"),r("br"),n._v("\n**")]),n._v(" "),r("p",[n._v("31.1 学科现状：有行政管理、教育经济与管理、社会保障3个二级学科硕士点。其中“行政管理”为省级重点学科。"),r("br"),n._v("\n31.2 建设目标：建成公共管理一级学科硕士点，行政管理博士点。"),r("br"),n._v("\n31.3 学科管理负责人： 陈光 教授"),r("br"),n._v("\n31.4 研究方向：与政府改革和运作相关的公共管理研究；与工程背景相关的公共管理研究；与公民社会发展相关的公共管理研究。"),r("br"),n._v("\n31.5\n团队建设：每年引进优秀博士生和海外留学人员2～5人，有海外留学背景的教师达到专业教师的30％左右，博士学位教师90％以上。组建公共政策研究团队、创新教育与管理教学研究团队、公共经济与社会保障研究团队、城市公共管理研究团队、公共工程组织与管理研究团队等若干个基于科研教学工作、研究中心和学科点基础上的学术团队。“十二五”期间省级学术团队从现在的2个增加到4个，国家级学术团队建设有突破。"),r("br"),n._v("\n31.6 人才培养：培养应用型的公共管理本科人才，适度加大研究生招生规模。"),r("br"),n._v("\n31.7 科学研究：积极组织争取国家社会科学基金、国家自然科学基金等项目。“十二五”期间主持国家级项目达到3～5项。"),r("br"),n._v("\n31.8\n基地平台建设：“十二五”期间在现有团队和科研工作基础上，建设四川省哲学社会科学科研基地1个，在电子政务综合实验室建设基础上建成为省级重点特色实验室。"),r("br"),n._v("\n31.9\n学术交流：每年有计划地组织、承办、协办国际、国内学术会议1～2次；在公共服务、教育财政、创新方法等几个优势领域逐渐形成学术会议品牌，扩大影响和交流。特别加强与美国、日本、欧洲和港台地区的学术交往活动。鼓励教师参加公共管理国际相关学术会议，有计划地组织安排教师参加每年度中国行政管理学会、全国MPA教指委举办的学术会议以及管理科学与工程相关学术会议等。"),r("br"),n._v("\n31.10\n发挥作用：组织、参与区域性学术会议和活动。加强与地方政府、产业联盟和非盈利组织的联系，参与区域发展战略、规划、政策研究以及产业振兴、灾后重建、公民社会发展等相关工作。"),r("br"),n._v("\n31.11 建设举措：学院拟采取加强组织和制度建设，建立学科建设基金，实施高水平专业性论文奖励制度等措施加强学科建设。")]),n._v(" "),r("hr")])}),[],!1,null,null,null);r.default=t.exports}}]);