(window.webpackJsonp=window.webpackJsonp||[]).push([[503],{865:function(n,r,v){"use strict";v.r(r);var _=v(14),t=Object(_.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  **"),r("br"),n._v("\n一级学科规划提要之"),r("br"),n._v("\n29 电子科学与技术"),r("br"),n._v("\n（征求意见稿）"),r("br"),n._v("\n**")]),n._v(" "),r("p",[n._v("29.1\n学科现状：包括物理电子学，电路与系统，微电子学与固体电子学，电磁场与微波技术4个二级学科。其中电磁场与微波技术是二级学科博士授权点。二级学科分设于物理学院和信息学院，为四川省重点学科。"),r("br"),n._v("\n29.2 建设目标：通过建设，取得物理电子学等多个二级学科博士点，建设电子科学与技术一级学科硕士点。"),r("br"),n._v("\n29.3 学科管理负责人：跨学院管理小组。"),r("br"),n._v("\n29.4\n研究方向：天线理论与技术，计算电磁学，电磁探测，微波器件和微波电路，强辐射源与高功率微波系统，光电子与光电子技术，集成电路设计，FRID技术，电子材料、器件与现代天线集成技术，集成传感器电路与系统，微波毫米波电路与系统，非线性电路与混沌系统。"),r("br"),n._v("\n29.5 团队建设：通过引进、外聘和培养，建立起一支高水平的师资队伍。具有博士学位的师资达到70％。"),r("br"),n._v("\n29.6 人才培养：加大研究生招生比例，争取能培养硕士100人，博士40人。"),r("br"),n._v("\n29.7\n科学研究：获得3项国家自然科学基金项目，力争获得主持国家自然科学基金重大项目、重点项目或重大研究计划项目1－2项，获得“863计划”项目1－3项，并力争获得一项863重点项目。发表学术论文数量大于250余篇，其中被SCI、EI收录论文100余篇。发明专利10项。"),r("br"),n._v("\n29.8\n基地平台建设：努力建设现有的“高功率微波技术实验室”和“高压科学与技术实验室”四川省高校重点实验室，争取建成智能信息获取技术与系统四川省重点实验室。"),r("br"),n._v("\n29.9\n学术交流：积极参加本学科全国性学术会议，积极组织各种小型学术研讨活动，派员参加各种形式的学术交流活动；积极开展国际合作研究、国际合作培养人才项目；每年邀请国内外高水平的专家、学者到校做相关学术报告；积极组织本校教师和研究生做学术报告，交流心得，探讨学术问题。"),r("br"),n._v("\n29.10 发挥作用：为省内同类学科、科研机构、和校内相关学科、工厂培训科研骨干20－50人次。"),r("br"),n._v("\n29.11\n建设举措：目前的关键在于办学资源较为分散，两学院将以学科建设为契机，理顺发展思路，进一步找准方向，加大人才队伍和实验平台的共建，提高学科整体水平。")]),n._v(" "),r("hr")])}),[],!1,null,null,null);r.default=t.exports}}]);