(window.webpackJsonp=window.webpackJsonp||[]).push([[509],{871:function(n,r,v){"use strict";v.r(r);var _=v(14),t=Object(_.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  **"),r("br"),n._v("\n一级学科规划提要之"),r("br"),n._v("\n24 法学"),r("br"),n._v("\n（征求意见稿）"),r("br"),n._v("\n**")]),n._v(" "),r("p",[n._v("24.1 学科现状：现有诉讼法学、经济法学2个二级学科硕士点，有1个法律专业学位硕士点。"),r("br"),n._v("\n24.2 建设目标：申报建设法学一级学科硕士点，培育法学二级学科博士点。建设成为西南知名、在全国有影响的法学学科。"),r("br"),n._v("\n24.3 学科管理负责人：陈家宏 教授"),r("br"),n._v("\n24.4\n研究方向：法学理论硕士点，设有立法学、司法学、人权理论与人权法三个研究方向；经济法硕士点，设有竞争与反垄断法律制度研究，劳动与社会保障法律制度研究，WTO与知识产权保护制度研究等方向。拟逐渐新增知识产权、商务及其他法律硕士方向"),r("br"),n._v("\n24.5\n团队建设：计划引进拥有博士学位的教师3人，引进具有正高级职称教师数人，大力提高现行师资队伍的博士学位比例和正高职称比例。达到每个硕士学位点配备两位以上的正高职称教师，在凝练学科方向的基础上逐步培育学科团队。"),r("br"),n._v("\n24.6\n人才培养：重视依托交通优势学科，打造法学学科特色，在本科培养计划增设“铁路法与交通运输法”、“建筑与房地产法”两个特色课程组，。继续开展2+2国际大学本科学生合作培养项目。加大研究生培养力度，增加硕士生数量。"),r("br"),n._v("\n24.7 科学研究：力争有学术研究专著、报告出版。"),r("br"),n._v("\n24.8\n基地平台建设：加强现有模拟法庭、刑事侦查实验室建设。加强与四川省高级人民法院、四川省人民检察院，成都市中级人民法院等合作关系，建立稳定的实践基地。积极开拓省内外科研合作基地。"),r("br"),n._v("\n24.9\n学术交流：2009年已经正式与德国安海尔大学开展“2＋2”大学本科学生合作培养项目，将继续拓展这一国际交流与合作模式。争取每学期、年有法学学科专项学术（国际、国内）会议召开。"),r("br"),n._v("\n24.10\n发挥作用：我校法学类的教师专业结构及研究方向涵盖法学理论、法律史、宪法与行政法、民商法、经济法、刑法、诉讼法、国际法等学科，以及交通法与铁路法等学科。有近20人在中国法学会及全国性专业研究会并地方研究会中担任会长、常务理事、学术委员等职务，在仲裁机构担任兼职仲裁员，或者坚持从事与法学教育有关的法律服务工作，在法律同行中具有一定的知名度。"),r("br"),n._v("\n24.11 建设举措：加强法学特色学科方向的凝练和规划，加强师资引进工作，提升师资队伍中的博士比例和正高职称比例；构建专门的学科考核、奖励机制。")]),n._v(" "),r("hr")])}),[],!1,null,null,null);r.default=t.exports}}]);