(window.webpackJsonp=window.webpackJsonp||[]).push([[510],{872:function(n,r,v){"use strict";v.r(r);var _=v(14),t=Object(_.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  **"),r("br"),n._v("\n一级学科规划提要之"),r("br"),n._v("\n23 应用经济学"),r("br"),n._v("\n（征求意见稿）"),r("br"),n._v("\n**")]),n._v(" "),r("p",[n._v("23.1 学科现状：现有区域经济学，产业经济学，数量经济学，金融学等4个二级学科硕士点。分布在管理学院和人文学院。"),r("br"),n._v("\n23.2 建设目标：积极准备申报应用经济学一级学科硕士点，并培育省级重点学科。"),r("br"),n._v("\n23.3 学科管理负责人：贾志永 教授"),r("br"),n._v("\n23.4\n研究方向：金融市场，管理经济学与实验经济学，区域经济一体化与成渝经济区发展，轨道交通与区域经济发展，县域经济发展理论与实践，区域经济可持续发展战略与政策等研究方向。"),r("br"),n._v("\n23.5 团队建设：专任教师队伍中，具有博士学位的人数不低于80%，优秀留学回国人员比例不少于20%，国家杰出青年基金1-2人。"),r("br"),n._v("\n23.6 人才培养：加大研究生培养力度，提高生源的数量和质量。"),r("br"),n._v("\n23.7\n科学研究：学院每年在核心期刊及以上刊物发表论文不少于100篇，其中SCI、EI、SSCI收录论文不少于15篇，在国际有影响的刊物发表论文不少于5篇，出版学术专著、译著、教材不少于10部。学院每年承担国家级课题（国家自然科学基金项目、国家社会科学基金项目等）3～5项，省部级课题10项左右，承担科研项目及经费数额达到同类院校领先水平。"),r("br"),n._v("\n23.8 基地平台建设：建设金融信息安全与复杂性研究室和微观社会与经济数据调查与分析研究室。"),r("br"),n._v("\n23.9\n学术交流：继续开展与国际知名院校的交流与合作，尽量多邀请国际著名学者、专家讲学，原则上每年保证3～5人次。大力支持骨干教师到国外著名科研院所进修、学习、合作研究，原则上每年派遣6～10人次。积极加强与国内著名院校的合作交流，定期邀请国内著名专家、学者来我院讲学，原则上每年不少于10人次。"),r("br"),n._v("\n23.10 发挥作用：办出特色，办出水平，为区域经济发展做出更大贡献。"),r("br"),n._v("\n23.11 建设举措：学院拟从人才队伍建设、科研平台建设、实验平台建设、管理体制建设等方面着手开展具体的建设工作。")]),n._v(" "),r("hr")])}),[],!1,null,null,null);r.default=t.exports}}]);