(window.webpackJsonp=window.webpackJsonp||[]).push([[513],{874:function(n,r,v){"use strict";v.r(r);var _=v(14),t=Object(_.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  **"),r("br"),n._v("\n一级学科规划提要之"),r("br"),n._v("\n20 哲学"),r("br"),n._v("\n（征求意见稿）"),r("br"),n._v("\n**")]),n._v(" "),r("p",[n._v("20.1学科现状：现有科学技术哲学、伦理学2个二级学科硕士点。"),r("br"),n._v("\n20.2建设目标：力争建成科学技术哲学二级学科博士点。"),r("br"),n._v("\n20.3学科管理负责人：王伯鲁教授"),r("br"),n._v("\n20.4研究方向：技术创新与科技管理，技术哲学与工程哲学，科学哲学与科技史，教育科学技术与教育管理，应用伦理学。"),r("br"),n._v("\n20.5团队建设：引进学科建设急需的相近学科专业博士或副教授以上海外归国留学人员1-3名，团队新增晋升教授和副教授3-6名。"),r("br"),n._v("\n20.6人才培养：培养20-40位硕士。"),r("br"),n._v("\n20.7科学研究：学术团队每位成员要在省级报刊、杂志发表5篇以上论文，其中至少在CSSCI以上期刊或三大检索发表1-2篇论文；团队出版专著2-5本，教材3-5本。力争中标国家级课题1-2项；进校经费达到20万以上；获得省部级以上政府奖2-5项。"),r("br"),n._v("\n20.8基地平台建设：建立专业图书馆，未来5年，图书达到2000~3000册。建立科研专网，形成科研数据中心和科研交流平台。"),r("br"),n._v("\n20.9学术交流：参加国内访问学者，进行国内学术交流3~6人，参加国外访学，做访问学者或者联合培养博士3~4人。召开技术创新方法、技术创新管理、科技史、自然辩证法方面的国际会议3次，国内会议4次。聘请学科方向的有关专家来进行学科交流3~4次。"),r("br"),n._v("\n20.10发挥作用：重视运用科学发展观，考察中国科技经济发展历史和现状，研究面向21世纪的中国，特别是西部地区科学技术与社会协调发展中的重大理论和实践问题。"),r("br"),n._v("\n20.11建设举措：学院将以学科队伍建设为基础，以构筑学科基地、拓展优势研究领域，突出传统特色研究为根本任务，以创造出高水平的科研成果，培养出高素质、文理兼通的综合型人才，提供高质量的社会服务为目标，坚持打好基础，充分利用地域优势，锐意创新，建构科技哲学——科学技术史——文化哲学交叉学科，争取在特色建设的基础上申报博士学位授权点。")]),n._v(" "),r("hr")])}),[],!1,null,null,null);r.default=t.exports}}]);