(window.webpackJsonp=window.webpackJsonp||[]).push([[514],{875:function(n,r,v){"use strict";v.r(r);var _=v(14),b=Object(_.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  **"),r("br"),n._v("\n一级学科规划提要之"),r("br"),n._v("\n19 物理学"),r("br"),n._v("\n（征求意见稿）"),r("br"),n._v("\n**")]),n._v(" "),r("p",[n._v("19.1学科现状：现有2个二级学科硕士点：理论物理硕士点和凝聚态物理硕士点。"),r("br"),n._v("\n19.2建设目标：建成物理学一级学科硕士点。争取获得凝聚态物理博士授予权，四川省和教育部重点学科。建设应用物理学本科专业。"),r("br"),n._v("\n19.3学科管理负责人：王黎 教授"),r("br"),n._v("\n19.4研究方向：超导及强关联物理学、亚稳材料的高压合成、电介质及自旋电子材料物理、凝聚态理论和固态量子信息、高压物性与物态方程、团簇和纳米体系物理特性。"),r("br"),n._v("\n19.5团队建设：以研究方向为基础，形成科研团队。再引进2-3名高水平人才，形成新的学术带头人。新增长江学者特聘教授一名、新增国家杰出青年基金获得者1名。"),r("br"),n._v("\n19.人才培养：争取五年内使在读研究生人数达到50人左右，其中包括部分博士研究生。其中1～2名全国优秀博士论文获得者，2～3名四川省优秀博士论文获得者。培养物理学本科人才，为硕士提供生源。"),r("br"),n._v("\n19.7科学研究：科研经费达到人均15万元/年。高水平论文数量达到50篇／年。"),r("br"),n._v("\n19.8基地平台建设：建立包括超导磁浮动态测试系统、超导带材连续制备系统、动态高压下物性的测试和监控系统、高压下物理性能（包括电磁热性能）测试系统等先进设备和研究平台。"),r("br"),n._v("\n19.9学术交流：加强与国内外的合作和交流，争取国家和学校科研经费的投入，建设先进的与国际、国内合作相适应的计算机和资料环境。"),r("br"),n._v("\n19.10发挥作用：与省内其它兄弟院校的物理学科形成良性的互补关系，为校内电子科学与技术、通信工程等学科提供理论服务与优质生"),r("br"),n._v("\n源。"),r("br"),n._v("\n19.11建设举措：针对目前建设与发展中的突出问题，学院拟从学科方向、学位点布局、团队建设、实验平台等方面加强建设。")]),n._v(" "),r("hr")])}),[],!1,null,null,null);r.default=b.exports}}]);