(window.webpackJsonp=window.webpackJsonp||[]).push([[529],{891:function(n,r,v){"use strict";v.r(r);var _=v(14),t=Object(_.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  **"),r("br"),n._v("\n一级学科规划提要之"),r("br"),n._v("\n6 测绘科学与技术"),r("br"),n._v("\n（征求意见稿）"),r("br"),n._v("\n**")]),n._v(" "),r("p",[n._v("6.1学科现状：包含大地测量学与测量工程、摄影测量与遥感、地图制图学与地理信息工程等3个二级学科博士点。"),r("br"),n._v("\n6.2建设目标：一级学科进入全国排名前5位，将“大地测量与测量工程”建设成国家重点学科。"),r("br"),n._v("\n6.3学科管理负责人：李永树 教授"),r("br"),n._v("\n6.4研究方向：测绘学科目前已形成了4个稳定且具有一定前沿性的研究方向，即：高精度卫星导航定位与空间大地测量理论；\n精密工程测量与形变监测及预报技术；地理信息系统与3S集成技术；遥感数据处理与制图。"),r("br"),n._v("\n6.5团队建设：到2015年，专职教师40-50人，具有博士学位比例70%以上，长江学者1-2人，杰青1-3人，百千万人选2-4人，新世纪人选3-5人。留学回国人员比例不少于20%。"),r("br"),n._v("\n6.6人才培养：在校研究生与本科生比例到达1:1。"),r("br"),n._v("\n6.7科学研究：科研经费达到人均30万元，在论文、专著、科研获奖、学术交流、国际化、人才培养及优秀博士论文等指标上达到同期国内一流研究型大学水平。"),r("br"),n._v("\n6.8基地平台建设：拥有1个国家级研究平台。"),r("br"),n._v("\n6.9学术交流：学术交流、国际化等指标上全面达到同期国内一流研究型大学水平。建立三个层次的定期学术讲座：院士讲座、名家论坛和沙龙。努力推进国际化，与境外大学和研究机构建立长期科学研究和人才培养合作关系，开展学术团队和教授工作室建设。以二级学科为基本单位积极主办各类学术会议,参与核心期刊编审,参加各类学会。"),r("br"),n._v("\n6.10发挥作用：将“大地测量与测量工程”建设成国家重点学科，在测绘学科的几个关键研究方向与研究成果上，具有自己显著的优势和特色，获得更多国家级科研项目的支持，培养和造就新的年轻学术带头人，学术队伍中在1-2个学科拥有院士、长江学者、杰青等组成的学术带头人，拥有1个国家级研究平台。为我国测绘事业和轨道交通事业的发展培养出更多优秀的专业技术人才。"),r("br"),n._v("\n6.11建设举措：学院提出４个建设工程、6个学科基金、4个基本调整、3层次申报学科及1个机制创新。以培养高层次创新人才和提高自主创新能力为目标，瞄准世界科学技术发展前沿，抓住能够促使学科上水平、出成果等关键方面，集中力量进行建设。")]),n._v(" "),r("hr")])}),[],!1,null,null,null);r.default=t.exports}}]);