(window.webpackJsonp=window.webpackJsonp||[]).push([[564],{926:function(_,t,v){"use strict";v.r(t);var n=v(14),p=Object(n.a)({},(function(){var _=this,t=_._self._c;return t("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[t("p",[_._v("|")]),_._v(" "),t("p",[_._v("高铁—悄悄开启群发性地质灾害的魔盒"),t("br"),_._v("\n作者／林文静 (转自电子邮件)")]),_._v(" "),t("p",[t("img",{attrs:{src:"/pic/img.bimg.126.net_photo_qoM6c4PEa67WVW_GXu4LXg==_1184165227022304937.jpg",alt:""}})]),_._v(" "),t("p",[t("strong",[_._v("被无视的反对声")])]),_._v(" "),t("p",[_._v("2006年春节前，当千家万户沉浸在阖家团圆的欢乐气氛中，一位老人孤独地躺在西安交大附属二院的病床上，在弥留之际反复重复着一句话：“高铁，一定不要建……高铁，一定不要建……”两天后，他留下了最后一句：“如果非要上高铁，至少一定要用有碴道床。”便与世长辞。"),t("br"),_._v("\n这位老人，是中科院西安地球环境研究所的张拾迈教授。在中国的高铁大潮面前，张拾迈是为数不多保持着谨慎、理性的学者。经过全面、细致的研究，他得出“中国的地质不适合建高铁”的结论。认为在中国的地质条件下，高铁产生的夏尔谢夫力将导致严重的斯蒂芬金效应，引起群发性的地质灾害。在人生的最后几年，他一直致力于阻止高速铁路的盲目上马，他曾多次向铁道部和国务院写信表明自己的担忧，却屡屡遭到无视。而现在，随着这一批高铁线路的陆续通车，张拾迈教授的警告正在渐渐地不幸变为现实。")]),_._v(" "),t("p",[t("strong",[_._v("揭密高铁背后不为人知的隐患")])]),_._v(" "),t("p",[_._v("“夏尔谢夫力”，是科里奥利力的一种特殊存在形式，由俄国铁路工程师夏尔谢夫于1903年发现并引起重视。由于地球的自转，任何移动中的物体都受到一个侧向的作用力，在北半球向右，在南半球向左。火车当然也不例外，在北半球会受到向右的力，对钢轨产生挤压，钢轨又将这种作用力传给地面，从而对地面产生一个向右的切向力，这个力就是夏尔谢夫力，车速越快，夏尔谢夫力就越大。早期火车速度慢，夏尔谢夫力并未严重到需要人们加以警惕的地步，然后随着火车速度越来越快，加之建铁路的地区地质条件千差万别，在特定条件下夏尔谢夫力终于量变产生质变，并引发斯蒂芬金效应。"),t("br"),_._v("\n斯蒂芬金效应，系詹天佑的同门，耶鲁大学土木工程系毕业的年轻工程师斯蒂芬金发现的。1926年，斯蒂芬金考察落基山区的铁路线时，发现翻过山顶后高速冲下的火车，使得行车方向右侧的岩层、山崖发生断裂甚至小规模滑坡。这种地质破坏，正是因重力加速的火车产生的夏尔谢夫力引起的，而且这种破坏可以是“隔山打牛”型的，斯蒂芬金亲眼观察到与铁路线隔着数条河谷的山崖，在火车通过时产生了裂纹。因为地表岩土的刚性可以使夏尔谢夫力持续传递，远至数百公里以外，就像一粒高速运动的台球击中一列紧挨着排放的台球左侧，最右边的球却会被弹开一样。坚硬的地表岩土起到了那一列台球的作用，传递着夏尔谢夫力，直到遇到特定的地质结构使力无法继续传递，便在此时释放能量，造成地质破坏。夏尔谢夫力在一定条件下可以被山脉、峡谷等复杂地貌反射、折射而改变行进方向，使得追踪和研究它变得尤为复杂。"),t("br"),_._v("\n斯蒂芬金效应并不总是造成严重的破坏后果，它的作用效果与当地的地貌、地质有着密切的关系。遍观全世界，建有高铁的大多数国家分布在西欧和日本，它们的地质和气候条件能让斯蒂芬金效应降到最低，从而适合高铁的建设，而中国很不幸，各种地质和气候条件决定了不宜照搬欧洲和日本的高铁方案。在地形平坦的地区，夏尔谢夫力很容易被均匀地分散开，就像形状规则的蛋壳能够承受一定的撞击而不破碎一样。而在起伏不定的地形，夏尔谢夫力的传递不再齐头并进，遇到突起的山峰，深陷的河谷等，数百米范围内不同大小的希尔谢夫力就会对局部地形产生撕扯、挤压、剪切的效果。西欧地势平坦，一马平川，夏尔谢夫力的被均匀的分散了。日本虽然山脉众多，但是绝大多数人口居住于大城市中，山区只有少数农民，且由于地震频发，大多数夏尔谢夫力造成的隐患已经在地震中被一并释放了，日本的地质灾害预防做得好，房屋抗灾标准高，使得斯蒂芬金效应无法产生有破坏力的影响。同样的地形条件下，在弹性系数大的岩土中，夏尔谢夫力则更容易被缓冲、吸收。日本和欧洲的土质条件也远比中国适合高铁。日本湿润多雨自不必说，西欧也比中国大部分地区多雨，加之欧洲和日本气候比中国寒冷，土壤中的有机质被微生物分解矿化的速度慢，地质史上数十亿年的积累使得欧洲和日本的土壤中含有大量的胡敏酸、多聚六磷酸肌醇络合物等大分子有机物，弹性系数大，能起到吸收缓冲夏尔谢夫力的作用。中国气候相对干燥少雨，夏季受副热带高压作用气温往往高于32度，微生物矿化速度快，加上近半个世纪来水土保持不佳，土壤沙化、板结，更是不利于土壤有机质的形成和积累，土质弹性低，无法起到降低夏尔谢夫力的作用。地球是个扁球体，纬度越低的地点地球半径越大，受到的夏尔谢夫力也就越大。日本和西欧建有高铁的地区比中国绝大多数地区的纬度高，如日本东京的纬度接近于中国济南，法国巴黎的纬度比满洲里还要北，而德国柏林的纬度，则相当于中国最北端的漠河。中国大部分地区地处低纬度，遇到的夏尔谢夫力之强是西方的高铁建设者们不曾遇到过的。2004年，当中国政府的代表与参加竞标的西门子集团洽谈高铁和动车技术引进时，德国人以近乎傻气的严谨和善意提醒到：“贵国是否论证过高铁项目在地质灾害方面可能带来的风险？”这种宁可自砸饭碗也要为客户的安全负责的态度，却被中国代表当作了耳边风。")]),_._v(" "),t("p",[t("strong",[_._v("有心无力的智者")])]),_._v(" "),t("p",[_._v("日本和西欧的气候、地质条件能有效地减少夏尔谢夫力带来的斯蒂芬金效应，适合建高铁，而中国需要结合自己的具体国情制定铁路修建方案。张拾迈教授清楚地看到了这一点。年迈的他肩负着对整个国家和民族的责任感，背着沉重的仪器走遍了中国的山川大河，详细测定了各地的土壤弹性、硬度系数等指标，考察了地形地貌，绘制了无数张地形图，挑灯夜战演算夏尔谢夫力的作用模型，计算了数百处可能发生灾害的“问题地带”，并写成报告，多次致信铁道部和国务院，陈述了高铁的风险，却没有得到任何回应。张拾迈教授又对世界各国的地质、气候条件与中国做了详细的比较，为中国的铁路发展指明了方向——学习印度的成功经验：不盲目追求高速度，降低车速以降低夏尔谢夫力，同时以更经济的手段提高运能。印度地处南亚次大陆，气候比中国更加炎热，土壤弹性小，纬度也比中国更低，印度铁路的夏尔谢夫力也是不容小视的。印度没有为了政绩或者自豪感而盲目提高火车速度，而是从自己的国情出发，降低车速，减小夏尔谢夫力。为了提高因缓慢的车速而降低的运输效率，印度在单位车次的运输量上发掘潜力，甚至不惜把车速降到人可以自由攀爬和跳车的地步，让每一列火车都发挥它最大的运能。张拾迈认为，中国尚有8亿农民，他们并不富裕，分散居住于广大的农村和县城地区，他们需要享受铁路带来的便利，需要与外界联系。借鉴印度铁路的模式，让火车慢下来，让农民可以随时随地搭车和下车，正是他们所需要的。让中国最大的群体享受到出行的便利，他们的经济状况会随着与外界的交流而提高，才能提高整个民族的富裕水平和发达程度。在这个问题上，张拾迈教授不仅肩负着中国地质安全的责任感，他也为中国最广大群体的切身利益发出了自己的声音。2004年，张拾迈不顾家人的关心劝阻，以无比的责任感，拿着自己微薄的退休金作路费，自费去印度考察铁路，直到患上严重的肠胃疾患，倒在了异国的土地上，吊着输液瓶被抬回国。在养病期间他也闲不下来，在病榻上写成《印度国铁考察报告》，寄给铁道部和国务院。这份燃烧了一个老人的生命和健康写成的珍贵报告，却被上面有意无意的忽略了。"),t("br"),_._v("\n2005年起，张拾迈多次带着自己的报告和数据上北京，希望能向相关部门的领导陈述高铁方案背后的隐患，却始终无法如愿。最后，张拾迈把希望寄托于他心中的底线，希望高铁建设至少保证使用有碴道床——就是平常我们所见的有石子铺垫的铁路。石子可以对夏尔谢夫力起一定的缓冲作用，而今天我们的高铁建设中普遍采用的无碴道床，把钢轨硬生生钉在水泥地上，则会给夏尔谢夫力带来火上浇油的效果。2005年底，劳苦奔波的张拾迈终于支持不住，病倒了，这一病就再也没有起来。")]),_._v(" "),t("p",[t("strong",[_._v("潘多拉之盒已经慢慢开启")])]),_._v(" "),t("p",[_._v("在2006年以前，中国大部分铁路的运营时速在120公里以下。今天，随着一批时速220，350，甚至380公里高铁的建成通车，夏尔谢夫力和相应造成的斯蒂芬金效应相比过去是飞跃性的。自2009年下半年起，中国的地震灾害变得异乎寻常的频繁。"),t("br"),_._v("\n2009年12月16日，武广高铁建成通车。2010年2月6日，郑西高铁建成通车。2010年5月12日，成灌高铁建成通车。2010年2月25日，云南楚雄发生5.1级地震。4月14日，青海玉树发生7.1级地震，造成数千死亡。8月7日，甘肃舟曲发生特大泥石流，伤亡惨重。8月14日，四川汶川发生泥石流，造成41人失踪。2010年4月27日，广东梅州地陷，出现400平米巨大天坑，深度超90米，四川宜宾更是在7天之内发生数十次地陷，产生26个深不见底的天坑。7月19日，正值长江洪峰期的九江大堤，发生数百米宽的崩岸。这些突然增多的地震灾害冥冥中仿佛有一种必然，与高铁究竟有着怎样的联系，也许只有逝去的张拾迈教授能告诉我们。")]),_._v(" "),t("p",[_._v('style="DISPLAY: block; FILTER: Alpha(opacity=100,style=3); WIDTH: 210px;\nHEIGHT: 300px; TEXT-ALIGN: center"')]),_._v(" "),t("hr")])}),[],!1,null,null,null);t.default=p.exports}}]);