(window.webpackJsonp=window.webpackJsonp||[]).push([[579],{940:function(v,_,t){"use strict";t.r(_);var n=t(14),p=Object(n.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("|  |")]),v._v(" "),_("blockquote",[_("p",[v._v("**怀 老 韩"),_("br"),v._v("\n文／庸 老"),_("br"),v._v("\n**")]),v._v(" "),_("p",[v._v("昨日接唐山电话，告知一噩耗：老韩于9月19日因病辞世，享年87岁。呜呼，又一老友仙逝矣！")]),v._v(" "),_("p",[v._v("韩有悌，校图书馆研究员，离休干部。在我校工作已数十年，平日勤奋敬业，雅好读书，于书无所不窥，博闻强记，兼晓几门外语，对于去图书馆查考资料的同志，倘有咨询，无不竭诚相助，是以其学问、人品在教师队伍中的口碑颇佳焉。"),_("br"),v._v("\n上世纪80年代，老韩曾兼任铁路各院校图书馆合办馆刊（忘其名）之总编辑，在各校图书馆之知名度也颇高也。")]),v._v(" "),_("p",[v._v("老韩性情平和，虽讷于言辞，待人却诚恳有礼，颇有人缘。因与笔者同是北京人，又同居单身宿舍，时相往还，遂成至交。94年余退休返唐，老韩亦携夫人来唐山定居，以是朝夕过从，并时常同去唐山各处觅踪怀旧，也算是老来一乐焉。")]),v._v(" "),_("p",[v._v("老韩自幼大约读书太过，以致眼睛受损，平日戴副高度老花镜，如同玻璃瓶底，益增其学究模样；然而看物犹如雾里看花，走路时遇到熟人，即使已到眼前，若不出声，犹认不出也。到了晚年，几同盲人，看电视只如听收音机，书报无论矣，幸有其女儿陪侍，尚不寂寞。老韩平日生活简朴，吃喝玩乐皆非所好，饭食如同北京下层人民习惯，炸酱面就是不错的了，饺子则是最高享受焉。我尝调侃他说：你连卤煮火烧、水爆肚都不知道，焉得算北京人耶？")]),v._v(" "),_("p",[v._v("韩夫人梁女士，早年亦为唐院图书馆馆员，与老韩同事，后调往兰院，其先生早逝。80年代末，老韩亦鳏居，二人遂又相聚，友朋皆谓真是白首良缘也。图书馆操持其婚事者命余撰一贺辞，欣然从之，乃题《长相思》一阙以应：")]),v._v(" "),_("p",[v._v("**《长相思●贺韩、梁二老喜结良缘》1992"),_("br"),v._v("\n**"),_("br"),v._v("\n往事悠，岁月稠，旧友如今做新俦，情意更相投。"),_("br"),v._v("\n离退休，兴未休，白发青春度金秋，堪比少年游。")]),v._v(" "),_("p",[v._v("其后二人同来唐山定居，至2000年，世纪末矣，竟悉二人终不能偕老，不胜感慨，乃复用前调，叠韵填辞一阙以赠：")]),v._v(" "),_("p",[_("strong",[v._v("《 无 题 》")])]),v._v(" "),_("p",[v._v("烦恼悠，口角稠，冰炭如何做伴俦，劳燕竟分投。"),_("br"),v._v("\n兴也休，事也休，却道天凉好个秋，黄昏独自游。")]),v._v(" "),_("p",[v._v("2001年余回上海定居，临别前老韩来舍间送行，相约北京再会，不料此别竟成永诀。噩耗传来，不胜悲怀，爰记老友数事，以为纪念。呜呼哀哉，尚飨！")]),v._v(" "),_("p",[v._v("庸老 庚寅秋分于沪上")])]),v._v(" "),_("table",[_("thead",[_("tr",[_("th")])]),v._v(" "),_("tbody")])])}),[],!1,null,null,null);_.default=p.exports}}]);