(window.webpackJsonp=window.webpackJsonp||[]).push([[608],{971:function(_,v,n){"use strict";n.r(v);var r=n(14),b=Object(r.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|  |")]),_._v(" "),v("blockquote",[v("p",[_._v("父亲的诗集"),v("br"),_._v(" "),v("strong",[_._v("整理／王柢小女儿：其实")]),v("br"),_._v("\n![](/pic/img.bimg.126.net_photo_g7cPP-\nUkpygL69v0pMrUVA==_5412200852192717970.jpg)")]),_._v(" "),v("p",[v("strong",[_._v("< 序>")])]),_._v(" "),v("p",[_._v("在母亲病重住院时，父亲为母亲写下一首首诗，满怀着深情，期待着母亲的康复归来。当母亲看到这些诗时，倍感熟悉和亲切。母亲很开心。我抓拍了母亲开心的笑容。回到家后，在电视机上向父亲展示了母亲手持诗稿满脸笑容的照片。为此，父亲兴奋了好几天。也就是从这个时候起，我发现了父亲的很多诗稿。问他时，他说是随便写写的。小时候记得父亲喜欢写诗，偶尔也看到一两篇。在我的感觉中，他只是抒发一下当时的情怀，写完后也就放在了一边。经过历届的政治运动和唐山大地震的浩劫，有的也就不知了去向。")]),_._v(" "),v("p",[_._v("料理完母亲的后事，我记起了父亲的诗。他告诉我，可以印一些给我的小叔叔寄去。我开始整理他的诗。越整理越觉得感兴趣。不知不觉就整理了很多。大多数内容都是跟家庭成员有关的。还没等我把诗发给四面八方的家人，就迎来了父亲百年华诞的准备工作。在整理他的资料的时候，我发现了他在清华校庆时刊登在校庆册子上的诗。又发现了曾登在西南交大刊物上的诗。这样，加上以前发现的，能搜集到的共有几十首。至于有多少压在唐山的废墟中，已无人知道。")]),_._v(" "),v("p",[_._v("一半的诗所基于的年代是在我出生前，也就是反右前。可是这些诗，象时光隧道，将我带到以前，我似乎能触及到那些年代。我被那一首首美妙的诗感动。一百年的历史尽显眼前。伴随着中华民族的苦难与崛起，再现出父亲人生的每个阶段，长长的路，坎坷蜿蜒，有惊险，有灾难，多少次九死一生，但没有怨言，没有仇恨，只有宽容、理解和感恩。")]),_._v(" "),v("p",[_._v("诗中流露着对母校的深情，对老师，对师长的感激与崇敬，对妻子的感恩，对家人的爱。虽然从57到78年被整了二十一年，但从诗中仍可读出他对政治运动回眸时的明了、平淡，并对选择海外归来报效祖国的无悔。近二十年的诗中，可品味出他对祖国不断强盛的欣慰，也反映出他生活中的幸福。")]),_._v(" "),v("p",[_._v("为了让更多的人，实际上是更多的中年人和年轻人读懂这本诗集，享受这本诗集，征得作者的同意，部分地方我加上了注释。在我加注解的地方都写明了：“子女注”。凡是没有注明“子女注”的地方，都是作者自己的注释。感谢学校，感谢土木学院，感谢铁道系，感谢所有的领导、同事与朋友，感谢为此诗集辛勤工作的人们。我们可以想象，当将这本诗集放在这位百岁老人的手中时，他会有多么的开心！")]),_._v(" "),v("p",[_._v("小小花絮：在整理他的诗稿时，因为有的字是繁体的，加之老人颤抖的笔迹，难以确认。我就将字打的大大的，拿去问他。他拿起放大镜，看了看，问我：“这是谁写的啊”？我说：“是你写的”。他说：“我写不出这样的诗啊”。我随即将他的原手稿给他看。他慢慢看过来以后说：“这是我的笔迹”。等他再慢慢看过以后，诗中的历史事件和人物使他明白了，这是他写的诗。他说：“哎呀，我能写这么好的诗啊”！当他知道我找到了他那么多老诗以后非常高兴，不断的说：太好了，太好了！")]),_._v(" "),v("p",[_._v("王柢小女儿：其实"),v("br"),_._v("\n2010年4月23日")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img11.bimg.126.net_photo_MEXflmD3sRRSPpH_jJKjMw==_4300374694186692780.jpg",alt:""}})]),_._v(" "),v("p",[v("strong",[_._v("（一）人生路")])]),_._v(" "),v("p",[_._v("![](/pic/img.bimg.126.net_photo_g7cPP-\nUkpygL69v0pMrUVA==_5412200852192717970.jpg)")]),_._v(" "),v("p",[v("strong",[_._v("清华园——培养与熏陶")]),v("br"),_._v("\n（1929-1933年间）")]),_._v(" "),v("p",[_._v("小河潺潺小桥横，桥上匆匆少年行。"),v("br"),_._v("\n走向科学真理境，追踪人世至理穷。"),v("br"),_._v("\n周、施、朱、夏开思路①，约翰 “喝雷”健体肱②。"),v("br"),_._v("\n谆教萦瀯六十载，铮铮德范印终生。"),v("br"),_._v("\n①母校师辈与学长，我所敬仰者，可以千计。周培源、施嘉炀、朱自清、夏坚白仅是我受业与受益的先生中之代表耳。"),v("br"),_._v("\n②约翰－马老，体育老师。“喝雷”－“Hurry up”，是最常听到他的一句话。")]),_._v(" "),v("p",[v("strong",[_._v("跋涉山")]),v("br"),_._v("\n（1933-1944年间）")]),_._v(" "),v("p",[_._v("大江滚浪身旁过，经纬准平度不偏。"),v("br"),_._v("\n无量山峦云海绕，澜沧水激濯缨尖。"),v("br"),_._v("\n路修万里终有尽，建设中华事无边。"),v("br"),_._v("\n科技兴邦虽素志①，奈他权贵太贪婪。"),v("br"),_._v("\n1933年毕业后在扬子江水利测量队，后参加浙赣铁路江西段的修建工作。3年后，为了支援抗战，又参加湘桂铁路和滇缅铁路的修建。"),v("br"),_._v("\n子女注：①素志—素来就有的志向。")]),_._v(" "),v("p",[v("strong",[_._v("日寇")]),v("br"),_._v("\n（1937-1945年间）")]),_._v(" "),v("p",[_._v("烧杀夺掠行如兽，屠我胞民占我畴。"),v("br"),_._v("\n崇诲舍身轰盗舰，①李、闻义气激神州。②"),v("br"),_._v("\n台庄一击扬士气，平型再捷振忾仇。"),v("br"),_._v("\n文武我无歼敌力，修通长管助军油。③"),v("br"),_._v("\n① 沈崇诲学长。后来他驾驶着飞机直接撞向日本的军舰。"),v("br"),_._v("\n② 李公朴、闻一多先生虽在战后的45年，但壮烈性质相同。"),v("br"),_._v("\n③ 指中国到印度的一段军用输油管，抗战所用。当时的油管工程异常困难艰苦。")]),_._v(" "),v("p",[v("strong",[_._v("记西南行")]),v("br"),_._v("\n（解放前后）")]),_._v(" "),v("p",[_._v("一心建设西边陲，不料敌寇自西来。"),v("br"),_._v("\n铁路大军齐既业，建筑机场剩敌夷。"),v("br"),_._v("\n妻女安全为事大，先至昆明再入渝。"),v("br"),_._v("\n数日颠簸尽灾累，初见公翁如父女。"),v("br"),_._v("\n安心致力输油管，幸得应试新胜利。")]),_._v(" "),v("p",[v("strong",[_._v("风险后到唐山任教")]),v("br"),_._v("\n（1949年间）")]),_._v(" "),v("p",[_._v("（一）"),v("br"),_._v("\n抗命违迁隔岸岛①，身肩重任独木舟。"),v("br"),_._v("\n急流岂顾惊涛险，变换无劳父老忧②。"),v("br"),_._v("\n老校③诚邀情勿却，新师半世换白头。"),v("br"),_._v("\n探研路径原吾志，粉笔生涯孺子牛。")]),_._v(" "),v("p",[_._v("（二）"),v("br"),_._v("\n归来④兴建探研业，不谙仕途不时宜。"),v("br"),_._v("\n幸有恩师提助力，更得好友借枝栖。"),v("br"),_._v("\n官方谕令急又切，警吏威胁倍相逼。"),v("br"),_._v("\n子弟兵员解救日，轻松北上新课题。")]),_._v(" "),v("p",[_._v("注：1947年受旧交通部任命兴建一所几乎从无到有的材料试验所。二年后因违反将实验所迁至台湾的命令，遭到军警特务的威胁，处境险恶。幸得程孝刚校长（上海交大）与同学赵九章帮助，更幸上海及时解放，得以将实验所全部财产交给新中国。")]),_._v(" "),v("p",[_._v("当时并无政治概念，只从试验所人员与设备的安全出发。想共产党是得人心的，对国家财产必然物尽其用。数年之后，经过学习，尤其在五十年之后的今天，自己庆幸当时的决定是对的。"),v("br"),_._v("\n①台湾"),v("br"),_._v("\n②违背命令，保住实验人员与设备的安全，虽涉险恶，未致损失。堪以告慰父老。"),v("br"),_._v("\n③唐山铁道学院"),v("br"),_._v("\n④美国归来")]),_._v(" "),v("p",[v("strong",[_._v("反右歌")]),v("br"),_._v("\n（1958年于唐山）")]),_._v(" "),v("p",[_._v("天旋地转险霾雾，是非曲直重诠注。"),v("br"),_._v("\n直言不逊罪应得，落井下石不可恕。"),v("br"),_._v("\n早争理、晚批斗，大会小会无其数。"),v("br"),_._v("\n扶摇直上干云霄，为民涉险全不顾。"),v("br"),_._v("\n此呼彼啸一何怒，认罪喃喃一何苦。"),v("br"),_._v("\n士杀不辱是常情，生死之间堪踌躇。"),v("br"),_._v("\n霎时间，父母均在且忧心，妻不忍别儿待哺。"),v("br"),_._v("\n独坐间，忽闻叩门声，漫谈书画与李、杜①"),v("br"),_._v("\n又有远自陇西来，带来诸弟拳拳嘱。"),v("br"),_._v("\n有意友情非虚轻，风云人事宜思度。"),v("br"),_._v("\n又亿起，沪上可敬指路人，子弟救我出险境。"),v("br"),_._v("\n当年信心未曾减，今朝浊水源何故？"),v("br"),_._v("\n吁嗟兮，难道世时真突变，千思百虑疑无路。"),v("br"),_._v("\n①子女注：李白、杜甫")]),_._v(" "),v("p",[v("strong",[_._v("“文化大革命”")]),v("br"),_._v("\n（1966-1976年间）")]),_._v(" "),v("p",[_._v("飒爽英姿少年兵，横眉立目辫上缨。"),v("br"),_._v("\n“蛇神”列队觳觫立，“牛鬼”不分左右行。"),v("br"),_._v("\n可叹三君生性烈，犹闻一曲嚎歌声。"),v("br"),_._v("\n忽听机坠温都汗①，又悉“则天”②已判刑。")]),_._v(" "),v("p",[_._v("注:“文革”期间诸老人也受到冲击。69年左右，伍老病卧医院时，我曾去探望。已经垂危，但神志清楚，情况很凄惨。见到我，显得很慰藉，知道自己已不久于人世，果然未多时即默默逝去。其它几位的结局也类似。")]),_._v(" "),v("p",[_._v("“文革”本质是我国社会发展中的一段反常经历。对于被利用的年轻人来说，是由无知、盲从到理智、思考的过程，对于“牛鬼蛇神”来说，可以说是在一场闹剧中扮演了倒霉的角色而已。至于那些还未及醒悟已经逝世的长辈和性情燥烈，不等事情弄清即轻生而去的朋友们，他们为教育、为建设奋斗一生，结果糊涂地死去，何等惨痛。"),v("br"),_._v("\n今天，在庆祝新中国五十周年大典之际，我希望青年人为国家的发展、人民的幸福，永远理智，永远正直。"),v("br"),_._v("\n子女注：①林彪坠机事件"),v("br"),_._v("\n②指江青")]),_._v(" "),v("p",[v("strong",[_._v("大地震")]),v("br"),_._v("\n（1976年）")]),_._v(" "),v("p",[_._v("震天巨响撼大地，顷刻全城化灰砾。"),v("br"),_._v("\n正待寻援妻女时，自身被致阎罗狱。"),v("br"),_._v("\n深埋黑夜八时整，重见光明一丝气。"),v("br"),_._v("\n子弟兵员树殊功，邻居救我还尘世。")]),_._v(" "),v("p",[_._v("子女注：唐山地震将王柢及夫人，还有六岁小外孙埋在了倒塌的房屋中。幸亏得到邻居，解放军，白衣天使的救助，使得被深埋8小时后出来奄奄一息的他得到了及时的救助。")]),_._v(" "),v("p",[v("strong",[_._v("改 正")]),v("br"),_._v("\n（1978年）")]),_._v(" "),v("p",[_._v("当年罪状六七条，不意而今一笔消。"),v("br"),_._v("\n二十一年尘覆面，古稀花甲尽弯腰。"),v("br"),_._v("\n错而能改非易事，往者知音却难交。"),v("br"),_._v("\n但愿余生延时日，追回逝月不辞劳。")]),_._v(" "),v("p",[v("strong",[_._v("欧陆行之一 西德")]),v("br"),_._v("\n（1979年）")]),_._v(" "),v("p",[_._v("希魔霸道邦①，争战被夷平。"),v("br"),_._v("\n不及三十载，尽为现代城。"),v("br"),_._v("\nUN②研讨会，介绍中土情。"),v("br"),_._v("\n30年建路事，足使四座惊。"),v("br"),_._v("\n德人问印象，坦言不钦赏。"),v("br"),_._v("\n相知自真实，解意共欣畅。"),v("br"),_._v("\n韩吏殷勤难却拒，印员虚傲堪挑剔。"),v("br"),_._v("\n高校育才颇有方，③东道至今传挚意。"),v("br"),_._v("\n子女注：①指德国"),v("br"),_._v("\n作者注：②UN指联合国。政府推荐以个人身份参加联合国铁路研讨会，参观国际交通技术博览会。"),v("br"),_._v("\n③访问汉诺威、亚琛和慕尼黑三技术大学印象。")]),_._v(" "),v("p",[v("strong",[_._v("欧陆行之二 巴黎.里昂")]),v("br"),_._v("\n（1981年）")]),_._v(" "),v("p",[_._v("千载帝国都，积成艺术宫。"),v("br"),_._v("\n画雕琳琅目，建筑叹奇功。"),v("br"),_._v("\n数理学科已傲世，三百时速又称雄。"),v("br"),_._v("\n惜乎哉，民性聪明不严谨，不敌日曼②两次攻。")]),_._v(" "),v("p",[_._v("作者注：①应邀出席法国高速铁路讨论会。"),v("br"),_._v("\n子女注：②日曼指日耳曼民族，即德国")])]),_._v(" "),v("table",[v("thead",[v("tr",[v("th")])]),_._v(" "),v("tbody")])])}),[],!1,null,null,null);v.default=b.exports}}]);