(window.webpackJsonp=window.webpackJsonp||[]).push([[618],{981:function(t,n,r){"use strict";r.r(n);var o=r(14),s=Object(o.a)({},(function(){var t=this,n=t._self._c;return n("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[n("p",[t._v("|")]),t._v(" "),n("p",[n("strong",[t._v("向唐山市“关心唐山交大的志愿者们”致敬！"),n("br"),t._v("\n文/镜湖客 2010-4-27"),n("br"),t._v("\n（本文应作者要求从电子邮件转载）")])]),t._v(" "),n("blockquote",[n("p",[t._v("这次回到唐山，有幸结识了志愿者们。近几年来，他们一直为保护和利用唐山交大原校址，为促进西南交大与唐山市的密切合作而呼吁，而奔走。他们之中有的是校友，有的是原交大教职工子女，也有的既不是校友，又不是子弟，仅仅是从父辈传承下来的对唐山交大的热爱。苏伟就是他们之中的杰出代表。他本人现在是天津银行唐山分行的副行长，工作很忙；但是，几年来，他抽出大量时间参与关心唐山交大的活动。在他的办公室里，有大量的唐山交大校史资料，他对唐山交大的了解，如数家珍，比我们一些老师还要熟悉。赵勇书记才上任，他立即写信反映保护、利用唐山原校址的意见。多年来，他们在环渤海新闻网上建立交大原校址论坛，发表看法，扩大社会影响。这次我们到唐山，他们几乎一直参加我们的活动，展示珍贵的老唐院平面图，加印上百张老唐院70年代地震前的照片，送给每一位老师，并且到每个房间征集老师的签名，作为这次返唐活动的珍贵历史资料。而我校都没有人想到这一点。总之，这些可敬的“志愿者们”令我们十分感动。"),n("br"),t._v("\n向唐山市“保护、利用交大原校址志愿者们”致敬！")])]),t._v(" "),n("hr")])}),[],!1,null,null,null);n.default=s.exports}}]);