(window.webpackJsonp=window.webpackJsonp||[]).push([[663],{1023:function(_,v,t){"use strict";t.r(v);var n=t(14),p=Object(n.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[v("em",[_._v("引用")]),_._v(" "),v("a",{attrs:{href:"http://epei1008.blog.163.com/",target:"_blank",rel:"noopener noreferrer"}},[_._v("老顽童"),v("OutboundLink")],1),_._v(" 的 "),v("a",{attrs:{href:"http://epei1008.blog.163.com/blog/static/23524860200911993553877",target:"_blank",rel:"noopener noreferrer"}},[_._v("引用\n钱学森逝世与周济下台"),v("OutboundLink")],1)]),_._v(" "),v("p",[_._v("|  |  |")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_qoM6c4PEa67WVW_GXu4LXg==_1184165227022304937.jpg",alt:""}}),v("br"),_._v("\n钱学森逝世与周济下台"),v("br"),_._v(" "),v("strong",[_._v("文／许锡良 2009-11-01 07:30:32")])]),_._v(" "),v("p",[v("strong",[_._v("核心提示：历史竟然这样巧合，一个以98岁高龄驾鹤仙逝，一个“洲际导弹”落地，竟然在同一天发生。天意，还是历史的巧合？无论怎样都是值得纪念一下的。")])]),_._v(" "),v("p",[_._v("（本文写于2009年10月31日）请记住今天，也即2009年10月31日，历史上将要记载的两件事：一件是著名导弹大师钱学森逝世，另一件是号称“洲际导弹”的教育部长周济下台。这两件事，前一件是突发的，也是令人悲伤的，后一件是构建的，却是令人大快人心的。但是他们却在这几年来，是那样微妙地密切联系在一起。前者虽然是搞导弹的大师，但是论在教育上的影响力，还远远没有教育界流传甚广的“洲际导弹”（周济捣蛋）来得凶猛。")]),_._v(" "),v("p",[_._v("由一个工程研究人员，一路平步青云，短短的十来年时间由一个普通的大学教师当到教育部长，官运确实亨通。但是他做教育部长六年来，却在教育界得了“洲际导弹”的外号，也不是没有由来的。在他主政教育部期间，如火如荼的新课程改革遭遇重创，已经奄奄一息。且看他主持教育部期间的主要捣蛋业绩。大力搞大学合并之风，大搞大学城，这股风一直刮到中小学，全国的大学、中学、小学，求大求全，打造教育的航空母舰，把全国的教育弄得鸡飞狗跳，劳民伤财。弄得那些农村的孩子每学期都必须缴数千元学费，辛苦地跑到“教育城”里接受教育。仅教育城，就让中国的大学欠款数千亿元。大学被办成了“学习集中营”。接着又在大学大搞教育评估之风。逼人造假，逼良为娼，其结果就是教育部权威空前膨胀，连下派到大学的一个临时雇员，也牛气得如同钦差大臣，一个什么也不是的小姑娘，惊得大学校长们跑到机场迎接，还众星捧月一般伺候着。在中学则大搞示范性高中评估，也是劳民伤财，折腾个够，大搞教育政绩工程。这也是捣蛋。但是，更捣蛋的还在后头，让“文革”时的革命样板戏京剧进课堂，勒令全国必须派教师进京学样板戏。刚刚学完，又虎头蛇尾，无声无息了。然后又是教育部发出指令要求全国的中小学生统一跳集体舞。再就是最近的“班主任有权批评学生”的规定出台。一直数下去，感觉简直一个比一个愚蠢弱智，令人发笑，让人发指。但是，教育界这些东西就这样在他任上折腾了六年之久。")]),_._v(" "),v("p",[_._v("直到真正的导弹大师钱学森在病床上面对前往看望他的温家宝总理几次三番地忧心忡忡说出了临终前一个难题，是的，他要为中国的教育事业尽最后一份力量。他给总理提出了一个值得深思的教育问题，这就是著名的“钱学森教育难题”：为什么中国的学校，培养不出杰出人才？这是一个严酷的现实。六十多年来，中国与诺贝尔奖无缘，但这还不是最令人伤心的，现在,中国那么多的孩子读书越来越难，教育的地区差距，学校差距越来越大，教育越来越不均衡,追求规模效应,离教育以人为本的目标越来越遥远。这种不均衡无疑是在愈演愈烈的评估风刮起来的加剧的。教育评估检查之风，折腾得整个教育界鸡飞狗跳，人心惶惶。“办人民满意的教育”呼声越来越急促，越来越响亮，但是“周济捣蛋”照捣不误。钱学森的教育难题令温总理几次三番提及，这两年来已经成为了温总理的在中国教育问题上的一大心病。但是周济主持下的教育部似乎总是无动于衷。还在国庆六十周年前一天，教育部还发表声明：中国高校的科研竞争力排名世界第五，因为我们发表的论文数，已经排美国、英国、日本、德国之后，成为世界第五大论文国。将发表的论文数与科研竞争力混为一谈。忽悠，继续忽悠。但是终于忽悠到头了。")]),_._v(" "),v("p",[_._v("教育不是随便什么人都可以搞的，教育部长也不是随便人都能够做得好的。温总理一言中的：\n"),v("strong",[_._v("中国的教育需要一批真正的教育家来办，这样的教育家要有自己的远大理想与坚定信念，要能够经住名利的诱惑，能够一往无前，目光远大地坚持自己的办学思想。")]),_._v("\n但是，放眼中国，这样的教育家在哪里呢？中国的教育有周济在捣蛋，就不要指望这样的教育家出来。当然，周济捣蛋决不是一个孤立的现象，那后面有盘根错节的复杂利益关系，有着思想观念的沉疴，还有教育体制上的沉重负担。这些烂帐当然不能够全部算到周济捣蛋上去，但是，这些问题周济捣蛋无疑是雪上加霜，推波助澜的重要力量。")]),_._v(" "),v("p",[_._v("钱学森总体上是令人敬佩的，虽然他在特殊的“大跃进”期间在《中国青年报》1958年6月16日第四版上曾经写过一篇论证亩产蔬菜与粮食可达160万斤的荒唐文章＿＿《粮食亩产量会有多少》。但是考虑到那样特定的政治背景，我们也可以原谅一下他。另外，他说的导弹研究业绩都是在马列思想的指导下做出来的，这样说也许有他的政治上的考虑。这些当然也是钱导弹的幽暗之处。但是，他一个导弹专家，论证农业科学上的事情，导弹研究也是马列指导的结果。谁信信去。因为，他的导弹研究成果是在美国搞出来的。美国的大学与实验室是不兴马列指导的，稍为正常的人拿脚丫子想想都能够想出，要是导弹是用马列指导出来的，那还要美国实验室干什么？还要那么大老远跑到美国去学这学那，学完还要待上十几年进一步深造？钱学森自然也有他的局限，毕竟他只是一个导弹专家，不能够强求过多。但是，他能够搞出导弹来，特别是在临终前这几年，念念不忘记中国的学校没有培养出杰出人才，引发总理一再思考，这就是值得人们敬佩的。")]),_._v(" "),v("p",[_._v("当然，周捣蛋下台与钱导弹的难题是不是有关，这就不好猜测了。不过，周济在届任未满时，就提前下课，肯定是与他把中国的教育搞得如此弱智且一塌糊涂有关的。前几天还在给挂职锻炼的校长讲课时说到，中国的教育被搞成这样，周济捣蛋不会太长久了，没有想到才三天，真的就下台了。")]),_._v(" "),v("p",[_._v("今天历史竟然这样巧合，一个以98岁高龄驾鹤仙逝，一个“洲际导弹”落地，竟然在同一天发生。天意，还是历史的巧合?无论怎样都是值得纪念一下的。")]),_._v(" "),v("p",[_._v("人民网北京10月31日电（记者常红 高星）第十一届全国人大常委会第十一次会议今天下午在北京闭幕，会议决定免去周济教育部部长职务，任命袁贵仁为教育部部长。")]),_._v(" "),v("p",[_._v("周济部长，虽然他在实行九年制义务教育等工作方面的功绩我们不能抹去，但是从他被免职的情况来看，显然，国民对他在任期间的表现不是很满意！")]),_._v(" "),v("p",[_._v("在中国诺大的一个国家，教育问题也是一个大问题，有戏称，教育问题也是新中国的“新三座大山之一”，可以想象，中国的教育却也存在问题。")]),_._v(" "),v("p",[_._v("说到教育，我们不能不想到许多问题，\n"),v("strong",[_._v("大学生就业困难、穷人上不起学、学术腐败、剽窃成风、中国范围内的学术质量低劣、择校费、教育乱收费……。诸多的问题，已经让中国的教育成了一个半死不活的动物。")])]),_._v(" "),v("p",[_._v("前不久，人大校长还放话说 "),v("strong",[_._v("“中国的博士基本都在官场”，可以想象，中国教育的目的不是作为社会的良心，而是成为一些热门升官发财的手段。")]),_._v("\n许多人连教育的方向、出路在那里都不知道，放眼我们的大学校园，基本上充斥了学而优则仕的理念，几千人为了一个公务员而奋斗。大学的良心也好像不存在了，学文的，没有了文人的味道；学理的，也没有了科研的精神与态度。很多人满脑子想的就是些怎么样去讨好人、怎么样去用尾巴巴结人、怎么样去……")]),_._v(" "),v("p",[_._v("如今的中国教育，其实到了非改变不可的地步，为什么没有人能当选诺贝尔，为什么出不了大师级的人物，才导致我们这个社会对大师有无比的敬仰。")]),_._v(" "),v("p",[_._v("是时候作出调整了，该做什么的做什么去，搞学术的就静下心来认真研究，你搞行政的就认真为人民服务，各就各位，不就做到了教育的和谐吗！")]),_._v(" "),v("p",[_._v("新的部长，我现在也不对你有任何幻想，要知道这可是个苦差事，只有你真正做好了，作出了成就，让人看得见，那才是你的本领，到时候赞扬你也不为迟！")]),_._v(" "),v("p",[_._v("教育体制改革一直是我们近年来讨论最热烈的一个问题，从高考要不要取消，到文理分班要不要取消。很多学者都在深思，中国现在的教育体制是否还能适应我们现今的要求？是否还能培育出我们所需要的建设性人才？")]),_._v(" "),v("p",[_._v("作为一个当代的大学生，是现今的教育体制，让我从农村走入了大城市，对于祖祖辈辈都是农民的农村孩子而言，读书似乎成为我们走出自己祖辈狭小的生活圈，走向成功的必由之路！但是我本人却并不太赞成这个观点，诚然读书对于农村的孩子来说是条不错的出路，但当我们把他当作唯一一条出路来说，我们则走进了一个思维的误区！")]),_._v(" "),v("p",[v("strong",[_._v("通过高考，这种全国性的选拔性考试，这对于没钱，没势的农村孩子来说，这是最公平的一条道路，因此我坚持保留高考，因为钱、权的力量我们每个人心中都有数！")])]),_._v(" "),v("p",[_._v("但是，作为我们现在的大学教育模式来说，我则非常之不满意！国外的大学教育是精英式的教育，可我们的教育确实在某些方面的的确确不如欧美国家！从诺贝尔奖的颁发我们可以看出！")]),_._v(" "),v("p",[_._v("现任教育部部长袁贵仁，是否在任期间能作出突出贡献，是不是中国教育的春天就会到来，我们拭目以待！")]),_._v(" "),v("hr"),_._v(" "),v("blockquote")])}),[],!1,null,null,null);v.default=p.exports}}]);