(window.webpackJsonp=window.webpackJsonp||[]).push([[666],{1029:function(n,r,v){"use strict";v.r(r);var _=v(14),t=Object(_.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  |  |  |")]),n._v(" "),r("p",[r("strong",[n._v("《唐院杂忆》五")]),r("br"),n._v("\n文／庸 老")]),n._v(" "),r("p",[n._v("以前拉杂地写了几则“唐院杂忆”，要在怀旧，因为“唐院”早已迁蜀并更名为西南交大焉，其唐院、唐山之陈年旧事、故地昔人，亦只待成追忆而已矣。却不料峰回路转，喜闻“唐院”与唐山久违多年之后，重修旧好——西南交大已于日前与唐山市签订了框架协议，即将在唐山市谋求新的发展焉。此事《唐山劳动日报》和河北《燕赵都市报》俱有报道，兹摘录两报对西南交大陈春阳校长的采访报道如下："),r("br"),n._v(" "),r("strong",[n._v("《唐山劳动日报》11月30日")]),r("br"),n._v("\n“如果条件允许的话，我们将尽量做大规模，保障办学质量，为唐山的人才培养提供很好的支撑和服务。”陈春阳对西南交通大学与我市的合作表现出了浓厚的兴趣和深切的期望，他说，“这是唐山人的夙愿，也是学校唐山籍教师的心愿。”"),r("br"),n._v("\n西南交大拥有１１３年的历史，其中有６６年在唐山，曾创造了唐山铁道学院的辉煌，成为中国近代史上名声最响的大学之一。这次来唐山，耳闻目睹了唐山近年来的快速发展，特别是曹妃甸建设的宏伟蓝图，陈春阳为此深表高兴：“交大目前有２５００名教师，其中有一半在唐山待过，交大人有着很深的唐山情结，我们希望能够放大这种情结，带着这份特殊感情，用我们的人才培养和科技力量为唐山的发展多做一些工作，实现互相支持、共同发展的美好愿望。”"),r("br"),n._v("\n离开唐山47年后，以唐山交通大学为前身的西南交通大学将把研究院和研究生院分院再次建设到唐山，西南交通大学还与唐山市政府签署了全面战略合作框架协议，双方在唐山铁道学院旧址共同建设纪念园，以纪念半个多世纪的难忘发展历史。\n……"),r("br"),n._v(" "),r("strong",[n._v("《燕赵都市报》11月30日")]),r("br"),n._v("\n113年校史不忘唐山辉煌"),r("br"),n._v("\n11月27号，西南交通大学校长陈春阳一行来到唐山市参观考察，陈春阳表示，唐山是西南交大的“故乡”，西南交通大学是我国近代最早的大学之一，在它113年的历史中曾有66年在唐山度过，那也是学校历史上最为辉煌的一段时期。"),r("br"),n._v("\n在唐建立西南交大研究院分院"),r("br"),n._v("\n参观考察后，西南交通大学与唐山市签署了全面战略合作框架协议，双方将按照“真诚合作、讲求实效、优势互补、共赢发展”的原则，在科研、经济、技术、教育等方面，有组织地开展多领域、多层次、全方位的交流与合作。\n……"),r("br"),n._v("\n在科研研发合作方面，唐山市支持西南交通大学在唐山(曹妃甸)设立研究生分院和研究院，建立科技研发基地、人才培养基地和产业发展基地；双方共同推进电子物流平台建设、数字通信信息软件研发基地建设并在装备制造、电子与信息、生物工程和新医药、新能源与高效储能节能等领域开展产学研合作。在规划指导、设计合作方面，针对唐山市正在编制的现代物流规划，西南交通大学将在规划编制方面给予指导帮助，同时依托西南交通大学强大的科研、设计能力，双方在城市建设、景观设计以及综合交通、环保等领域开发建设上将开展深入合作，在人才智力方面也将以多种形式展开合作。"),r("br"),n._v("\n共建“老交大”纪念园"),r("br"),n._v("\n唐山市与西南交大将以合作的方式推进唐山铁道学院纪念园的建设。唐山交通大学本部原址位于唐山建设南路南侧，唐山南湖生态城自然风景区东部，原占地600余亩。市校决定依托唐山市南湖生态城文化创意(软件开发)等功能定位，拓展唐山铁道学院纪念园内容，在唐山铁道学院旧址上，以合作方式(或者以其他方式)建设集纪念园与科研中心、文化创意产业、电子信息产业等于一体的唐山铁道学院纪念园或者西南交通大学唐山校区(科技园)。(记者张汇\n)"),r("br"),n._v("\n知道了这样的新闻，我想，唐院的老人和老校友该是何等的欣慰！历史并未断裂，今后再去唐山，将不仅是怀旧，还要赏新焉。即使未在唐山呆过的年轻校友和学子，闻知此讯，亦必动心也。盖今日之唐山，已非昔日之唐山；今日之西南交大，也非昔日之唐院，各自俱已在科学发展观的指引下，与时俱进，面目全新矣。历史并未断裂，双方协议展现了承前启后，继往开来之精神；随着协议之逐步实施，其双赢的灿烂发展前景，岂可胜言哉！")]),n._v(" "),r("blockquote",[r("hr")])])}),[],!1,null,null,null);r.default=t.exports}}]);