(window.webpackJsonp=window.webpackJsonp||[]).push([[669],{1030:function(_,v,t){"use strict";t.r(v);var p=t(14),n=Object(p.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|  |  |  |")]),_._v(" "),v("p",[_._v("|")]),_._v(" "),v("hr"),_._v(" "),v("p",[v("img",{attrs:{src:"http://img.blog.163.com/photo/l8M_GYHVMCV2E9kCWwbwFQ==/4519080751089939626.jpg",alt:""}})]),_._v(" "),v("p",[_._v("《关注唐山，关注母校》系列报道之一"),v("br"),_._v(" "),v("strong",[_._v("阔别37载西南交大回唐山建研究院分院"),v("br"),_._v("\n来源/燕赵都市报 记者/张汇")])]),_._v(" "),v("blockquote",[v("p",[_._v("离开唐山47年后，以唐山交通大学为前身的西南交通大学将把研究院和研究生院分院再次建设到唐山，西南交通大学还与唐山市政府签署了全面战略合作框架协议。")]),_._v(" "),v("p",[_._v("** 113年校史不忘唐山辉煌**")]),_._v(" "),v("p",[_._v("11月27号，西南交通大学校长陈春阳一行来到唐山市参观考察，陈春阳表示，唐山是西南交大的“故乡”，西南交通大学是我国近代最早的大学之一，在它113年的历史中曾有67年在唐山度过，中国第一]、丗界驰名！是学校历史上最为辉煌的时期。")]),_._v(" "),v("p",[_._v("西南交通大学的前身是始建这所著名的学校曾先后被定名为：山海关铁路官学堂、唐山路矿学堂、唐山路矿学堂、唐山工业专门学校、唐山交通大学、唐山铁道学院等。1916届毕业生茅以升在美国康乃尔大学的优异考试成绩，为所有唐山交大学子获取了免试入学攻读研究生的资格，从此唐山交大在国际上被誉为“东方康乃尔”。1972年被迫迁往偏僻闭塞的四川峨眉山，定名为西南交通大学，元气再度受创，总校别唐37年。\n期间：1985年——2004年在唐建有分校，与唐山大学合署办学，著名的劳远昌教授即为老师之一。67年中，唐山交大培养出了茅以升、杨杏佛、竺可桢、林同炎、刘大中、张维、姚桐彬、沈志云、王梦恕等著名学者，创造出了100个全国第一。")]),_._v(" "),v("p",[_._v("陈春阳一行考察了曹妃甸、南湖生态城等地，他说，这次来唐山，时间虽短，收获甚大，看到了唐山的飞速发展，看到了唐山的精神、唐山的速度和唐山的热情，倍感亲切，无论在战火纷飞的旧中国还是社会主义的新时代，交大都始终得到了唐山人民的大力支持，我们非常愿意与唐山展开合作，尽最大努力与唐山人民共创美好明天。如今，西南交大虽然坐落在成都，但学校师生对唐山仍怀有特殊情谊。唐山市市长陈国鹰说，西南交大与唐山人民结下了深厚的友谊，为唐山的建设和发展提供了强有力的人才和智力支持。交大虽然迁出唐山，但双方一直保持着密切的合作，友谊长存。")]),_._v(" "),v("p",[v("strong",[_._v("在唐建立西南交大研究院分院")])]),_._v(" "),v("p",[_._v("在科研研发合作方面，唐山市支持西南交通大学在唐山(曹妃甸)设立研究生分院和研究院，建立科技研发基地、人才培养基地和产业发展基地；双方共同推进电子物流平台建设、数字通信信息软件研发基地建设并在装备制造、电子与信息、生物工程和新医药、新能源与高效储能节能等领域开展产学研合作。在规划指导、设计合作方面，针对唐山市正在编制的现代物流规划，西南交通大学将在规划编制方面给予指导帮助，同时依托西南交通大学强大的科研、设计能力，双方在城市建设、景观设计以及综合交通、环保等领域开发建设上将开展深入合作，在人才智力方面也将以多种形式展开合作。")]),_._v(" "),v("p",[_._v("**共建“老交大”纪念园")]),_._v(" "),v("p",[_._v("**\n唐山市与西南交大将以合作的方式推进唐山铁道学院纪念园的建设。唐山交通大学本部原址位于唐山建设南路南侧，唐山南湖生态城自然风景区东部，原占地600余亩。市校决定依托唐山市南湖生态城文化创意(软件开发)等功能定位，拓展唐山铁道学院纪念园内容，在唐山铁道学院旧址上，以合作方式(或者以其他方式)建设集纪念园与科研中心、文化创意产业、电子信息产业等于一体的唐山铁道学院纪念园或者西南交通大学唐山校区(科技园)。")])])])}),[],!1,null,null,null);v.default=n.exports}}]);