(window.webpackJsonp=window.webpackJsonp||[]).push([[672],{1036:function(_,v,r){"use strict";r.r(v);var n=r(14),b=Object(n.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[v("em",[_._v("引用")]),_._v(" "),v("a",{attrs:{href:"http://epei1008.blog.163.com/",target:"_blank",rel:"noopener noreferrer"}},[_._v("老顽童"),v("OutboundLink")],1),_._v(" 的 "),v("a",{attrs:{href:"http://epei1008.blog.163.com/blog/static/23524860200971475723110",target:"_blank",rel:"noopener noreferrer"}},[_._v("引用\n钱学森最后提出的问题"),v("OutboundLink")],1)]),_._v(" "),v("p",[_._v("**"),v("br"),_._v("\n钱"),v("br"),_._v("\n学"),v("br"),_._v("\n森"),v("br"),_._v("\n:"),v("br"),_._v("\n没"),v("br"),_._v("\n有"),v("br"),_._v("\n一"),v("br"),_._v("\n所"),v("br"),_._v("\n大"),v("br"),_._v("\n学"),v("br"),_._v("\n能"),v("br"),_._v("\n够"),v("br"),_._v("\n按"),v("br"),_._v("\n照"),v("br"),_._v("\n培"),v("br"),_._v("\n养"),v("br"),_._v("\n科"),v("br"),_._v("\n学"),v("br"),_._v("\n技"),v("br"),_._v("\n术"),v("br"),_._v("\n发"),v("br"),_._v("\n明"),v("br"),_._v("\n人"),v("br"),_._v("\n才"),v("br"),_._v("\n的"),v("br"),_._v("\n模"),v("br"),_._v("\n式"),v("br"),_._v("\n去"),v("br"),_._v("\n办"),v("br"),_._v("\n学"),v("br"),_._v("\n,"),v("br"),_._v("\n没"),v("br"),_._v("\n有"),v("br"),_._v("\n自"),v("br"),_._v("\n己"),v("br"),_._v("\n独"),v("br"),_._v("\n特"),v("br"),_._v("\n的"),v("br"),_._v("\n创"),v("br"),_._v("\n新"),v("br"),_._v("\n的"),v("br"),_._v("\n东"),v("br"),_._v("\n西"),v("br"),_._v("\n,"),v("br"),_._v("\n老"),v("br"),_._v("\n是"),v("br"),_._v("\n冒"),v("br"),_._v("\n不"),v("br"),_._v("\n出"),v("br"),_._v("\n杰"),v("br"),_._v("\n出"),v("br"),_._v("\n人"),v("br"),_._v("\n才"),v("br"),_._v("\n."),v("br"),_._v("\n这"),v("br"),_._v("\n是"),v("br"),_._v("\n个"),v("br"),_._v("\n很"),v("br"),_._v("\n大"),v("br"),_._v("\n的"),v("br"),_._v("\n问"),v("br"),_._v("\n题"),v("br"),_._v("\n.")]),_._v(" "),v("p",[_._v("** |")]),_._v(" "),v("blockquote",[v("p",[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_qoM6c4PEa67WVW_GXu4LXg==_1184165227022304937.jpg",alt:""}}),v("br"),_._v("\n--------------------------------------"),v("br"),_._v("\n我为啥称钱学森为战士？"),v("br"),_._v("\n--------------------------------------"),v("br"),_._v("\n文／杨恒均 2009-11-1")]),_._v(" "),v("p",[_._v("10月31日，98岁高龄的钱学森先生与世长辞，本人在此对这位伟大的战士致以崇高的敬意。")]),_._v(" "),v("p",[_._v("钱学森同志生前有无数的头衔，最响亮的就是“享誉全球的中国科学家”，但我却更愿意称他为“战士”。我之所以不愿意突出钱老的“科学家”身份，是因为与比他在科学领域更有成就的九位华人诺贝尔科学奖获得者一样，他的科学家身份并不是“中国”培养的，他是美国大学和国防部培养出来的导弹专家，虽然他从内心到外表都是不折不扣的中国人。")]),_._v(" "),v("p",[_._v("我更愿意尊称钱老为战士，却并不是因为他30多岁时就是穿校官服的美军上校，也不是因为他回到中国后，又被授予共产党军队的中将军衔。我尊钱老为一名共和国的“战士”，是因为他的一生都像一名战士一样赤胆忠心，热爱国家，热爱政府，相信党，像战士一样与落后的科学技术战斗，并具有一名战士最优秀的品质——以服从命令为天职，从不提问，更不提个人要求……")]),_._v(" "),v("p",[_._v("钱老从条件那么好的美国回到满目疮痍、百废待兴的中国大陆，不计个人得失，任劳任怨。据目前已经公开的资料显示，他老人家没有给国家添任何麻烦，而自从他1955年回来后，却为共和国军事科技的发展解决了无数的问题，扫清了航天事业上的无数障碍。"),v("br"),_._v("\n钱老一生都在解决问题，他只向共和国提了一个问题……")]),_._v(" "),v("p",[_._v("查看钱老生前记录，我们看到，他老人家只是在生命的最后阶段，对前来探望自己的共和国总理温家宝提到过一个问题，他说，“现在中国没有完全发展起来，一个重要原因是没有一所大学能够按照培养科学技术发明人才的模式去办学，没有自己独特的创新的东西，老是冒不出杰出人才。这是很大的问题。”")]),_._v(" "),v("p",[_._v("是的，这一定是很大、很大的问题，否则共和国最杰出的科学家，中国航天事业的奠基人，两弹一星的元勋绝对不会在与共和国风雨同舟半个世纪后，在自己生命走到最后的关头，才提出这唯一一个问题。")]),_._v(" "),v("p",[_._v("这是一个很大的问题，大得连前来探望他的共和国总理温家宝同志的脸色都渐渐凝重起来。实事求是的温家宝没有不懂装懂，他显然也在思考这个问题，但却没有现成的答案与钱老分享。")]),_._v(" "),v("p",[_._v("于是，总理召集了6位大学校长和教育专家，他以共和国总理之尊，提出了钱老向他提出的问题，那几位校长和专家争先恐后地答道：老师不行……大学很大，但不够强……不是大学出问题，是基础教育出毛病了……")]),_._v(" "),v("p",[_._v("家宝总理得到了答案？他满意了吗？如果他满意了的话，他一定会直奔已经走到人生尽头的钱老的睡椅前，告诉钱老答案并共商大计。然而，和我们一样，温总理显然对这些大学校长和专家的答案并不满意。"),v("br"),_._v("\n于是，大家看到了，过去几年忙得东奔西走的温家宝总理突然沉寂了下来，我想，钱老的问题一定让他感到了沉重，是啊，到处跑有什么用？")]),_._v(" "),v("p",[_._v("于是，在刚刚过去的9月4日，温家宝总理前往北京35中初二（5）班，端端正正坐在那里听老师上课，做笔记，一座就是一上午，然后他小心翼翼地对老师的讲课做了点评。然后他离开了，后来发生了一件事，我们稍后再讲……")]),_._v(" "),v("p",[_._v("那几个小时听课的温家宝总理找到答案没有？为什么中国的教育制度培养不出人才，而要不停地从美国这些国家引进？就在昨天，10月29日，在山东考察的温总理在短短一个多月里，竟然第二次来到一所中学，这次，他来到费县一中高二（8）班，亲自给同学们讲授《张衡传》，张衡，和钱学森一样，是中国伟大的科学家……")]),_._v(" "),v("p",[_._v("两次和中国教育零距离的接触，温总理是不是在为钱老的提问寻找答案？他找到了吗？也许没有找到，也许找到了，但对于一生都在为共和国科技发展的各种问题寻找答案的伟大战士钱学森来说，显然已经晚了，就在两天后的昨天，钱老悄然离开了人间……"),v("br"),_._v("\n其实，钱老的问题只有他自己能够回答……")]),_._v(" "),v("p",[_._v("谁能解答钱老这一生提出的唯一一个问题？我想，即便我再自不量力，也不会自以为可以三言两语说清楚连钱学森和温家宝两人都无法回答的问题。然而，这却不能阻止我对这个问题有我自己的思考。")]),_._v(" "),v("p",[_._v("一经思考，我就发现，把钱老的问话当成问题，其实有点问题。因为，无论从钱老短短的几句话里，还是从钱老长长的人生中，我们看到的不只是他提出的问题，其实也看到了这问题的答案……也许，这才是温总理脸色凝重的原因，也许这才是温总理对大学校长和教育专家的答案不满意的道理，也许这才是温家宝总理放下堂堂共和国总理的公务不做，而去教室听初中老师讲课、走上讲台给高中学生上课的理由……")]),_._v(" "),v("p",[_._v("钱老说中国的大学出不了杰出人才，是他归国55年后说的，而早在55年前，美国的大学就出现了成千上万杰出的人才，包括钱学森本人。我再次使用“战士”这个称号，是想借今天这个机会，不但向钱老，而且向所有的“钱学森”们致以崇高的敬意，这些人有学成归来的，有的学成后，人虽然没有归来，但心却从来没有离开……；有的名气虽然不如钱老大但贡献一点也不小，有的却因为心照不宣的原因根本就没有名字，更别说名气……")]),_._v(" "),v("p",[_._v("他们都有一个共同的特点，那就是都在美国等先进国家学习和研究高科技，人回国或者心回国报效祖国、报效人民。他们带回来的是科学技术，他们和共和国的领导人一样，认为只要带回先进的科学技术，中国就会先进了。")]),_._v(" "),v("p",[_._v("然而，半个多世纪过去了，我们的大学依然培养不出杰出人才，我们的科技依然是世界上比较落后的，我们的人民仍然是世界上最穷困的一群——为啥呢？因为原因很简单，发展科学技术，推动国家前进，和培养科学技术大师和杰出人才一样，并不是单单是靠“科学技术”就可以的，还需要宽松和自由的人文思想，需要与时俱进的大学和教育制度，需要适应新的时代的政治体制和社会环境……"),v("br"),_._v("\n是啊，只有当钱老的问题牵涉到如此巨大的“答案”的时候，才会难倒当今中国最伟大的两位人物——钱老和温总啊。否则，他们难倒会回答不出？"),v("br"),_._v("\n科学可以让卫星上天，“科学”也可以让人活活饿死……")]),_._v(" "),v("p",[_._v("有网友指责说钱老一生埋头科学，不关心政治和社会，而唯一一次关心“政治”，竟然是在1958年6月16日的《中国青年报》上的那篇文章《粮食亩产量会有多少？》，在这篇文章里，钱先生以“科学”的方法，论证出一亩地可以亩产160万斤蔬菜……")]),_._v(" "),v("p",[_._v("这位从美国回来的科学家的“科学论证”，无疑给当时造成N千万人死亡的浮夸火上浇油。而到底会有多少饿死的中国老百姓应该归罪于这类昧良心弄出的“科学论证”？也许，世间的任何科学都无法计算出……")]),_._v(" "),v("p",[_._v("科学无法计算出的，政治却应该有所交代！可惜迄今为止，我还没有看到我尊重的战士钱学森对那篇文章有任何解释，难道他当时只是像战士一样忠实地执行“政治任务”？难道他还有更加苦痛的难言之隐？")]),_._v(" "),v("p",[_._v("也许，读者看到这里会对我对一位刚刚过世的老人的质疑与求全责备而生出反感，可你是否知道，我走到这里，正是因为我在探寻钱老生前提出的那个问题的答案，而就在此时此刻，我感到自己已经接近答案了……")]),_._v(" "),v("p",[_._v("是的，我找到了答案！钱学森老人家像一名勇敢的战士一样从美国带回了先进的科学，却没有带回科学的精神，我们的国家重视所有带回先进的生产力和科学技术的人才，却把那些试图带回来的比科学技术同样有用的人文精神和先进的价值理念拦阻在大门之外——而这一切，还是让我们从钱老人生的故事中体会……")]),_._v(" "),v("p",[_._v("钱老是美国培养的空军上校，国防部科学小组成员，他参入设计了美军最重要的导弹，无疑掌握着美国的核心军事机密，无怪乎他在1949年共和国成立后要求回国被美国FBI拦下来，不准离开美国、进行监视长达五年之久，最后中国政府通过高层接触，用11名美军战俘换回了钱学森——这也是中美交往的历史上，第一次中国人的人命大于美国人的。")]),_._v(" "),v("p",[_._v("美国人后来一定后悔了，因为钱学森回国后，正是用自己在美国学习和研究的高科技发展了中国的导弹。可是，即便是这种情况下，1985年，美国总统的科学顾问基沃思访问中国时公开表示，当年对钱学森的刁难是没有道理的，是不对的，因为那时美国盛行反共的麦卡锡主义。他主动邀请钱学森访问美国。")]),_._v(" "),v("p",[_._v("也许美国人是出于政治考量，但是，他们毕竟对当时对钱老的迫害做了道歉和说明。我想说，这和科学无关，这和科学技术无关，甚至和泄密，偷窃先进技术无关，这和人文精神，和人权，和一个人的自由有关，这就是精神，这就是很多从国外归来的学者应该一起带回来的精神……"),v("br"),_._v("\n其实，我们每一个人都知道答案……")]),_._v(" "),v("p",[_._v("我们从钱老的经历中，隐隐约约看到了问题的答案。其实从正在为钱老寻找答案的温总理身上，我们也看到了类似的答案——请大家回顾一下，温总理9月4日离开北京三十五中后发生了什么？")]),_._v(" "),v("p",[_._v("发生了被很多人忽视，被有些人说成是“作秀”的一桩小事，温总理亲笔写信，为自己在点评老师讲课时犯的一个小小的错误真诚的道歉！——温总理找到了答案，他并没有说出来，他做了出来。")]),_._v(" "),v("p",[_._v("如果总理能够为一桩这么微不足道的错误道歉的话，那么钱学森同志为啥不能为那篇也许害死了好多饥民的文章道歉？而伟大的共和国，又为什么不能为历史上那些人为的灾难真诚地说一声：对不起？")]),_._v(" "),v("p",[_._v("也许有人说，这种无法起死回生的认错，这种不关痛痒的认错，能和大学是否可以培养出杰出的人才扯上关系吗？难道道歉了，我们的火箭就能够上天了？难道道歉了，我们的科学就发达了？难道……")]),_._v(" "),v("p",[_._v("不错，我要确切地告诉你，不但扯得上关系，而且息息相关，国家政权和统治者的反思和道歉也许只是我说的那种人类比任何科学技术都要先进和强大的精神力量的一种，但却是至关重要的一种！")]),_._v(" "),v("p",[_._v("你能够想象，在一个知错不改、在一个没有包容精神、在一个死不认账、在一个扼杀自由精神的地方，在一个只注重科学却不知道科学精神为何物的地方，在一个只想要最先进的技术，却无法自由思想的地方，在一个所有的东西都被标上价钱，可人的心里却缺乏了基本的价值观念的地方……那里，能够培养出真正杰出的人才？那里，能够产生真正的和谐？")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/img460.ph.126.net_8mZN9GNbk18J96V2xjcOGw==_2567614737555670985.jpg",alt:""}})])]),_._v(" "),v("p",[_._v("---|---")])])}),[],!1,null,null,null);v.default=b.exports}}]);