(window.webpackJsonp=window.webpackJsonp||[]).push([[673],{1033:function(_,r,v){"use strict";v.r(r);var n=v(14),t=Object(n.a)({},(function(){var _=this,r=_._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[r("p",[r("em",[_._v("引用")]),_._v(" "),r("a",{attrs:{href:"http://xyd20405.blog.163.com/",target:"_blank",rel:"noopener noreferrer"}},[_._v("旻雯"),r("OutboundLink")],1),_._v(" 的\n"),r("a",{attrs:{href:"http://xyd20405.blog.163.com/blog/static/19501572009102111629187",target:"_blank",rel:"noopener noreferrer"}},[_._v("李开复：十句箴言赠武汉学子（转载）"),r("OutboundLink")],1),r("br"),_._v(" "),r("strong",[_._v("以下"),r("a",{attrs:{href:"http://zifeng88.blogcn.com/diary,29487626.shtml",target:"_blank",rel:"noopener noreferrer"}},[_._v("原文"),r("OutboundLink")],1),_._v("转载自"),r("a",{attrs:{href:"http://zifeng88.blogcn.com/",target:"_blank",rel:"noopener noreferrer"}},[_._v("★读书时间★"),r("OutboundLink")],1)])]),_._v(" "),r("p",[_._v("|  |  |  |  |")]),_._v(" "),r("blockquote",[r("p",[_._v("**李开复：十句箴言赠武汉学子"),r("br"),_._v("\n** 来源：楚天都市报")]),_._v(" "),r("p",[r("img",{attrs:{src:"http://news.xinhuanet.com/book/2009-10/23/xinsrc_402100623103407877192.jpg",alt:""}})]),_._v(" "),r("p",[_._v("李开复在成都（图片来源：四川在线）")]),_._v(" "),r("p",[r("strong",[_._v("嘉宾简介")])]),_._v(" "),r("p",[_._v("李开复，信息产业的执行官，计算机科学的研究者。李开复1961年生于中国台湾，祖籍四川。11岁时赴美，1983年毕业于美国哥伦比亚大学，后就读于卡耐基梅隆大学，获计算机学博士学位。1998年，李开复加盟微软公司，随后创立了微软亚洲研究院；2005年7月加盟Google，担任中国区总裁一职；今年9月4日，宣布离职并创办“创新工场”，任董事长兼首席执行官。")]),_._v(" "),r("p",[_._v("李开复曾梦想在中国创建一所有世界影响力的私立大学，但是最终因为种种原因没有成功。他开始用另外的方式实践梦想：给中国的大学生写信，写书，创办“我学网”，每年面对十万学生做演讲。他曾说，他曾经想过自己的墓志铭应该有科学家或者企业家的注脚，但他现在已经没有这种想法，如果他的墓志铭上有热心教育者的字样，他会感觉到内心的温暖。"),r("br"),_._v("\n他从广州飞抵重庆，昨日，他从重庆飞临江城，明天他又将飞向下一个城市。"),r("br"),_._v("\n昨日，从下午1点到晚上9点，从华中科大到武大，再到华中农大，李开复如旋风般连跑3所高校，所到之处，“李旋风”追随者如潮。")]),_._v(" "),r("p",[_._v("李开复到华中科大计算机学院演讲的消息不胫而走。中午12时许，慕名而来的学生们将该校8号楼大门堵了个水泄不通，讲座场地不得不临时换到1号楼报告厅；下午，武汉大学也将场地临时更换到露天剧场，以让更多人一睹其风采；傍晚，六千名大学生，将华中农大体育馆塞得满满当当……")]),_._v(" "),r("p",[_._v("晚七点，李开复入场，全场爆发出热烈的掌声。在时长一个半小时的讲座中，六千大学生聚精会神地聆听着开复老师讲述的成长启发，一直到讲座结束，仍有不能进场的大学生在体育馆外徘徊。")]),_._v(" "),r("p",[_._v("和许多人的成长经历相似，李开复的成长历程中，也有儿时的淘气顽皮，求学时期的疑问与困惑，有成功时的喜悦和骄傲，也有遭遇挫折时的彷徨与苦闷，也曾在人生的低谷饱受痛苦的煎熬。但尤为可贵的是，李开复在成功中收获了经验，在失败中得到了成长。他把富有传奇的人生经历，在如同与你交谈般的娓娓道来中浓缩成为十句具有启发性的箴言。"),r("br"),_._v(" "),r("strong",[_._v("启发一：自信中不要失去谦虚，谦虚中不要失去自信")])]),_._v(" "),r("p",[_._v("台湾规定6岁上小学。李开复5岁的时候，就跟父母说想读小学。母亲看他很自信，让他去考，结果李开复果然考上了。邻居阿姨听说了，就开玩笑：“你读得来吗？”“我还没看到过99分呢。”李开复言下之意，自己每次都考100分。"),r("br"),_._v("\n一周后，李开复拿着90分的考卷回家，被妈妈打了一顿，“打你不是因为成绩不好，而是你忘记了中国人谦虚的传统美德。”"),r("br"),_._v(" "),r("strong",[_._v("启发二：天赋就是兴趣，兴趣就是天赋")])]),_._v(" "),r("p",[_._v("1979年，李开复进入哥伦比亚大学政治系学习，与现任美国总统奥巴马是同班同学。但进校后，发现政治系的课非常枯燥。大二开始，李开复转到了计算机专业。“天赋就是兴趣，兴趣就是天赋。”李开复认识到了这一点。"),r("br"),_._v(" "),r("strong",[_._v("启发三：思考比传道更重要，观点比解惑更重要")])]),_._v(" "),r("p",[_._v("计算机系系主任尼克跟李开复传递了一个观念——思考比传道更重要，观点比解惑更重要。“当你忘记你所学的一切的时候，剩下的就是教育。大学四年最重要的是学习的能力和思考的能力。”李开复建议。")]),_._v(" "),r("p",[_._v("博士毕业论文中，李开复按照导师的要求，研究了一年，发现有新的方法。要坚持和权威相悖的观点吗？李开复向导师提出了自己的设想。导师说：我不同意你，但我支持你。"),r("br"),_._v(" "),r("strong",[_._v("启发五：挫折不是惩罚，而是学习的机会")])]),_._v(" "),r("p",[_._v("毕业后李开复留校任教。但在系主任那拿薪水时，李开复却受到了莫大打击：学生评价他上课犹如唱独角戏，从不用目光与学生交流。“但挫折不是惩罚，而是学习的机会。”李开复学会了与学生目光交流，学会了演讲。"),r("br"),_._v(" "),r("strong",[_._v("启发六：创新不重要，有用的创新才重要")]),r("br"),_._v("\n教书工作两年后，李开复加入了苹果，之后到了SGI(美国视算科技公司)。在SGI做了一个很酷的三维浏览器，却失败了。"),r("br"),_._v("\n这么酷的产品为什么失败？因为科学家做产品，衡量的标准是“新”，但产业首先要求的是“有用”。"),r("br"),_._v(" "),r("strong",[_._v("启发七：用勇气改变可以改变的事情，用胸怀接受不能改变的事情，用智慧分辨两者的不同")]),r("br"),_._v("\n2005年，李开复跳槽到谷歌，结果“微软很生气，后果很严重”，把李开复和谷歌告上了法庭。李开复用上了自己所有的法律和计算机知识，最终官司取得了胜利。"),r("br"),_._v(" "),r("strong",[_._v("启发八：求知若饥，虚心若愚")])]),_._v(" "),r("p",[_._v("2005年9月，李开复开始了在谷歌的工作，在那里，他学到的不仅是最精确的搜索技术、最大规模的数据中心，还有最好的创新模式和如何打造一个工程师最向往的公司。"),r("br"),_._v("\n美国做过一个调查，人的一生平均换4到5个工作。求知若饥，虚心若愚，是要永远保持的。"),r("br"),_._v("\n**启发九：追随我的心"),r("br"),_._v("\n** 每个人总有些想坚持的东西。追随你的心，用它引领你的一生，任何其他事情都是次要的。不要让任何人的意见淹没了你的心声。"),r("br"),_._v(" "),r("strong",[_._v("启发十：你的价值不是你拥有多少，而是你留下多少")]),r("br"),_._v("\n今年9月，李开复创造了“创新工场”。这个选择正确吗？人生的目的是什么？你的价值在哪里？“一个人的价值不是你拥有多少，而是你留下了多少。”李开复说。")]),_._v(" "),r("p",[r("strong",[_._v("互动问答")]),r("br"),_._v("\n问：网上有篇帖子说，高校计算机专业毕业的“正规军”打不过培训机构出来的“游击队”，您是如何看待的？")]),_._v(" "),r("p",[_._v("答：高校的某些课程设置的确显得陈旧，似乎跟实际应用脱钩。但大学是打基础的，先打好坚实的学科基础，再通过实习来培养自己的技能，就不是任何培训机构的学员可以相比的了。"),r("br"),_._v("\n问：现在流行大学生创业，可我感觉自己除了有创业激情外一无所有，我们该怎么办？")]),_._v(" "),r("p",[_._v("答：创业分为两种：一种是参与创业，一种是主导创业。我建议大学生不要一毕业就创业，而是先做一个打工者，熟悉你所要从事的行业，熟悉公司的运行机制，然而再去考虑创业。"),r("br"),_._v("\n问：您的小学是在台湾念的，中学和大学是在美国念的，能比较一下中美两国教育体制的各自优势和缺陷吗？")]),_._v(" "),r("p",[_._v("答：美国的教育优势在于，他们有最好的师资，最开放的学习环境，鼓励学生根据个人兴趣来发展，利于学生养成开拓性、批判性的思维方式；而中国的教育优势在于，注重培养学生扎实的基础，训练学生的毅力。这两种教育方式可以互补，你们现在已经得到了一半的培养，剩下的就是培养自身的另一半能力。")]),_._v(" "),r("p",[r("strong",[_._v("精彩语录")]),r("br"),_._v("\n大学里遭遇迷惘期，交女朋友，学专业课，找工作，都没有问题，这都是大学里应该或者可以经历的。 ——谈大学"),r("br"),_._v("\n我不鼓励任何人辍学创业，如果你一定想模仿比尔·盖茨，那至少等你的公司开起来后再这样做吧。 ——谈创业"),r("br"),_._v("\n工科的学生一定要注重实习，拥有实习经验的低分毕业生，好过没有任何实习经验的高分毕业生。 ——谈人才")])]),_._v(" "),r("hr")])}),[],!1,null,null,null);r.default=t.exports}}]);