(window.webpackJsonp=window.webpackJsonp||[]).push([[674],{1035:function(_,v,r){"use strict";r.r(v);var n=r(14),t=Object(n.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|  |  |  |")]),_._v(" "),v("p",[_._v("|")]),_._v(" "),v("hr"),_._v(" "),v("blockquote",[v("p",[v("img",{attrs:{src:"http://img.blog.163.com/photo/l8M_GYHVMCV2E9kCWwbwFQ==/4519080751089939626.jpg",alt:""}})]),_._v(" "),v("p",[_._v("《狼来了》"),v("br"),_._v("\n**——引自"),v("a",{attrs:{href:"http://home.51.com/azj0818",target:"_blank",rel:"noopener noreferrer"}},[_._v("http://home.51.com/azj0818"),v("OutboundLink")],1)]),_._v(" "),v("p",[_._v("文／好人好梦**")]),_._v(" "),v("p",[_._v("“老师,教德语口语的老师蔑视我们学生。”")]),_._v(" "),v("p",[_._v("两个中德班经济学的女生坐在我办公桌前向我哭诉，样子好象是压了很久才来的。我清楚这个班，几位经常逃课且德语成绩又很差的同学早以引起授课老师强烈的愤慨。尽管班住任护着这些家境富裕的孩子，委婉的解释汇报中也多少有所察觉。"),v("br"),_._v("\n能说什么呢，有人说：苦难产生蔑视，蔑视产生强硬，强硬产生高效，高效产生轻松。"),v("br"),_._v("\n德国安海尔科技应用大学福来梅克教授和多拉教授,与22日下午到成都。")]),_._v(" "),v("p",[_._v("院长几次叮嘱我做好在校期间各方接待和衔接工作。因为这次来访的福来梅克教授是贵校经济系的主任，多拉教授是副主任。关键的问题是我院在法学专业想与对方开展合作，给学生搭建一个对外互访的平台。因此，在考察中方的授课情况的同时，还有两份协议需要签定。经济学由以前协议中规定的10人出国名额现需增加到15人；法学几次磋商给了5个名额，理由是德国的法律太难学。他们将用一周的教学调查来握手签定两个合作协议。"),v("br"),_._v("\n这对于人文学院的今后发展意义重大。政治学院已宣布从人文剥离，系室成员已在网上公布，只是双方的领导班子还在酝酿中。 这是个谜。"),v("br"),_._v("\n快了！都已疲惫，都在忙碌中掩饰无奈！都在揣测各种可能。"),v("br"),_._v("\n接机的任务是由年长温和的杜老师和我院参加中德经济双学位的一名帅气的学生钱坤负责。")]),_._v(" "),v("p",[_._v("高大的两位德国教授，从法兰克富飞柏林，又从柏林飞北京，再由北京飞成都，对于快60岁的福来梅克教授和45的多拉教授还是很辛苦的，但为了学院的发展他们必须。"),v("br"),_._v("\n我将外事秘书发来的电子文本协议书，按德方的文本样式：经济和法律、中文和德文制作规范。我知道德国的法律在全球都是有名的，万万不可有所疏忽。")]),_._v(" "),v("p",[_._v("接风的宴席上，两位主动表示喜欢辛辣的食物，川菜对他们很和口味。当然，在整天面对兔子才吃的食物，川菜无疑是美味珍肴。见他们吃地酣畅淋漓，赞声不迭时。我把接待日程安排表拿出，建议在两天的工作日之外去游览九寨沟。一是九寨沟天堂般的仙境能与日耳曼民族分享是四川人的骄傲。二是离开成都以外的游览我们可以不支付、不陪同。这样可以节约学院的接待费。所以我同院长力推九寨沟。可刻板的二位只认手里在德国买的旅游手册，让我惊讶的上面居然没有九寨沟的介绍。"),v("br"),_._v("\n怕被忽悠，能理解！")]),_._v(" "),v("p",[_._v("27日上午9点签合同，各方都确定协调好了。25日下午快下班时院长给我打电话，让我将合同给法律系一份，给经济系一份。紧接着法律系一老师要求修改合同，从我这要去了电子文本，花了半小时认真斟酌后按他的意思修改了一份新的合同并让我找人译成德文......为此,嚷到校党委副书记那里去。因为，他又暂兼两院的院长。"),v("br"),_._v("\n我知道这不是冲着我来的。"),v("br"),_._v("\n明白书记直接打电话让我给他送一套过去，事情绝没那么简单。 当人们兴趣盎然围着锅说事时，其实意并不在锅，而是锅以外的菜肴。"),v("br"),_._v("\n合约没有向糟糕而又复杂的方向重做。")]),_._v(" "),v("p",[_._v("签约议事结束后，我冒雨回到办公室，再一次给参加此项目教学的老师打电话，提醒下午的与德国教授座谈的时间和地点。得到的回答，总结起来有四种：一是我有重本的教学任务（理解）。二是我有学术会议但尽量抽身来。三是我家孩子六.一演出，我得守望。四是对不起！我感冒了怕传染给大家就不来了。每种理由我都无言以对，再说下去显得我很无赖......好在法律那边还有几位老师撑着，经济这边我只好找刚分来不久的年轻博士生充数。"),v("br"),_._v("\n拥有双博士学位的富莱梅克教授在交流中，流露出明显的困惑和难言的无奈。"),v("br"),_._v("\n28日整天是留给中德班学生。逐一谈话看似轻松，出来的学生满脸通红、紧张的脸上挂满汗珠。"),v("br"),_._v("\n“问得问题真有点八卦，你回答那么好干吗，把我们差的全抖落出来了.”"),v("br"),_._v("\n我去买纪念品，准备明天的送别晚宴中互换的礼物。"),v("br"),_._v("\n送别晚宴是订在古色古香的顺兴茶馆。成都精致的小吃套餐他们很是满意，超负荷的收拾了剩余菜肴。"),v("br"),_._v("\n看得出两位德国教授不忍浪费......"),v("br"),_._v("\n我用征询的目光示意他们用好餐。让德语翻译老师告诉他们，饭后喝咖啡时还有一些细节需要磋商。希望在磋商中能将这次德语面试不过关的５位孩子送出国门。")]),_._v(" "),v("p",[_._v("回家的路上，我满耳都是翻译老师重重地话语和他们夸张而又刻板的表情：你们送来的学生越来越差；你们的学生考试找人代考、作弊。你们的德语语言课开了多少个学时？基本的语言不能交流去德国干什么？在中国的两年学习你们有清到制度吗？已经去的学生中有三分之一的拿不到毕业证，我得退回来你们处理;德语是哲学家的语言．去学德国学法律，您的学生有耐心吗？"),v("br"),_._v("\n一位国外的传媒大亨说过：没有蔑视，就没有伟大！"),v("br"),_._v("\n酒足饭饱后他们比我困惑忧虑，这大概是咖啡的作用。望着他们仿佛看到的是被白蚁蛀空的家．")]),_._v(" "),v("p",[_._v("习惯无语也习惯这种文化落差。找回一丝感悟：我们在经济上欺瞒，我们有了记忆深处，刻骨铭心的三年自然灾害；我们政治上的欺瞒，我们有了苦风凄雨的十年浩劫；我们文化上欺瞒，我们是搬起石头砸自己的脚；一次次教训在加重，却没有悔改。"),v("br"),_._v("\n我们从故事里教会孩子：狼来了！")])])])}),[],!1,null,null,null);v.default=t.exports}}]);