(window.webpackJsonp=window.webpackJsonp||[]).push([[679],{1040:function(_,v,t){"use strict";t.r(v);var p=t(14),n=Object(p.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|")]),_._v(" "),v("blockquote",[v("p",[v("strong",[_._v("我国市政卫生专家朱泰信教授")])]),_._v(" "),v("p",[v("strong",[_._v("文/刘 涛")])]),_._v(" "),v("p",[_._v("编者按：日前刘涛先生在留言中介绍了我校校友朱泰信教授的一些资料，我们十分感谢。")]),_._v(" "),v("p",[_._v("朱泰信，1924年毕业于我校土木工程系，曾留学英、法两国学习市政工程。1931年至1942年在我校任副教授、教授。讲授都市计划、道路工程、市政管理、污水处理和排放、给水工程微生物学等课程.\n他是在中国最先开出城市规划课的教师。"),v("br"),_._v("\n朱泰信与许元启、罗河、李汶等四人，除教学有出色成绩外，共同对我校建设、管理诸方面有重要贡献。在学校中，相对于“五老”，有“四少”之称。")]),_._v(" "),v("p",[_._v("朱泰信热爱母校，曾将“以校为家”作为一种美德加以提倡。他建议草拟“久任教授优待条例”，请教育部批准实施。")]),_._v(" "),v("p",[_._v("朱泰信喜欢文史，能文。1919年1月任我校1922级所创办《壬戌杂志》总编辑。五四运动时，为我校学生救国团所发行的《救国报》总编辑。在我校历史上出版过的刊物上常有他的文章发表。其中1947年一月《唐院月刊》（五）上发表的《一个革命救国的教育家——为孙揆百先生逝世九周年纪念作》一文，记述孙鸿哲三度出任唐院院长爱国爱校的光辉事迹，十分感人。既有历史价值，又有教育意义，是我校历史资料中不可多得的文章。")]),_._v(" "),v("p",[_._v("抗日战争时期，朱泰信为湘潭复校、杨家滩开课以及平越交大贵州分校建设，都是千方百计、克服困难、竭尽心力，作出了巨大贡献。")]),_._v(" "),v("p",[v("strong",[_._v("朱泰信")]),_._v(" "),v("strong",[_._v("教授")]),_._v(" "),v("strong",[_._v("的资料：")]),_._v(" ****")]),_._v(" "),v("p",[v("strong",[_._v("刘 涛")])]),_._v(" "),v("p",[_._v("****"),v("strong",[v("a",{attrs:{href:"pic/img623.ph.126.net_AF8zR4aqfXnizAG00cM1fg==_1940769964421279660.jpg"}},[v("img",{attrs:{src:"/pic/img623.ph.126.net_AF8zR4aqfXnizAG00cM1fg==_1940769964421279660.jpg",alt:""}})])])]),_._v(" "),v("p",[_._v("朱泰信，英文名Peacecall T.S.Chu，籍贯：安徽全椒，号喈平 ，1924年通信处：津浦南段乌衣转全椒东门。")]),_._v(" "),v("p",[_._v("民国5年(1916年)夏毕业于全椒县立中学校，考入交通部部立唐山工业专门学校预科，同年因患病，休学2年。民国7年（1918年）入交通部部立唐山工业专门学校读“中三”（即预科一年级，民国七年章伯初校长将预科一二年级改称中学三四年级）。民国8年（1919年）读预科二年级（这一年中学停办，“中四”改回预科二年级）。民国9年（1920年）入交通部部立唐山工业专门学校本科。民国13年（1924年）毕业获学士学位。朱泰信读“中三”时，正值五四运动开始，“朱泰信君那时主救国报笔政”（引自1924届毕业纪念册）。朱泰信与马汝邴二人均曾代表交通部唐山大学学生出席唐山各界联合会，响应爱国运动。在唐求学期间，朱泰信“译有科学数学名著多种出版”（引自1924届毕业纪念册）。")]),_._v(" "),v("p",[_._v("民国14年（1925年），考取安徽省官费留学生，就读于英国伦敦大学市政卫生系，专攻城市规划和市政工程，后又转该校医科攻读微生物学。民国16年（1927年）夏赴法，入巴黎大学医科公共卫生学院续读微生物学和公共卫生。后经杜嘉利克教授介绍入巴斯德学院实验室研究水的微生物学。")]),_._v(" "),v("p",[_._v("民国19年（1920）8月回国，任江苏省建设厅工程师。次年（1931）至民国31年（1942）夏被交通大学唐山工学院聘请，先后任卫生工程系副教授、教授，土木系代理主任；讲授城市规划、市政管理、道路工程、给水工程、污水处理及排放、微生物学等课程。他是在中国最先开出城市规划课的教师。")]),_._v(" "),v("p",[_._v("民国31年8月至民国32年8月，任国民党中央工作竞赛委员会专家委员兼主任秘书。在此期间，与杨铭鼎、过祖源等发起成立“中国卫生工程学会”，与茅以升、竺可桢、李四光等同时被聘任为中央训练团高级班专家讲师。民国33年8月至民国37年6月，先后作为湖北、湖南省政府高级技术顾问，主持武汉及大长沙区域规划工作。民国37年（1948年）7月至民国38年（1949年）6月，复被聘任为交通大学唐山工学院教授，借教于长沙克强学院，教授市政规划和卫生工程。同年任湖南大学水利系教授。1950年元月，被华东工业部调任淮南工业专门学校卫生工程专业讲座(高级教授)，兼淮南煤矿技术顾问。1953年8月至1954年9月任中央燃料工业部煤矿总局特级工程师。1954年10月至1964年6月，任中南土木工程建筑学院营造系、道路工程系教授，1961年后兼任该校给水排水实验室主任(该校1958年改名湖南工学院，1959年改名湖南大学)。")]),_._v(" "),v("p",[_._v("朱泰信著述颇丰，留学期间，作为《中学生杂志》的专栏作者，向国内介绍了大量欧洲科技见闻。在巴斯德学院实验研究期间，与当时世界名微生物专家、他的导师杜嘉利克教授合作的“对辩别真假大肠菌群的微生物手术研究报告”，发表在《法国卫生学报》上，并在法国国家医学会年会上宣读，被邀参加1930年在巴黎召开的国际微生物学会年会。民国19年至民国20年，草拟了《江苏省水检设施计划》、《镇江“入口门”填河成街计划》、《镇江城市规划》、《镇江下水道规划》、《江苏省工程建设区分区计划》，并著述《水与卫生》、《城市沟渠工程概要》出版。在唐山任教期间，组建了我国第一个卫生工程实验室，他是在国内讲授城市规划的第一个中国教授。这一时期，发表了《教育工程与国防建设》等论文，在全国工程师年会上宣读。在鄂、湘时期，主要的论文有《城市建设之新观点》、《我国城市复兴之途径》等。建国后在淮南期间，著有《卫生工程》；在湖南大学最后九年里，发表了“沉淀理论”和“污水处理”论文七篇，另完成了一部专著《水卫生生物学》；此外，培养了我国第一个水卫生生物学研究生，为我国填补了一项空白。"),v("br"),_._v("\n1961年6月9日，朱皆平因脑溢血症病逝于长沙湖南医学院第二附属医院。")])]),_._v(" "),v("hr")])}),[],!1,null,null,null);v.default=n.exports}}]);