(window.webpackJsonp=window.webpackJsonp||[]).push([[685],{1049:function(n,t,v){"use strict";v.r(t);var _=v(14),r=Object(_.a)({},(function(){var n=this,t=n._self._c;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("p",[n._v("|")]),n._v(" "),t("blockquote",[t("p",[t("strong",[n._v("我校信息学科发展回顾")])]),n._v(" "),t("p",[n._v("**")]),n._v(" "),t("p",[t("strong",[n._v("文/靳蕃")])]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("2009年是百年交大建校113周年，又欣逢新中国60华诞。忆过去，看现在，想未来，不禁胸潮澎湃，感慨万千，为祖国腾飞的势头和发展的高速拍手叫好，为作为一个中国人而倍感骄傲自豪。")]),n._v(" "),t("p",[n._v("就拿信息学科在西南交大的发展历程来说，早在20世纪60年代即创办了计算技术、自动化和无线电三个专业，后因高校专业结构调整而中断招生。到改革开放前，我校只有属于强电的电气工程专业，属于弱电的电子信息科学领域还是一片空白。1978年，解放思想改革开放的春风吹遍神州，已故曹建猷院士高瞻远瞩，结合世界科技进步的形势和我校学科发展的需要，在当时的电机系及时开办了计算机应用专业。随后在1980年派遣两位中年骨干教师分赴美国、德国学习人工智能和数字信息传输与处理科学，为西南交大开办信息学科专业做准备。1985年，单独成立了计算机科学与工程系，1986年开办信息工程专业，1993年开办通信工程专业，到2005年，一个羽翼逐渐丰满的信息科学与技术学院终于在西南交大正式诞生。")]),n._v(" "),t("p",[n._v("现在，信息学院已拥有涵盖6个一级学科的8个本科专业、11个硕士学位专业，5个博士学位专业授权点和两个博士后流动站，还有6个工程硕士学位和3个在职教师硕士授权点。由于学校的大力支持和全院师生的努力拼搏，西南交大在信息科学领域中从无到有、从小到大地茁壮成长，不但在培养人才方面桃李芬芳，硕果累累，在移动通信、计算机网络、信息处理和密码学等专业领域中崭露头角，而且在信道编码、序列设计和人工神经网络等信息科学研究前沿取得国内外同行瞩目的成就。国际上有重大影响的IEEE（即国际电气工程师与电子工程师学会）于2007年在成都建立了IEEE成都分会，由范平志教授担任主席，就在最近，又在成都设立了IEEE-\nVT（车辆技术）和IEEE-IT（信息理论）分学会，并分别由我校方旭明教授和唐小虎教授任主席。")]),n._v(" "),t("p",[n._v("如果有人问我，你最得意的和感到骄傲的事情是什么？我回答的是：当我1980年西去欧洲进修时，是德国教授资助我来启蒙学习信息科学ABC，而十年之后的90年代初，两位来自法国GRENOBLE\n理工大学的留学生，却东来西南交大神经网络与信息技术研究所，并在我的国家863和国家攀登计划课题基金资助下，学习中国的人工神经网络新技术。先当学生，后当老师；先受资助，后资助人，正是从一个侧面反映了中华民族在近两百年落后世界，并在近六十年急起直追开始取得成功的生动写照。"),t("br"),n._v("\n信息、能源和材料是当前世界新经济的三大支柱，其中信息科学又是带动各行各业向前发展的火车头\n。有着百年辉煌历史的西南交大，正在向着“建设交通特色的多学科协调发展的高水平研究型大学”战略目标迈进。当前，铁路交通正在朝高速化、自动化、网络化和智能化方向发展，我校有传统特色的土、机、电、运等专业，都有引入现代信息科学技术进行“刷新”以保持领先优势的问题，信息学院本身也必须围绕交通特色才能根深叶茂，做大做强。因此，西南交大信息科学的发展任重道远，要依靠全校各院系各专业学科交叉，群策群力，才能在国内外的强烈竞赛竞争中取得胜利。"),t("br"),n._v("\n最后，特别要提到的是，继计算机、互联网和移动通信之后，一个来势迅猛的物联网（INTERNET OF\nTHINGS）信息科技新浪潮正在世界范围内掀起，它是信息科学技术发展的新热点新动向，将对包括交通运输、物流、管理和网络通信等多种专业的发展起巨大的推动作用，值得我校领导和各院系师生密切关注，并把握时机组建相应的学科群来抢先占领这个信息科技发展的制高点。")])]),n._v(" "),t("hr")])}),[],!1,null,null,null);t.default=r.exports}}]);