(window.webpackJsonp=window.webpackJsonp||[]).push([[702],{1066:function(_,v,p){"use strict";p.r(v);var t=p(14),o=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|")]),_._v(" "),v("blockquote",[v("p",[_._v("**记新中国第一位女火车司机田桂英"),v("br"),_._v("\n**")]),_._v(" "),v("p",[v("strong",[_._v("转载凤凰网")])]),_._v(" "),v("p",[v("strong",[_._v("记者 杨滨 特约记者呼兴宝")])]),_._v(" "),v("p",[_._v("编者按：新中国第一位火车女司机、全国劳动模范田桂英，1955年至1960年在唐山铁道学院机械系学习，今年已经80岁了。在全国庆祝建国60周年之际，母校向她表示热烈祝贺，祝愿她健康长寿。")]),_._v(" "),v("p",[_._v("新中国历史上一个响亮的名字、一段不凡的业绩，不仅照亮了她奋斗过的那个时代，也让那个时代的精神延续到了今天。")]),_._v(" "),v("p",[_._v("7月13日，记者见到了久违的新中国第一位女火车司机———田桂英。")]),_._v(" "),v("p",[_._v("已是80岁高龄的田桂英老人，虽然身体状况大不如前，但她回忆起那段激情燃烧的岁月，仍然激动万分。")]),_._v(" "),v("p",[_._v("田桂英出生在旅大市（现为大连市）一个贫困的渔民家庭。小时候，她每天和父亲在海上撒网捕鱼。虽然生活贫苦，但惊涛骇浪练就了她百折不挠的性格。")]),_._v(" "),v("p",[_._v("12岁那年，在大姐的提议下，田桂英进入小学学习。3年后，田桂英辍学，到一家印刷厂学徒，贴补家用。在印刷厂做了两年，经一位小学同学介绍，田桂英被安排到大连机务段的一个食堂里发饭票。")]),_._v(" "),v("p",[_._v("当时，食堂每月发25公斤高粱，十几元工资，中午还提供一顿高粱米干饭，17岁的田桂英对自己的工作很满意。她说：“那时的想法很简单，就是多挣点钱，养活父母，再买点线，绣几对枕头，到年龄了嫁个好人家。”")]),_._v(" "),v("p",[_._v("参加了机务段组织的夜校学习后，田桂英的想法慢慢发生了转变，她决心和男人一样建设国家。于是，田桂英向段长提出学技术的请求。田桂英告诉记者：“我跟段长说，男人能干的活我也能干，让我干什么都行，只要能学技术。”后来，段长还真把她派到了轻油车库，让她学开轻油车。")]),_._v(" "),v("p",[_._v("田桂英学习热情非常高，政治觉悟也在不断提高。1948年，她加入中国共产党，第二年被评为旅大市一等劳动模范。")]),_._v(" "),v("p",[_._v("不久，一个让田桂英兴奋不已的消息传来，国家要在大连机务段培养新中国第一批女火车司机。")]),_._v(" "),v("p",[_._v("19岁的田桂英不顾家人反对，填写了女火车司机培训班报名表。经过严格挑选，她被安排参加上岗前的学习培训。那时的火车都是蒸汽机车，所以，如何投煤炭便成了田桂英首要也是最难的一项学习任务。15分钟投280锹煤炭，几个小时下来就累得腰酸腿疼，但田桂英还是咬牙挺了下来。")]),_._v(" "),v("p",[_._v("接下来要学的机车构造技术管理和制动机理论，对于田桂英来说就更难了。和她同时进入培训班的还有9人，其中文化程度最高的一人也只是读到了小学6年级，还有几个是文盲。针对这样的情况，老师只能在黑板上画图表，照图讲解，手把手地教，由易到难。晚上回到宿舍，姐妹们七嘴八舌地讨论，互相帮助，直到完全弄懂当天的培训内容。靠着顽强的毅力，田桂英和姐妹们了解了压油、主连杆大端的热度试验及灌油等知识。同时，她们还弄懂了各种信号、道岔转线器等部件原理。")]),_._v(" "),v("p",[_._v("经过近9个月的培训，田桂英和姐妹们全部通过了最后考试。")]),_._v(" "),v("p",[_._v("1950年3月8日，“三八女子包乘组”出车仪式在大连站站前广场隆重举行，田桂英等3人获得了司机证。这一天，由田桂英驾驶的“三八号”机车从大连站缓缓启动，开往旅顺。")]),_._v(" "),v("p",[_._v("“我第一次登上自己驾驶的机车，心里太激动了。我记得车上的每名旅客也很兴奋，因为他们坐的是新中国第一列由女司机开的火车。”田桂英回忆起当时的情景，脸上洋溢着幸福。")]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/img.bimg.126.net_photo_VNHuKSxRyfkUjd3PokKMdw==_3171097087623477611.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_VNHuKSxRyfkUjd3PokKMdw==_3171097087623477611.jpg",alt:""}})])]),_._v(" "),v("p",[_._v("从这一天开始，田桂英和姐妹们成了铁道线上一道亮丽的风景。据她介绍，那个时候，火车上的工作条件可不是今天我们看到的这个样子。她们要在脖子上围个手巾，以免煤灰钻进脖子里，跑一趟下来，满脸都是黑的。工作条件艰苦不说，她和姐妹们还要时刻保持高度警惕，防止行车中发生危险。")]),_._v(" "),v("p",[_._v("在田桂英担任司机长的整整3年时间里，“三八号”机车行程20多万公里，从未发生一起事故。")]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/img.bimg.126.net_photo_Zq7l1IP7FvTUPy0uHCLagg==_3171097087623477610.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_Zq7l1IP7FvTUPy0uHCLagg==_3171097087623477610.jpg",alt:""}})])]),_._v(" "),v("p",[_._v("在那个年代，田桂英的行动影响了成千上万的女青年，开辟了新中国第一代妇女参加工作的新途径，也谱写了中国妇女运动史上的新篇章。")]),_._v(" "),v("p",[_._v("1950年五一劳动节，田桂英驾驶“三八号”机车从大连出发，沿途运送200多名劳动模范去哈尔滨参加东北铁路劳模大会。这是田桂英驾驶“三八号”机车以来，行车距离最长的一次。")]),_._v(" "),v("p",[_._v("1950年9月，田桂英作为东北铁路工人代表，到北京参加全国工农兵劳动模范代表大会。9月25日，来自全国各地的劳模代表聚集在中南海怀仁堂，田桂英被安排向毛主席献旗。")]),_._v(" "),v("p",[_._v("从北京回来，田桂英的生活发生了改变。她不但作为中国青年代表访问了苏联，还成为朝鲜战场上志愿军战士的精神榜样。")]),_._v(" "),v("p",[_._v("1951年，田桂英作为列席代表，进京参加全国政协会议，再次见到了毛主席。毛主席对她说：“我们见过面。”然后，主席打量了一眼田桂英瘦弱的身躯，问：“你能开得动大火车？”田桂英心想：主席，您太小瞧我了！在和主席握手的时候，她故意使了点劲。主席风趣地说：“你的脾气不小呢！”")]),_._v(" "),v("p",[_._v("主席又问：“现在全国上下都在支持抗美援朝，你准备怎样支持抗美援朝呀？”")]),_._v(" "),v("p",[_._v("“报告主席，我准备把火车开到朝鲜战场！”")]),_._v(" "),v("p",[_._v("主席笑了：“你开的是客车，到朝鲜战场不起什么作用。你只要好好工作，多作贡献，就是支持抗美援朝了。”")]),_._v(" "),v("p",[_._v("谈到和毛主席的两次见面，田桂英满怀深情地说：“我从一名普通的渔家后代成长为一名火车司机长，全靠党和毛主席的培养，我从内心深处感激党和毛主席……”")]),_._v(" "),v("p",[_._v("1952年年底，“三八女子包乘组”解散了，田桂英和姐妹们被送到学校补习文化知识。1955年，田桂英考进唐山铁道学院，在机械系学习蒸汽机车理论。毕业后，她被分配到沈阳铁路局机务处当工程师。")]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/img.bimg.126.net_photo_IIvV2EUoPSUbVEi_JDVeMw==_3407254593083442662.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_IIvV2EUoPSUbVEi_JDVeMw==_3407254593083442662.jpg",alt:""}})])]),_._v(" "),v("p",[_._v("在唐院学习期间给同学签字留念")]),_._v(" "),v("p",[_._v("党的十一届三中全会后，她到沈阳局工程处任女工科副科长。")]),_._v(" "),v("p",[_._v("1985年，田桂英离开了工作岗位，但她仍然闲不住，经常应邀到工厂、学校，给工人和学生讲革命传统。近年来，她又来到沈阳市劳模物业公司，每天与20多位离退休劳模一起，义务维护沈阳北运河的环境卫生。")]),_._v(" "),v("p",[_._v("早些年，沈阳市退休办组织成立老年时装模特表演队，要选一个有影响力的劳模参加，选来选去就选上了田桂英。那时的田桂英已经62岁了，刚刚患过脑血栓还没有痊愈。对于模特表演，她一无所知，不过，对于这项新任务她还是接受了。功夫不负有心人！在1995年举行的全国老年时装舞蹈表演大赛上，田桂英和同伴获得了团体第三名的好成绩。")]),_._v(" "),v("p",[_._v("在新中国成立50周年之际，田桂英又随辽宁劳模代表团到北京第三次参加国庆观礼，并受到党和国家领导人的亲切接见。")]),_._v(" "),v("p",[_._v("虽然退休多年，但田桂英始终为自己曾是个铁路人而自豪。她说，现在看到奔驰在铁道线上的动车组列车，这是当年想都不敢想的事情。她为和谐铁路建设取得的成就而感到由衷的高兴。")])]),_._v(" "),v("hr")])}),[],!1,null,null,null);v.default=o.exports}}]);