(window.webpackJsonp=window.webpackJsonp||[]).push([[704],{1065:function(_,v,p){"use strict";p.r(v);var t=p(14),s=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("h1",{attrs:{id:""}},[v("a",{staticClass:"header-anchor",attrs:{href:"#"}},[_._v("#")])]),_._v(" "),v("hr"),_._v(" "),v("p",[v("strong",[_._v("黄万里修桥记")]),_._v(" **"),v("br"),_._v("\n文/郑碧贤 2013.8.2（来源南方周末）")]),_._v(" "),v("hr"),_._v(" "),v("p",[_._v("**")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/images.infzm.com_medias_2013_0801_70519.jpeg@660x440.jpg",alt:""}})]),_._v(" "),v("p",[_._v("高50米、长150米的高家桥，是水利专家黄万里在中国大地上惟一存在至今的立体作品。 （郑碧贤 供图）")]),_._v(" "),v("p",[v("strong",[_._v("高家桥并非无名姓")])]),_._v(" "),v("p",[_._v("“我尝治水涪关道，三载移家居梓州。凿石开河资灌溉，一桥飞若彩虹浮。”")]),_._v(" "),v("p",[_._v("这是被美国名人传记称为“20世纪全世界杰出人物”的水利专家黄万里，为四川省三台县郑泽堰设计的石质渡槽“高家桥”写的诗。高家桥是黄万里在中国960多万平方公里的土地上，惟一存在至今的立体作品。")]),_._v(" "),v("p",[_._v("初识“高家桥”是2006年11月，我第一次踏进三台县，见到横跨两座山峰的石质渡槽——“高家桥”。这座石渡槽，七十多年依然如故，桥身的石缝钻出蓬蓬杂草。法囯里昂中法大学里有个一千多年前的罗马石质渡槽，早已废弃，而这座桥是流动的、生命鲜活的古迹。我问了，但没有人知道它是谁设计修建的。")]),_._v(" "),v("p",[_._v("2008年汶川地震之后，我惦念着这座桥的安危又来到这里，没想它依然巍然屹立，身上连点伤痕也没有。和周围遭地震破坏而坍塌的房屋相比，它平静、淡然、傲视。这一次我认识了三台县93岁的霍连科老先生，他的父亲霍新吾，就是1937年10月在县衙门门口拦马向郑县长呈递修堰图的人，当时是二十九军测绘局局长，也是后来修建郑泽堰的工程总管。霍连科当时是西南联大的学生，这段珍贵的历史在他心埋藏了几十年。见到我的那一刻，他拉着我的手激动得泪花滚滚。他说这座桥是黄万里先生设计修建的呀。")]),_._v(" "),v("p",[_._v("在霍连科老人执意下，由他儿子陪同我们一起来到“高家桥渡槽”，我原来担心他这么大岁数行动不方便，没想到他不仅硬朗而且很兴奋，站在桥头他指着渡槽摆起了龙门阵：在争胜坝南七里到新德乡之间，架一座石拱渡槽，把两山连接起来，水，由争胜上山通过渡槽引向新德山那边；把水送到农民门口。但这里地势险要，山峰突起，沟壑深，要在两峰之间架一座渡槽，需要非常复杂的力学结构，在那个连水泥都没有的年代，其难度可想而知。结实、实用、省钱、美观，这就是黄万里的设计理念。")]),_._v(" "),v("p",[_._v("霍连科说，理念虽好，但难度却出乎意料。高家沟是架桥的必经之路，口子沟底全是烂泥巴、流沙，根本无法施工，挖一锄泥巴，烂泥马上就把锄坑填上，越挖越垮，一个民工下去探底，立即就陷进去，只好用抬绳拉上来。他父亲身材高大不信邪，以为别人不行他行，于是脱光衣服，用四根绳子捆住自己，再砍一根楠竹竿，把竹竿先插下去，他不顾大家劝阻跳下去，下去后才知道烂泥的厉害，只要一动就陷得更深，泥没到脖子还没踩到底。众人把他拉上来时，已全身发青。但那根竹竿还立在那里。")]),_._v(" "),v("p",[_._v("他父亲终日冥思苦想而不得法。有一日，他突然想起什么，从床上跳起来顾不得穿好鞋就往高家沟跑。他见到我父亲、当时的三台县长郑献徵，说可以用青冈木打底桩，青冈木又硬又喜欢水越泡越结实百年后就变成了乌木。这种木材在我们三台到处都有。我父亲高兴地说，马上送报告给黄万里。")]),_._v(" "),v("p",[_._v("经黄万里查证，这种当地盛产的野生椴木，树干笔直，坚硬，生性爱水，在水里越泡越结实，永不腐烂，可谓不是水泥胜似水泥。黄万里也兴奋异常，直呼英明。")]),_._v(" "),v("p",[_._v("两岸滑坡台地，逐台梯次打桩，编栅护坡，垫桥桩就用了三万多根青冈树棒。黄万里是学桥梁出身，后改学水利，称得上是真正的桥梁水利专家。整座桥所用的石料非常讲究，要求石料一律是火包硬石头为条料，石条中若有巴石头夹在中间，一律不能成为架桥的材料，每一块石都不能含糊。这样精选的优质条石，历经了战乱、山洪、地震、自然风化，始终坚固地屹立着。")]),_._v(" "),v("p",[_._v("桥身用坚硬的花岗岩条石，以石灰、砂子、糯米、碎麻混合而成的四合土砌筑；东向西，桥高五十米，长一百五十米，上端桥面宽四米。中间水槽宽二米，深二米，左右各一米宽可供人行。下端桥厚六米，分两层，上层六孔，下层三孔。桥下宽阔的河水由南向北穿流而过，上下两层互相呼应。不仅雄伟，简直就是一幅山水画。“清江急湍跨石桥，晴岚朝旭光摇摇。”这是当时东北大学金毓黻教授对高家桥的感叹。")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/images.infzm.com_medias_2013_0801_70520.jpeg@660x440.jpg",alt:""}})]),_._v(" "),v("p",[_._v("1933年黄炎培先生写给黄万里的一封家书。 （郑碧贤 供图/图）")]),_._v(" "),v("p",[v("strong",[_._v("不能起名叫“万里桥”")])]),_._v(" "),v("p",[_._v("黄万里1911年8月20日出生于上海，自幼顽皮，父亲黄炎培将其长期寄宿于学校严加管教。但他聪明，从小学到大学皆名列榜首，以最优等生毕业。黄万里自幼喜爱文史，且极有天赋，最终选择桥梁工程为专业。22岁任实习工程师参加建造江山江铁桥，以他的智慧、勤奋努力、善待民工，获得大家的喜爱。")]),_._v(" "),v("p",[_._v("1931年，汉江大水一夜间没城，淹死七万人，之后黄河又决口十多处损失无数，使黄万里立志改学水利以知识拯救民族危难。1934年赴美留学专攻水利。1937年抗日战争爆发后为报效祖国，黄万里从美囯回到大后方。")]),_._v(" "),v("p",[_._v("他到四川建设厅报到时就是时任建设厅秘书长的我父亲接待的。俩人一见如故，很是投缘。郑献徵很欣赏这位才华横溢的年轻人，敬佩他为国、为民立志治水的志向。黄万里被任命为涪江航道工程处处长，为了掌握第一手资料，他曾步行三千多公里，六次勘测岷江、沱江、涪江、嘉陵江，建立起自己的水文地貌的数据库。这些都得到我父亲的支持。当黄万里在何北衡厅长那里得知三台旱情，郑献徵要求为三台兴修水利，解民之苦的消息时，他第一个向何北衡厅长报名参加。")]),_._v(" "),v("p",[_._v("27岁的黄万里被任命为总工程师。来三台之前，他仔细研究过霍新吾的测量图和计划，也查过三台的地质地貌，但他要亲自再进行实地考察。治理江河、修缮农村水利，涉及的可能是人命关天、子孙万代的大事，不可丝毫懈怠。")]),_._v(" "),v("p",[_._v("黄万里带着老婆孩子到三台来，在离工地最近的地方修了一栋茅草房，供他的家眷和员工住宿。大有工程不完决不回家之势。")]),_._v(" "),v("p",[_._v("几天时间他穿山越岭纵横一百五十多公里查看地形地貌，“日踏芒蹊行百里，归途重过彰明丘。匡山太白读书处，犹瞰江滨中坝头。”在勘测过程中，设计构思已在逐步形成；哪里该凿山；哪里该劈崖；哪里该飞架渡漕；哪里该引水上山，大渠、支渠、小渠，将如一张大网覆盖这片干渴的土地……")]),_._v(" "),v("p",[_._v("老百姓为这座天生伟岸的石质渡槽取名“万里桥”。但黄万里的父亲黄炎培先生不同意：“一个才27岁的年轻人，承受不了这么大的荣誉，就以当地的村名叫‘高家桥’吧。”")]),_._v(" "),v("p",[_._v("黄万里在三台出生的女儿，被爷爷取名“黄无满”，以戒骄傲。老年的黄万里每念及父亲亦曾沉痛地反思自己：“星河回转岁月流，宇内寓形八十秋，忆昔童蒙犹似昨，悲欢历历思悠悠。双亲养我多劳劬，勤苦工农亦足讴，愧恨平生效世少，拼将心力为群谋。”")]),_._v(" "),v("p",[_._v("桥修成后，当地的孩子特别喜欢这座又高、又长、又险的桥，孩子们打着赤脚在仅一米宽的渡槽上玩，开始是小心翼翼地走来走去；老虎不咬人，就开始跑，双脚跑还不过瘾，就在上面滚铁环玩，嘻嘻哈哈的笑声在山涧之间回荡。没来过的小孩心痒难耐，也要一试高低，便问：桥有多高？去过的孩子傲然答：有天那么高！")]),_._v(" "),v("p",[v("img",{attrs:{src:"/pic/images.infzm.com_medias_2013_0801_70521.jpeg@660x440.jpg",alt:""}})]),_._v(" "),v("p",[_._v("1937年，黄万里、丁玉隽夫妇与长女黄且圆在四川三台。（郑碧贤 供图）")]),_._v(" "),v("p",[v("strong",[_._v("农民“捐地”VS政府“征地”")])]),_._v(" "),v("p",[_._v("2008年5月下旬，我匆匆回到北京，通过各种方法终于找到了黄万里的女儿，黄万里已在2001年带着一颗对黄河仍然担忧的心走了。")]),_._v(" "),v("p",[_._v("这座坐落在清华大学教师宿舍的套房，平实无华，甚至有些破旧，但很温馨，到处都有黄万里先生的身影、气息。客厅外有块十多平方米很小的园地，种了些花草小树，这是黄先生生前最爱来的地方。也是在这个地方与几位老朋友相遇聊天，写了篇“花丛小语”表达一点市政建设修路的意见而戴上右冠二十多年。客厅墙上装裱在镜框里的一封家书，是黄炎培先生1933年写给黄万里的：")]),_._v(" "),v("p",[_._v("“儿万里将赴美学习土木工程，濒行，特写下列数条，备留学时之自省：")]),_._v(" "),v("p",[_._v("一，认清求学包括自身修养功夫，勿因注意物质科学，而忘却精神修养。修养方法，最要在养成规律生活。（写日记切勿间断。）")]),_._v(" "),v("p",[_._v("二，注意锻炼身体。多锻炼，便不至怯弱，亦不至肥胖。")]),_._v(" "),v("p",[_._v("三，学自外国，勿忘在中国。须时时注意中国需要何种学识与技能。四，见爱好之物，勿忘其中尚有坏处存在，处处须深一层看。五，语言、容貌、态度皆须注意整饬。使人起敬重心。勿惹人轻视，以至厌恶。六，专门学者，必须熟悉人情世故，才行得通。否则全无用处。民国二十二年除夕，父书”")]),_._v(" "),v("p",[_._v("他还要求儿子在任何时候， 绝不可忘对人做事的原则：")]),_._v(" "),v("p",[_._v("一、必须尊重农民。我国自有历史以来，劳动人民从来沒有对不起我们统治阶级。")]),_._v(" "),v("p",[_._v("二、为人必须喷出热血地爱人！爱，乃是道德的根基，所谓“真善美”实皆包含在爱中。")]),_._v(" "),v("p",[_._v("三、戒骄傲。古人云：“虽有周公、孔子之德之能而骄者，则其人决不得称贤”。戒骄不是仅求在言行，乃是要求从内心出发。")]),_._v(" "),v("p",[_._v("黄夫人丁玉隽坐在轮椅上，行动不太方便，有她的小女儿肖路帮忙，一些往事还能慢慢回忆起来。我与肖路虽然从未谋过面，但因父辈是故交，彼此少了些客套，多了份亲情。连她家的四川籍保姆对我也格外亲热。饭后，肖路说：妈妈今天特别高兴，吃得也比平时多。我问黄夫人还记不记得郑献徵，她说：“当然记得，他长得很帅哟，那时他是三台县县长，年轻、能干。我们在三台住了三年呐。我还在霍新吾家吃过好多次饭哟，他夫人的菜烧得好吃，门口那棵黄桷树好大、好香。我总爱摘几朵戴在身上，要香一天。”黄万里夫人丁玉隽，是国民党元老、山东同盟会创始人丁惟汾之幼女。")]),_._v(" "),v("p",[_._v("见她高兴，我就问起她与黄万里相识、相恋、相爱的故事。她浅浅地笑了，黄万里写的“忆初遇”那首诗她还能诵出几句：“重洋游学罢，东风送，巨舸泊横滨。看蓬莱胜境，樱花未兴；屐声遥震，和服轻匀。蓦地里，惊鸿掠清影，大地破新春……”丁玉隽把黄万里带回山东老家向父亲介绍时，丁老先生听说黄万里是上海人，不由分说当场就把他赶了出去。在老人心中上海人就是小赤佬。后经黄炎培出面才成就了这段美好的神仙眷侣。")]),_._v(" "),v("p",[_._v("他们在疾风暴雨中相伴一生，却没躲过1957年那场险恶的漩涡。“窗外檐声催夜沉，灯前玉指刺针深。停针长叹泪沾臆，折煞狂夫一片心。”与妻夜坐无寐，坚强的黄万里也有痛楚。")]),_._v(" "),v("p",[_._v("这位天才的水利专家，在他46岁时，以精确的数据舌战群儒七天，对违反科学的“圣人出黄河清”的阿谀之词，进行毫不留情的反驳：“迋争面折迄无成，既阖三门见水清。终应愚言难蓄水，可怜血汗付沧溟。”“樗散书生不晓机，竟然抗疏犯龙鳞。”“逆龙鳞”得到的回报是一顶右派帽子，以及鞭打、剃阴阳头、抄家、扫厕所。但黄万里始终坚持自己的意见：“一定要修将来要闯祸的，历史将证明我的观点。”三门峡工程建起来了。违背大自然科学的工程，给人类带来了正如黄万里所预见的无穷灾难。黄万里痛心疾首，几十年来不停地抗争，当了右派也要向全国人大写议案、写文章论述。真是“自古长才难为用，孔丘汲汲屈平恸”。直到2001年黄万里90岁时的临终遗言还是惦着治水，遗言不是留给家人子女，而是用颤抖的手向看望他的学生所嘱：“治江原是囯家大事，‘蓄’‘拦’‘疏’及‘挖’四策中，各段仍应以防‘拦’为主。汉口段力求堤固，堤临水面宜打钢板柱，背水面宜以石砌，以策万全，盼注意，注意。”黄万里活得艰难，却活得堂堂正正，有声有色。")]),_._v(" "),v("p",[_._v("临别时，黄肖路送了我一本《长河孤旅》，其中有黄万里在各个荣辱时期留下的诗篇。诗歌记载了他不朽的人生，记载了他那颗为国为民的质朴良心。")]),_._v(" "),v("p",[_._v("我决定以黄万里先生设计修建的“高家桥”为起点，加固、修复渡槽，疏通河道，从河床梯级式向上延伸直到山坡，修建一座纪念碑和水博物馆，展示古堰的历史和现代文明，开办农业水利知识讲座、增设滴水书屋，把这块荒山坡建成一座小而精的水文化园，也许它还会带动一方百姓的发展。")])])}),[],!1,null,null,null);v.default=s.exports}}]);