(window.webpackJsonp=window.webpackJsonp||[]).push([[714],{1074:function(_,v,p){"use strict";p.r(v);var t=p(14),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|")]),_._v(" "),v("blockquote",[v("p",[v("strong",[_._v("建文化唐山需“交大”回归")])]),_._v(" "),v("p",[_._v("**")]),_._v(" "),v("p",[_._v("——关注“唐山交大”旧址复建系列报道（三）")]),_._v(" "),v("p",[_._v("**")]),_._v(" "),v("p",[_._v("转载"),v("a",{attrs:{href:"http://www.yzdsb.com.cn/",target:"_blank",rel:"noopener noreferrer"}},[_._v("燕赵都市网 http://www.yzdsb.com.cn"),v("OutboundLink")],1)]),_._v(" "),v("p",[_._v("燕赵都市报冀东版记者 任哲 尚开红")]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/img.bimg.126.net_photo_uM5RmoQWYHn712U6t5kZQA==_3433994715870811718.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_uM5RmoQWYHn712U6t5kZQA==_3433994715870811718.jpg",alt:""}})])]),_._v(" "),v("p",[_._v("唐院铁道学院明诚堂。（资料片）")]),_._v(" "),v("p",[v("strong",[_._v("难以磨灭的文化名片")])]),_._v(" "),v("p",[_._v("“唐山有两张其他城市无法复制的名片，一张是中国近代工业发源地开滦；一张就是中国高等工程教育发源地唐山交大。”")]),_._v(" "),v("p",[_._v("随着唐山建设的不断发展，唐山市主要领导提出“要经过5至10年的努力把唐山建设成为经济强城、文化名城、宜居靓城、滨海新城。”而对于唐山文化名城的建设，作为曾在唐山创造过辉煌历史的唐山交大，无疑是一张分量极重的“文化名片”。")]),_._v(" "),v("p",[_._v("而唐山交大这张“文化名片”在唐山的完美展出，需要的则是对唐山交大旧址的复建。")]),_._v(" "),v("p",[_._v("作为中国高等工程教育发源地之一，唐山交大在中国工程史上创造了诸多令人骄傲的成绩。在该校位于唐山67年的历史中，先后培养出的众多院士，更如一颗颗璀璨的明珠，让唐山交大的历史散发出耀眼的光芒。")]),_._v(" "),v("p",[_._v("“唐山有两张其他城市无法复制的名片，一张是中国近代工业发源地开滦；一张就是中国高等工程教育发源地唐山交大。”对于唐山交大在唐山文化中的重要地位，复建唐山交大旧址的3名发起人，有着自己独特的看法。")]),_._v(" "),v("p",[_._v("据他们介绍，被誉为“东方康奈尔”的唐山交大，不仅培养了詹天佑、茅以升、竺可桢、林同炎等著名学者，成就了中国第一座现代化大桥“钱塘江大桥”等百余项全国第一，更以其“俟实扬华”为代表的科教兴国精神，感染了一批又一批在各自领域颇有成就的学子。至今许多从唐山交大毕业的海外著名教授，心中依然将唐山交大作为敬爱的母校。这种兴学重教的理念，应成为“感恩、博爱、开放、超越”新唐山人文精神的重要历史文脉之一。")]),_._v(" "),v("p",[_._v("与复建唐山交大的3名发起人看法相同，原唐山交大教授王兆祥在接受本报记者采访时也表示，目前位于唐山市的全国知名高校不多，而唐山交大作为中国高等工程教育的源头之一，复建该校园旧址无疑能为唐山的文化教育事业，增加一枚分量极重的砝码，让唐山市的高等教育从人们感情认知上提升一个高度。")]),_._v(" "),v("p",[_._v("此外，唐山师范学院经济管理系教授邱珂认为，作为重工业城市唐山，虽然有着深厚文化底蕴，但目前来看，文化氛围比较薄弱，文化设施相对较少。“作为精神文明的物质附属，大学是一个重要的标志。”邱教授说，虽然唐山目前拥有4所本科院校，其中2所为省属院校，但对于唐山打造文化名城和科技示范区来说，仍显得有些少。复建唐山交\n大旧址，并很好地加以利用，不但能为唐山增加一所历史名校的景观，提升南湖生态城的整体文化品位，对于唐山建设文化名城来说，也大有裨益。")]),_._v(" "),v("p",[v("strong",[_._v("城市发展的人才支撑")])]),_._v(" "),v("p",[_._v("“复建唐山交大旧址不仅是要复建其学校原貌，更重要的是要恢复治学严谨的唐山交大精神，为唐山市的发展建设培养更多的专业人才。我希望能让它作为一所院校，为唐山培养人才。”")]),_._v(" "),v("p",[_._v("在众多复建唐山交大旧址支持者心中，复建唐山交大旧址仅仅是恢复这座名校辉煌的第一步。如何利用唐山交大复建后的校园、利用好唐山交大这块“金字招牌”，为唐山市的发展培养更多的人才，则是这些支持者们最终的梦想。")]),_._v(" "),v("p",[_._v("如今的唐山市正处在一个飞速发展的时期，城市的规划和建设日新月异，“七大主导产业链”、“四大主体功能区”以及“四点一带”的产业发展，尤其是南湖生态城的开发、曹妃甸深水大港的建设、新首钢精品钢材生产基地、我国第一个国产时速350公里高速铁路动车组生产线等一系列新兴项目，需要着大量的专业人才。而作为工程院校的唐山交大，正是以培养工程人才著称于世，复建唐山交大校园，利用唐山交大培养人才，成为了与唐山市发展相得益彰的事情。")]),_._v(" "),v("p",[_._v("唐山市分管文教的原副市长冯国安，日前在接受本报记者采访时表示，复建唐山交大旧址不仅是要复建其学校原貌，更重要的是要恢复治学严谨的唐山交大精神，为唐山市的发展建设培养更多的专业人才。")]),_._v(" "),v("p",[_._v("在1972年搬离唐山市区后，事实上与唐山市有着深厚渊源的这所高校，很长一段时间内并未真正远离唐山。据冯国安介绍，上世纪80年代，时任唐山市分管文教工作副市长的他，曾在西南交大校庆时前往成都，为该校送上了绘有原唐山交大校门的陶瓷壁画。此外，他还与西南交大学校领导，商谈了西南交大利用留唐教授为唐山市培养学生的事宜。")]),_._v(" "),v("p",[_._v("“当时考虑到西南交大在唐山培养学生，与西南交大商议后，决定让留唐教授在唐山开4个专业，先培养三年制的大专学生。”冯国安回忆说，在于西南交大签订了这项在唐山培养学子的协议后，没多久，他们又与西南交大协商决定了在唐山成立西南交大本科分校，利用西南交大指标在唐山培养本科学生。西南交大唐山分校设在了当时的唐山大学（今唐山学院），位于成都的西南交大也向唐山派来了授课教授。")]),_._v(" "),v("p",[_._v("然而，随着时间的推移，西南交大唐山分校设立后所在校园———唐山大学发展成为了拥有本科教育的唐山学院，西南交大唐山分校也退出了唐山教育体系。")]),_._v(" "),v("p",[_._v("“唐山交大旧址重建一个重要的方面，是校园建设后如何利用，我希望能让它作为一所院校，为唐山培养人才。”采访期间，对于唐山交大旧址复建后的用途，冯国安提出了自己的想法。")]),_._v(" "),v("p",[v("strong",[_._v("交大重建与南湖开发")])]),_._v(" "),v("p",[_._v("“目前南湖开发中对自然景观的规划建设非常漂亮，但人文景观的建设相对较少，而这恰恰是复建唐山交大旧址能够满足的。”")]),_._v(" "),v("p",[_._v("2007年，唐山市委立足建设科学发展示范区和人民群众幸福之都的全局，对城市南部采沉区生态化改造进行重新审视和定位，整合南部采煤塌陷地及其周边地区、开发建设南湖生态城成为唐山生态城市建设的重中之重。2008年，市委、市政府又明确指出，按照省委“三年大变样”的要求，举全市之力，把南湖打造成世界一流的城市中央生态公园，把唐山打造成全国闻名的“华北水城”。目前，地震遗址公园建设、扩湖工程、环湖景观路建设、垃圾山封山等重点工程相继竣工，南湖生态公园也已于5月1正式开园。")]),_._v(" "),v("p",[_._v("和南湖生态城热火朝天的建设相对，与南湖公园仅相隔一条建设南路的唐山交大旧址，则显得冷冷清清。低矮的平房、熙熙攘攘穿行的人群———这座曾经辉煌校园的旧址，依旧沉睡在市民的生活中。")]),_._v(" "),v("p",[_._v("据了解，目前存在于唐山市路南区的唐山交大旧址，位于正在开发中的南湖生态城自然风景区东部。整个唐山交大旧址范围，向东与地震遗址公园相隔大学路，向北与开发中的国家矿山公园紧邻，本部原址占地600余亩，建筑面积约10万平方米。其校园旧址全部为欧美式园林建筑风格，大多数建筑为两至三层，整个校园规划错落有致。")]),_._v(" "),v("p",[_._v("“目前南湖开发中对自然景观的规划建设非常漂亮，但人文景观的建设相对较少，而这恰是复建唐山交大旧址能够满足的。”作为一直关注唐山交大旧址的复建发起人，苏伟对唐山交大旧址复建与南湖生态城开发，有着自己独特的见解。他告诉记者，按照复建唐山交大旧址3名发起人的设想，唐山交大旧址复建后，将成为南湖生态城区域内一处文化底蕴的独特人文景观，其难以复制的文化特性，将与南湖生态公园的自然景观融为一体，给前来南湖景区游玩的市民，提供更丰富的旅游景点。")]),_._v(" "),v("p",[v("strong",[_._v("老交大人心中的期盼")])]),_._v(" "),v("p",[_._v("“河北省尤其是唐山市，现在全国知名的高校很少，而唐山交大曾经非常有名气和影响。”")]),_._v(" "),v("p",[_._v("如今已经退休在家的原唐山市政协主席李汉，对于唐山交大有着特殊的感情。")]),_._v(" "),v("p",[_._v("“我父亲和姐夫都是交大学生，我大学也在交大读书。”提起唐山交大，李汉自豪地向记者讲述了他与这所学校的渊源。据李汉介绍，他的中学时代就读于当时位于贵州的交大附中，1946年考上的唐山交大，1948年成为了唐山交大的学生会主席。解放后，离开唐山交大的李汉参加了工作，虽然自己离开了学校，但与校友们之间的联系并没有间断。")]),_._v(" "),v("p",[_._v("按照李汉老人的回忆，1972年唐山交大迁往四川峨眉后，曾与重新迁回唐山擦肩而过。1984年前后，已经迁至四川峨眉的唐山交大面临着再次的校址搬迁，而在重新选定校址上，出现了重新回唐山和留在四川两方面意见。在此期间，不少身在四川峨眉的交大校友找到时任河北省政府秘书长的李汉，希望能将处于四川峨眉的交大重新迁回唐山。")]),_._v(" "),v("p",[_._v("“交大的这个想法我当时特别欢迎，随后与当时分管教育的副省长商议后，对交大迁回唐山做出了支持的批复。”李汉告诉记者，但由于多方面的原因，从峨眉迁出的“交大”1989年最终落户在了四川成都，更名成了西南交通大学。而这次机会的错失，也让唐山市失去了回迁唐山交大最好的机遇。")]),_._v(" "),v("p",[_._v("虽然错过了唐山交大回迁唐山市的最好机遇，但获知目前正在有3名民间人士忙碌于唐山交大旧址复建呼吁后，李汉和其他7名原唐山交大校友们一起，于今年2月份，给唐山市主要领导提交了《关于恢复唐山交通大学的建议》，随后该建议得到了唐山市主要领导的支持和批复。")]),_._v(" "),v("p",[_._v("“河北省尤其是唐山市，现在全国知名的高校很少，而唐山交大曾经非常有名气和影响。”李汉告诉记者，作为给唐山市主要领导提出的建议，他们希望能够通过恢复唐山交大旧址，来提高唐山高等教育的档次和水平。")])]),_._v(" "),v("hr")])}),[],!1,null,null,null);v.default=n.exports}}]);