(window.webpackJsonp=window.webpackJsonp||[]).push([[724],{1087:function(v,_,p){"use strict";p.r(_);var t=p(14),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("|")]),v._v(" "),_("blockquote",[_("p",[_("strong",[v._v("学 校 动 态")])]),v._v(" "),_("p",[v._v("**"),_("br"),v._v(" "),_("strong",[v._v("文/校内新闻")])]),v._v(" "),_("p",[v._v("1. 我校正式启动第三期“211工程”建设项目")]),v._v(" "),_("p",[v._v("2. 我校获准进入国家优势学科创新台")]),v._v(" "),_("p",[v._v("3. 我校一批学科成为省级重点学科")]),v._v(" "),_("p",[v._v("4. 我校与常州市政府签署合作协议")]),v._v(" "),_("p",[_("strong",[v._v("一、我校正式启动第三期“211工程”建设项目")])]),v._v(" "),_("p",[v._v("2008年11月，国家发改委和教育部正式批复我校三期“211工程”中的11个重点学科建设项目。为此，我校三期“211工程”建设正式启动，建设时间从2008年至2011年止。")]),v._v(" "),_("p",[v._v("我校三期“211工程”建设项目包括11个重点学科项目、创新人才培养和队伍建设项目以及校内公共服务体系建设项目，建设总投资达一亿九千多万元人民币。")]),v._v(" "),_("p",[v._v("我校是首批进入“211工程”建设的重点大学。“211工程”的建设，有力地促进了我校学科的发展和整体水平的提高，“211工程”二期的重点学科建设项目取得许多重大成果，其中，“铁道机车车辆-\n轨道耦合动力学理论体系、关键技术及工程应用”获国家科技进步一等奖，入选2005年中国高校十大科技进展；“牵引供电自动化系统成套技术及其应用”获国家科技进步二等奖；“高温超导磁悬浮实验车”入选2001年中国高校十大科技进展、获四川省科技进步特等奖。通过重点学科项目建设，我校的学科整体水平有了极大的提高。交通运输工程在全国一级学科整体水平评估中排名第一。重点学科建设带动了相关学科的发展，学校的科研综合实力明显增强。“十五”期间我校科研经费达到7.5亿元，并在2005年年度经费突破2亿元大关。其中纵向项目科研经费达3.1亿元，占整个科研经费的41.3%。鉴定科研成果103项。获科技成果奖励61项，其中国家级奖励8项。")]),v._v(" "),_("p",[v._v("经过两期的建设，我校的“211工程”重点学科建设项目的质量有实质性提升，项目数量也实现稳步递增：从一期的6个、二期的8个发展到三期的11个，并且增强了对理学、物流、地质、测量等多个学科专业的带动和促进作用。这11个重点学科项目包括高速列车基础研究平台、轨道交通车辆运行安全关键技术、机械设计理论及先进制造技术、新材料制备与工艺控制技术及应用、大型桥梁隧道静动力设计理论与关键技术、高速铁路线路设计理论与关键应用技术、岩土体动力性质及工程结构安全稳定、智能型地面快速交通运输系统关键技术、智能信息传输与列车控制安全保障技术、轨道交通电力牵引与传动控制、牵引供电与自动化。")]),v._v(" "),_("p",[_("strong",[v._v("二、我校获准进入国家优势学科创新平台")])]),v._v(" "),_("p",[v._v("2008年12月，教育部、财政部批准了我校“轨道交通运输工程优势学科创新平台”项目，这标志着我校进入了国家高水平大学建设行列。")]),v._v(" "),_("p",[v._v("国家设立“优势学科创新平台”建设项目，力争使一部分高校在一些领域达到国际领先水平。我校申请的“轨道交通运输工程优势学科创新平台”项目获批准立项，标志着学校学科建设乃至整个水平和实力实现了新的突破和发展。")]),v._v(" "),_("p",[_("strong",[v._v("三、一批学科成为省级重点学科")])]),v._v(" "),_("p",[v._v("2008年11月\n，四川省教育厅、四川省人民政府学位委员会对省级重点学科进行了重新确认和遴选新增工作。经省学位办组织专家评审、省教育厅审核批准，由四川省教育厅、四川省人民政府学位委员会下达了四川省省级重点学科名单，我校13个一级学科和8个二级学科榜上有名。")]),v._v(" "),_("p",[v._v("此次我校获批13个的四川省一级学科省级重点学科包括：马克思主义理论、力学、材料科学与工程、电气工程、电子科学与技术、信息与通信工程、控制科学与工程、土木工程、测绘科学与技术、地质资源与地质工程、生物医学工程、管理科学与工程、工商管理；获批的8个四川省二级学科省级重点学科包括：比较文学与世界文学、凝聚态物理、建筑设计及其理论、安全技术及工程、环境工程、行政管理、应用数学、计算机应用技术。")]),v._v(" "),_("p",[v._v("我校的省部级重点学科从1987年的15个铁道部重点学科，发展到2000年的17个、2004年的24个省部级重点学科，到现在的13个一级学科成为省级重点学科、8个二级学科成为省级重点学科。而且，13个一级学科省级重点学科（涵盖46个二级学科，其中有2个为国家重点学科，2个为国家重点（培育）学科）。这些表明我校的省部级重点学科建设取得了长足的进步。")]),v._v(" "),_("p",[v._v("开展省级重点学科建设工作，是进一步落实科学发展观，推动四川省高等教育更加上水平、上台阶，更好地发挥教育服务于地方经济的重要举措。我校将按照分层次进行重点学科（国家级、省级、校级）建设的原则，高度重视省级重点学科建设工作，认真制定省级重点学科建设与发展规划。根据学科发展趋势及国内外学术动态，结合国家和四川省经济建设重大需求及我校实际情况，提出学科发展的目标和具体建设方案，制定学科研究方向、人才培养、科学研究、学术队伍建设、实验室管理体制、经费投入与使用等方面的规划和具体措施，确保省级重点学科建设工作取得实效，为国家级重点学科的申报和建设，为把我校建设成具有交通特色的多学科协调发展的高水平研究型大学打下坚实的基础。")]),v._v(" "),_("p",[_("strong",[v._v("四、我校校长陈春阳率团参加并与常州市政府签署合作协议")])]),v._v(" "),_("hr"),v._v(" "),_("p",[v._v("2009中国常州先进制造技术成果展示洽谈会于5月18日在江苏常州举行，我校校长陈春阳应邀赴常州市参加洽谈会，并与常州市人民政府签署合作协议。")]),v._v(" "),_("p",[v._v("校长助理、研究生院常务副院长蒲云，校长办公室主任赵彦灵，科技处处长张文桂、处长助理周南，招生就业处处长谭建鑫，校产集团总经理苏斌，牵引动力国家重点实验室主任张卫华，党总支书记吴柏青，电气学院院长高仕斌，超导中心主任赵勇，机械学院副院长肖世德等相关部门负责人陪同陈校长参加了洽谈会。")]),v._v(" "),_("p",[v._v("陈春阳校长一行在常州其间与常州市常务副市长、常州科教城党工委书记王成斌，科教城党工委副书记、管委会副主任赵正斌及科教城有关领导进行了会谈。会后，常州市市委书记范燕青、常务副市长王成斌、市委秘书长常青等常州市领导会见了陈春阳校长一行，双方就市、校发展需求，产学研合作模式以及适时建立研究院等方面交换了意见，并达成了共识。")]),v._v(" "),_("p",[v._v("18日，陈春阳校长出席了2009中国常州先进制造技术成果展示洽谈会开幕式与签约仪式。签约仪式上，陈春阳校长与常州市市长王伟成分别代表校市签署了《常州市人民政府西南交通大学关于加强全面合作的协议》。按照协议内容，双方将在共同促进科技成果转化、争取共建科技创新平台、共建人才培养、就业与创业机制等方面进行全面合作，共同发展，实现双赢。")]),v._v(" "),_("p",[v._v("此外，陈春阳校长一行还访问考察了南车戚墅堰机车有限公司、常州轨道车辆牵引传动工程技术研究中心和江苏新誉风力发电设备有限公司。")]),v._v(" "),_("p",[v._v("。")])]),v._v(" "),_("hr")])}),[],!1,null,null,null);_.default=n.exports}}]);