(window.webpackJsonp=window.webpackJsonp||[]).push([[729],{1089:function(t,n,v){"use strict";v.r(n);var _=v(14),o=Object(_.a)({},(function(){var t=this,n=t._self._c;return n("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[n("blockquote",[n("p",[n("strong",[t._v("我校校友王玉泽、刘培硕")])]),t._v(" "),n("p",[n("strong",[t._v("荣获全国工程勘察大师称号")])]),t._v(" "),n("p",[t._v("**"),n("br"),t._v(" "),n("strong",[t._v("文/校友办")])]),t._v(" "),n("p",[t._v("中华人民共和国住房和城乡建设部2008年12月发布公告第211号，公布了第六批全国工程勘察设计大师名单共26名。我校王玉泽校友和刘培硕校友位列其中。")]),t._v(" "),n("p",[n("strong",[t._v("王玉泽校友")]),t._v("\n，1982年毕业于西南交通大学铁道工程系，现任中铁第四勘察设计院集团有限公司总工程师。教授级高级工程师，享受国务院政府特殊津贴，铁道部“铁路专业技术带头人”，湖北省五一劳动奖章获得者、湖北省“优秀科技工作者”，湖北省新世纪高层次人才工程第二层次人选。")]),t._v(" "),n("p",[t._v("多年来，王玉泽敬业奉献，创造性地解决了诸多工程技术难题。特别是担任我国第一条高速铁路京沪高速铁路总体设计负责人以来，勇于面对挑战，相继完成了国家“九五”科技攻关项目等多项专题研究工作，取得大量科研成果。为稳定线路方案，组织开展了四院历史上三次大规模的勘察设计工作，为京沪高速铁路建设作出了突出贡献。担任铁四院主要技术领导后，他更是严格自律、精益求精，强化技术管理，加大科技研发力度，为全面提高铁路设计技术水平做了大量实在有效的工作。从一个初出茅庐的大学生，逐步成长为交通工程建设领域特别是高速铁路勘测设计领域有较高专业造诣的知名专家。")]),t._v(" "),n("p",[n("strong",[t._v("刘培硕校友")]),t._v("\n，1982年毕业于西南交通大学桥隧系，工学学士。2002年获西南交通大学建筑与土木工程硕士学位。现任铁道部第一勘察设计院副院长、教授级高级工程师。刘培硕从事隧道及地下工程勘察设计工作26年，作为总体设计负责人，先后主持或负责了秦岭特长隧道、乌鞘岭隧道、终南山公路隧道、关角隧道等长大隧道的总体设计、科研及技术决策工作，以及青藏、西康、宝中、侯月、兰新等多条铁路长大干线，哈大、郑西、西宝等客运专线，北京、南京、西安地铁，三峡对外交通专用公路等多项国家重点工程项目的隧道及大型地下工程的规划、勘察设计、技术决策及科研工作。其中“秦岭特长铁路隧道修建技术”科研成果获2004年度国家科技进步一等奖，秦岭Ⅱ线特长隧道工程获国家优质工程金质奖。")]),t._v(" "),n("p",[t._v("刘培硕在多年的工作中，开创了我国隧道勘测设计领域的多项第一，为我国铁路及公路隧道建设做出了突出的贡献。先后荣获铁道部标准设计一等奖、国家银奖、重点项目建设青年功臣等光荣称号。")])]),t._v(" "),n("hr")])}),[],!1,null,null,null);n.default=o.exports}}]);