(window.webpackJsonp=window.webpackJsonp||[]).push([[744],{1105:function(_,v,p){"use strict";p.r(v);var t=p(14),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|")]),_._v(" "),v("blockquote",[v("p",[v("strong",[_._v("中国桥梁少帅——周孟波（上）")])]),_._v(" "),v("p",[_._v("**"),v("br"),_._v(" "),v("strong",[_._v("文/雷斌")]),_._v(" T>")]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/img.bimg.126.net_photo_wvmp0-JTIJuG1DmrK0FEVQ==_4856850723143133558.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_wvmp0-JTIJuG1DmrK0FEVQ==_4856850723143133558.jpg",alt:""}})])]),_._v(" "),v("p",[_._v('2004年5月28日下午14点40分，西南交通大学学生会堂已经座无虚席，各专业、各年级的学弟学妹们期待着，朋待他们的师兄，1990届硕士毕业生，中铁大桥局股份有限公司董事长、党委副书记，全国劳动模范，中国青年五四奖章获得者，中国铁路工程总公司优秀企业家——周孟波校友给他们做\n"成功之路"的先进事迹报吉。15点正，周孟波董事长走上了主席台，全场掌声雷动。在整个报告会的过程中，交大人被他的事迹深深打动，不时响起热烈的掌声，交大为有这样忧秀的校友而骄傲，同学们也为自己是交大人自豪。榜样的作用是无穷的，优秀校友周孟波的先进事迹给予交大学子无尽的动力。就让我们来细细体味周孟波的人生历，解析他的成功之路。')]),_._v(" "),v("p",[v("strong",[_._v("一。吃苦耐劳 默默奉献")])]),_._v(" "),v("p",[_._v("每一个成功者都具有相同的可贵品质，这就是:刻苦勤奋、知难而上、勇担重任、无私奉献、求真务实、勇于创新。周孟波就是具有这些优秀品质的企业家。")]),_._v(" "),v("p",[_._v("1965年2月，他出生在河南洛阳一个农民家庭。小时候，他的理想就是成为一名桥梁设计师。1981年，年仅16岁的他，就以优异的成绩考上铁道兵工程学院(今石家庄铁道学院)桥梁专业，向他的理想迈进了一大步。农村艰苦的生活、铁道兵工程学院严格的军事训练，造就了周孟波吃苦耐劳、刻苦勤奋的品质。1985年毕业时，他最大的愿望就是能够回到洛阳，去铁道部地十五工程局工作，这样既可以实现自已的理想，也能够照顺父母。")]),_._v(" "),v("p",[_._v("20岁这一年，周孟波巳经开始在自己设计好的人生之路上前行。")]),_._v(" "),v("p",[_._v("他顺理成章地成为铁事务局的一名职工。他背包还没有来得及打开，就接到了一纸通知：赴新疆参加北疆铁路建设。6000里路以外的那个不毛之地，注定成了周孟永水远唯以忘记的一段人生历程。")]),_._v(" "),v("p",[_._v("北疆铁路是兰新线的延伸，由乌鲁木齐向西，直抵阿拉山口。在那里与哈萨克铁路接轨，通过俄罗斯、德国直到荷兰阿姆斯特丹。这是一条名副其实的“欧亚大陆桥”。北疆铁路所在的地段大部分是隔壁荒滩，上年是人沙裹首风、风卷着沙，风起之处一米之外看不见人影，由于远离人烟，生活的艰苦自不必言。最难的还是住，搭起的帐篷常常第二天早上就没了踪影，只好在戈壁滩上就地挖一个洞，上面盖上木头，木头上盖上一层油毡。这种地窝子被大家戏称为“高级墓室”。夏天下雨的时候，一大帮子人只能冲出来，眼睁睁地看着地窝子灌满水。")]),_._v(" "),v("p",[_._v('分配给他的任务是搬片石、抗草袋、修护坡。开始，他有些想不通，四年的大学就为了干这个？想归想，当他看到比他大的老工程师也在高高兴兴地做这个工作，心中慢慢地平静下来了。他记起了临来时父亲的嘱咐：一定要好好地干！他想到自己年轻，干脆把码围堰和打水井都揽了过来。后来，兼任了测量员，每天早晨，工具袋里塞两个窝头，几块咸菜。拎着垂球和记录本出发，常常到深夜才能回到地窝子吃饭休息。他的吃苦耐劳的精神得到领导和同事们的普遍赞扬。不久，领导让他当了技术主管，他有些惶恐，学校里学的那点儿知识一下子就不够用了。一条线上有小桥、中桥、大桥，有圆涵、板涵、箱涵，路基、石方、车站……甚至细化到信号灯如何设置、锥体护坡怎么铺，都是他这个技术主管必须了解的事情。北疆铁路这么大的工程，任何一点的质量问题就是国际大事。周孟波没有心思去想太多的将来，常常是白天跑一整天工地，晚上钻进自己那间小地窝子，点上一枝蜡烛，在摇曳的烛光下一点一点地看材料、准备第二天的图纸……铺在地上的草席边就是挖开的地质剖面，一层砂石、一层卵石，这是典型的海相地层，已经累积了几十万年。每一次画图累了的时候，周孟波抬头就能看见这些。一个人的一生和这些相比是那么短暂，这一段经历在自己的生命沙层里属于哪一段呢?“猛志逸四海，骞翮思远翥”\n在北疆戈壁恶劣的工作环境中，周孟波坚持边工作边学习，凭恒心和毅力，于1987年考取了西南交通大学桥隧与结构工程专业研究生。要离开这个陪伴了自己两年的"高级墓室"了，七尺男儿竟没能忍住自己的眼泪。当列车在满目秀色的城市停下来的时候，恍惚间在他眼前飘荡着的依然是漆黑的地窝子里面那一丝烛光，这烛光的记忆将伴随他的一生。')]),_._v(" "),v("p",[_._v("在西南交大三年深造，周孟波不仅步入了桥梁科学的最前沿领域，而且熟练掌握了英语、计算机，科技论文的写作水平也有显著提高。这期间，周孟波担任了土木系研究生党支部书记、西南交大研究生会主席，对管理学及市场经济理论亦有广泛涉猎，逐渐成长为懂技术叉懂管理，懂政治叉懂业务的复合型人才。")]),_._v(" "),v("p",[v("strong",[_._v("二、知难而进 才华初现")])]),_._v(" "),v("p",[_._v("1990年，周孟波从西南交通大学毕业后被分配到中铁大桥局桥梁科学研究院从事桥梁设计工作。他由衷地喜欢这个工作，每一座桥都是一座彩虹，它们都是设计师思想的结晶。每设计一座桥时，周孟波常常把自已完全融进每一张精美的图画之中，他热衷于把自已的梦想用手中的笔变成现实，并且深深地迷恋这种感周207")]),_._v(" "),v("p",[_._v("觉。参加工作才两年，他就陆续作为主力参与了南昌桥、武汉航")]),_._v(" "),v("p",[_._v("空路立交侨的设计。但是让他感到有些意外的是，自己第一个独立设计的不是桥，而是一座塔。")]),_._v(" "),v("p",[_._v('1992年初，大桥局接到为深圳“世界之窗”设计制造安装仿埃菲尔铁塔的任务。任务到了纲结构室，可三个月过去了，设计工作进展很小、甲方一天一个电话，催得每个人都焦急万分。周孟波属于桥梁设计室，本来和这项任并没有关系，领导刚刚决定他做副主任，可他却想啃啃这块硬骨头。周孟波主动去找院长："我能不能试试？"\n院长叹了口气说:。要是你年龄大一点，经验多一点几就好了，这可不是一股的任。"\n可是，除了这个主动请缨的27岁小伙子、大家都生怕陷进这个看不见的“泥潭”中。原理决定掉他去设计了，很多人都不理解。“你去哪里就是个设计员，这里马上要替你做副主任啊。”周孟波乐呵呵地不说什么，其实他心里也没底，可越难的工作在设计师的眼中就越有挑战性。其实，在这之前，他很清楚这一涉及难度极大。埃菲尔铁塔是世界著名的钢结构建筑。塔高320米，光塔基占地面积就1万平方米。整个塔身全部是有钢架镂空结合，重达9000吨。有1.8万个部件组成，铆钉就用掉100多万个。深圳“世界之窗”要求按原塔比例缩小，缩小后仍高103米，近主塔钢结构就重达700吨。3000多根钢件靠10万多个高强度螺栓连接，空间几何关系和三维变化异常复杂，稍有差错就难以合拢。更要命的是，埃菲尔铁塔的设计是保密的。除了两页从科普书上抄下来的资料和一张旅游风景明信片，没有任何可以参考的数据和资料！这给设计工作带来巨大的难度。周孟波把家安在设计室，每天一睁开眼，就能看到那张放大的铁塔照片。他用放大镜仔细地研究铁塔的每一个细节，足足一个星期才从照片上分析出一点点结构的奥秘。接着是不断地推算和比较，有时候深夜一个闪念出现，马上打开手电筒，在照片上标注出来。他把设计桥梁中运用的球面坐标和空间几何理论用到这个设计中，结合对图片的分析，推导出十几个公式，用在图纸设计中居然解决了大部分难题!在设计室一株就是7个月！周孟波抱出了700张大大小小的图纸，上面的一笔一划，都凝聚着他的心血。埃菲尔铁塔的神秘面纱一天天被他揭开了，仿建的埃菲尔铁塔成为深圳“世界之窗”的标志性景观。2003年，周孟波到法国巴黎，看到埃菲尔铁塔与自已当年的设计竟无两样，笑言:"我可自称\n\'埃菲尔第二\'了"。')]),_._v(" "),v("p",[_._v("知难而进、刻苦钻研的精神，使得周孟波的专业水平与领导能力得到了认可，1993年，被任命为大桥局桥梁科学研究院副院长，那年，他才满28岁。")]),_._v(" "),v("p",[_._v("1994年，周孟波被派往武汉长江二桥，担任大桥局方面的的常务副指挥长，该桥建成后，获得“鲁班奖”。")])]),_._v(" "),v("hr")])}),[],!1,null,null,null);v.default=n.exports}}]);