(window.webpackJsonp=window.webpackJsonp||[]).push([[745],{1106:function(_,v,p){"use strict";p.r(v);var n=p(14),t=Object(n.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|")]),_._v(" "),v("blockquote",[v("p",[v("strong",[_._v("中国桥梁少帅——周孟波（下）")])]),_._v(" "),v("p",[_._v("**"),v("br"),_._v("\n文/雷斌**")]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/img.bimg.126.net_photo_wvmp0-JTIJuG1DmrK0FEVQ==_4856850723143133558.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_wvmp0-JTIJuG1DmrK0FEVQ==_4856850723143133558.jpg",alt:""}})])]),_._v(" "),v("p",[v("strong",[_._v("三、 勇担重任 树世纪丰碑")])]),_._v(" "),v("p",[_._v("1996年底。周孟波被任命为大桥局副局长。1997年4月，国家“九五”重点工程，总投资30亿元的芜湖长江大桥正式开工，她被任命为工程指挥部指挥长兼党工委书记，执掌起芜湖长江大桥指挥部的帅印。芜湖长江大桥的建成，将打通华东铁路的第二通道，将皖南、皖北建通连成一片，促进经济的发展，意义重大。")]),_._v(" "),v("p",[_._v('芜湖长江大桥是我国在长江上建造的最后一座公铁两用大桥，长11公里，主跨312米，计划四年建成通车。大桥设计新颖，采用了20多项国内先进技术，工程量相当于武汉、南京两座长江大桥的总和，被人们称为"世纪大桥"。担当如此重任，必须要有非凡的勇气，同时还要在实践中从技术、管理等方面不断创新。为了建设一流工程，周孟波带指挥部的同志们大胆做出了"建世纪大桥，创国优工程"的承诺。提出了“建一座桥梁，树一座丰碑，育一批人才”的目标。')]),_._v(" "),v("p",[_._v('鉴于芜湖长江大桥在我国同类型桥梁中，工程量最大、建设工期最短，技术含量最高。为了按期高质量地完成施工，周孟波制定了《芜湖大桥准项目法施工管理考评办法》，第一次以合同制的形式明确了指挥部与各工程处的关系，要求管理层与作业层分离运行，逐步转换经营机制，把进度、质量、效益、安全和文明施工纳入你目标管理，以此作为奖惩的标谁。这一办法的实行，打破了以往国优建筑企业旧机制造成的惰性和阻力，给企业带来了新的活力。为保证工程质虽，周孟波还首创了"准项目法，进行施工管理。1999年，他代表建设者发出《芜湖大桥质量宣言》，承诺对大桥质量终身负责，使得全桥工程优良率大100%。')]),_._v(" "),v("p",[_._v("在工作中。周孟波以身作则，最攻坚的技术环节、最艰苦的现场、最危险的时刻都可以看到他的身影。")]),_._v(" "),v("p",[_._v("10号主桥墩在江中水深流急处，封底先要将直径30.5米、近20层楼高的钢围堰穿过30多米的砂层扎在基岩上。然后浇筑8米厚的混凝土底板·巨大的水压，施工中稍有不慎就会出现大井喷。20多天里，周孟波精心组织，合理调度，和工人们一道驯服了这个重700吨的庞然大物。")]),_._v(" "),v("p",[_._v("芜湖长江大桥主桥墩8-8号墩原设计为高桩承台基础，每个桥墩用直径为3.2米的钢管柱，，震功下沉至出层。施工中存在着塌方、清基、水中灌注混凝土等一系列难题，且设计中无补桩位置，一旦产生断桩将无法补救，要求100%的成功。然而，在试验时，钢管桩在插入泥沙、砾石中20多米时，竟因弹性变形，拒绝下沉，试验和理论分析严重不符。在指挥部的全力力协助下，负责施工的四处总工程帅孙黄花带着一批年轻的技术人员苦战20多天，做了200多组适应这一地区岩层的泥浆配合比试验，最后终于配制出价格比牛奶海龟的泥浆。同时，在一批老桥梁专家的指导下，他们制定出了严密的深水覆盖层大直径泥浆护壁钻孔桩施工工艺、操作规程、检测手段和补救措施，确保了基础工程无一差错地按期完工。")]),_._v(" "),v("p",[_._v("针对芜湖桥低塔斜拉桥型的钢梁悬拼难题，在周孟波的领导下，毕业于西南交大，并正在攻读在职硕士的二处总工程师周祖干提出了新的思路，成功地完成了斜拉桥架梁的工艺方案，结果使这一桥型新颖的特大公铁两用斜拉桥顺利悬拼。")]),_._v(" "),v("p",[_._v("1998年夏季，长江洪水肆虐，考验着建设中的芜湖长江大桥，也考验着周孟波。汛前他做出了“汛前大干50天，汛期抗洪抢险，汛后大干100天”的宏观施工战略。")]),_._v(" "),v("p",[_._v("13个水中桥墩正在加固，抗击着汹涌的洪水一次又一次的冲击。任何一个小小的纰漏，都可能酿成一场灾难。周孟波站在“西林号”小艇上，眉头紧锁，倾泻的暴雨让披在身上的雨衣形同无物。作为大桥工程指挥长，扑面而来的压力使他感到前所未有的沉重。面对这场洪水，如何保证汛期施工安全？如何保证大桥的施工质量？都是摆在面前的难题。10号桥墩双壁钢围堰正在进行水下混凝土封底。江面骤起8级大风，暴雨倾盆，周孟波几天几夜没有离开施工现场，精心调度，沉着指挥，研究和据解决关键技术和组织协调问题。终于使封底圆满成功。")]),_._v(" "),v("p",[_._v("当安徽省准备将大桥北岸的子堤破堤行洪的关键时刻，他力陈理由阻止破堤行洪，并带领800多名职工严防死守100天，保住了生产区，为大桥按期通车抢出了宝贵时间。")]),_._v(" "),v("p",[_._v("2000年9月30日，总投资超过30亿，工程规模和技术含量均居亚洲第一位的芜湖长江大桥建成通车。这座集我国现代桥梁新技术、新结构、新材料和新工艺之大成的低塔公铁两用钢斜拉桥创造了工程合格率100%\n和优良率100%\n的极高标准。并于次年获得中国建筑工程最高奖——鲁班奖；其大跨度低塔斜拉桥板桁组合结构建造技术获得2002年度国家科技进步一等奖，这也是该年度唯一的土木工程一等奖。大桥的建成，标志着我国桥梁施工控制技术达到了国际领先水平。被称为是继武汉、南京、九江长江大桥之后的我国桥梁建设第四座里程碑。")]),_._v(" "),v("p",[_._v("周孟波先后荣获安徽省“百万职工跨世纪赶超工程功臣”、“安徽省五一劳动奖章”、“湖北青年五四奖章”、“全国劳动模范”称号。")]),_._v(" "),v("p",[v("strong",[_._v("四、 勇于创新 创一流企业")])]),_._v(" "),v("p",[_._v('2000年8月，年仅35岁的周孟波被任命为大桥局局长、党委副书记，挑起了领导中国最大的桥梁建筑企业的重任，带领这支有2.2万名职工的"建桥国家队"。')]),_._v(" "),v("p",[_._v('当时，大桥局一年合同额20多亿元，主要是靠"娘家"铁道部给饭吃。周孟波上任后，大桥局与铁道部彻底脱钩，没娘的孩子开始找奶吃。2001年4月，大桥局整体改制为"中铁大桥局集团"，周孟波出任总经理。')]),_._v(" "),v("p",[_._v('上任之初，他就矢志带出一支国际知名的中国"建桥国家队"。他跑遍了分布全国的10多个二级单位和几十个工点，深入调查研究，指出了企业存在的“发展战略研究不够、发展不够he\n创新不够”等主要问题，和领导班子成员一起，从企业实际出炭，以解放思想、转变观念为先导，由易到难、由浅入深、由局部到整体，积极稳妥地推进各项改革。')]),_._v(" "),v("p",[_._v('周孟波与领导班子其他成员一起，审时度势，制定了“BIO”的经营发展战略。"B"(Bridge)即做强做大桥梁建造和与桥梁建造相关的产业链;"I"(Investment)即积极开展资本运营，发展投资业务，"0"(Overseas)即进一步巩固和扩大海外桥梁及土建业务承建和提供相关服务。这一总体战略，又被具体描述为"一业为主、二元经营、三个转变"的发展战略。一业为主，即做专、做强、做大建桥主业，继续巩固和发展建桥业务在业内的优势地位；二元经营，即桥梁建造\n(桥梁制造)与投资业务二元并举，积极稳妥发展投资业务，开展资本运营，培育集团新的经济增长；三个转变，即由单一内陆建桥向内陆与跨海大桥建设并重转变，由单一国内建桥问国内外建桥并重转变，由建筑承包商向承包商兼投资商转变。明确的战略规划指明了前进的方向，为大桥局经济效益和社会效益跨越式增长奠定了坚实基础。')]),_._v(" "),v("p",[_._v('两度改制，为企业插上腾飞的翅膀。第一次改制是2001年4月，按照中铁工程总公司的要求，铁道部大桥局整体改制为"中铁大桥局集团"，组建了"中铁大桥局集团有限公司"和10个控股子公司，周孟波任集团公司总经理。此次改制，建立了现代企业制度，健全了法人治理结构，规范了各项管理制度，提高了工作效率。第二次改制是实施股份制改造。周孟波从上任伊始，就提出通过股份制改造，用优良资产筹组上市公司，增强主业整体实力和核心竞争力的思路。经过不懈努力，于2004年10月正式创立了中铁大桥局股份有限公司。目前，股份公司已完成了第一期上市辅导。在股份制改造的同时，坚持稳妥推进、主辅分离、两层分开、剥离"企业办社会"等改革，多项改革率先在中铁工程总公司系统取得重大突破和进展。经过改革改制，大桥局主业的生产经营运行良好，母体负担逐渐减轻，迎来了新的发展的春天。')]),_._v(" "),v("p",[_._v('坚持创新发展，实施精细管理。作为教授级高级工程师的周孟波，对创新发展有更深的理解。他始终致力于观念与文化、体制与机制、经营与管理、科技与装备的创新，大力实施精细化管理，提出了"理念现代化、制度规范化、责任明晰化、考核严格化、技术最优化、缺陷最小化、效益最大化、节奏快速化、塑形标准化"的精细化管理理念。以此为背景，大桥局设计和建成了以芜湖长江大桥\n上海东海跨海大桥、澳门西湾大桥、孟加拉帕克西大桥等为代表的350多座举世瞩目的世界级桥梁，实现了由内陆建桥问海洋建桥的跨越。2002年，江泽民同志亲笔为大桥局承建的大佛寺桥和马桑溪桥题写了桥名。2005年8月22日，细雨沐江城，被称为"中国桥梁建设史上第五座里程碑"\n——由中铁大桥局集团承建的武汉天兴洲公铁两用长江大桥正在施工。上午8点50分，中共中央总书记、国家主席、中央军委主席胡锦涛来湖北考察工作的第一站便是大桥工地。胡总书记说，我对大桥局是了解的，你们修建过很多有名的大桥，特别能打硬仗，有非常好的传统，我对你们充满信心。希望你们再接再厉，把天兴洲大桥建成世界一流的桥梁，为我国的桥梁事业和世界的桥梁事业做出贡献，向世界展示中国的建桥水平。')]),_._v(" "),v("p",[_._v('人才和科技、装备实力进一步增强，企业核心竞争力全面提升。以"三支人才"队伍建设为重点，强化人才培训及激励机制，一大批高素质的年轻人才走上了管理和技术的重要岗位。五年间，新增享受国务院特殊津贴专家5名，新增教授级高级工程师48名，新增高级工程师313名。科技创新取得了丰硕成果，桥梁施工技术进一步向桥梁大跨、新型、整体、快速施工等方向发展，近年获得国家科技进步奖的数量为历史上最多的时期。五年间投入科研开发经费5·4亿元，完成科研课题101项，共有111项、794人次获得了国家、省部级和集团公司科技奖励。五年间共获得国家科技进步奖6项，省部级科技进步奖8项，获得建筑工程鲁班奖6项，获得国家级、省部级"工法"6项，获得国家专利5项，创"中国企业新纪录"15次。五年新制新购设备投入资金7·94亿多元，为"九五"期间的2·4倍，研制了"小天鹅"号2500吨起重船、"海天号。混凝土工作船、海上桥墩运架船、"天一号"3000吨起重船、"大桥海虹"号200吨起重船、"大桥海龙"号34米抛锚工作船等海上工程船舶，组建了实力强大的海上施\n"航母群"，全面提升了企业核心竞争力。')]),_._v(" "),v("p",[_._v('短短几年间，脱胎换骨的大桥局集团相继拿下上海东海跨海大桥等30余项国家重点工程，在国际竞标中也屡屡得手，承建了孟加拉国帕克西桥和澳门澳幽三桥、香港后海湾大桥等工程。2003年，该集团新签合同额80亿元，比1999年翻了近两番，跻身世界十大桥梁承包商之列。企业经营成果丰硕。大桥局完成生产经营指标连续五年高速增长，提前一年超额完成了"十五"规划确定的各项主要经济指标。五年共建成大中型项目324个，桥梁折合247680延米，分别是"九五"期间的2·3倍和1·6倍。五年累计完成营业额285·3亿元、实现新签合同额435亿元、实现税前利润2·67亿元，分别是\n"九五"期间的3·08倍、3·8倍和3·03倍;员工人均收入由"九五"末的11567元提高到"十五"期末的19515元，增加了7948元，年人均增长11%。2004年，中铁大桥局集团入选世界最大的225家国家承包商和十大桥梁承包商。')]),_._v(" "),v("p",[_._v('近年来，大桥局先后荣获\n"全国五一劳动奖状"、"全国优秀施工企业"、"全国质量管理先进企业"、"全国和谐劳动关系优企业"、"全国精神文明创建工作先进单位"和\n"湖北省最佳文明单位"等，大桥局这块金字招牌再度焕发出夺目的光彩!\n周孟波也光荣地获得了"全国优秀创业企业家"、"全国最受关注企业家"、"中国青年五四奖章"、"中央企业十大杰出青年"、"湖北省杰出青年企业家"、"中国铁路工程总公司优秀企业家"、武汉市企业界"十佳新闻人物"等荣誉称号。')]),_._v(" "),v("p",[_._v("尽管取得了不俗的业绩，但周孟波并没有满足于以往的成绩，他始终以一个共产党员的标准严格要求自已，保持了艰苦奋斗、勤俭办事的优良作风。")]),_._v(" "),v("p",[_._v('作为一位有远见卓识、勇于创新的国有企业领导者，周孟波正以胸怀全球的眼光，带领这支国际知名的"建桥国家队"，以“跨越天堑，超越自我”的精神，在江河湖海上架起更多的绚丽彩虹。')])]),_._v(" "),v("hr")])}),[],!1,null,null,null);v.default=t.exports}}]);