(window.webpackJsonp=window.webpackJsonp||[]).push([[747],{1108:function(_,v,p){"use strict";p.r(v);var t=p(14),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("|")]),_._v(" "),v("blockquote",[v("p",[v("strong",[_._v("李本琪")]),v("br"),_._v(" "),v("strong",[_._v("——下达“点火起飞”命令的人")])]),_._v(" "),v("p",[_._v("**")]),_._v(" "),v("p",[v("strong",[_._v("文/徐贞宇 王新阁")])]),_._v(" "),v("p",[v("strong",[_._v("图 胡建兵 高峰")])]),_._v(" "),v("p",[_._v("编者按：李本琪，西南交通大学计算机系92届校友，西昌卫星发射中心1号指挥员，多次荣获全军及总装“优秀人才奖”，为我国成功发射卫星做出了突出贡献。")]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/img.bimg.126.net_photo_oaEIsCyEf1h4Ra9MIMxAYg==_2880333436681067776.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_oaEIsCyEf1h4Ra9MIMxAYg==_2880333436681067776.jpg",alt:""}})])]),_._v(" "),v("p",[_._v("李本琪，这位下达“点火，起飞”命令的“嫦娥一号”卫星发射01指挥员，在2007年10月24日18时05分，名闻天下。10～11月间，李本琪的曝光率不亚于任何一位明星大腕。当娱乐明星、文体明星备受追捧的时候，人们有理由记住这样的明星：他们扎根山沟，无私奉献、矢志航天，是托举神箭上天的英雄好汉。")]),_._v(" "),v("p",[_._v("今天让我们走近这位西南交通大学计算机学院校友，12次担任01指挥员，且次次发射圆满成功的传奇人物……")]),_._v(" "),v("p",[v("strong",[_._v("火箭发射失败让他走进航天城")])]),_._v(" "),v("p",[_._v("坐在女生宿合前的草坪上，弹着心爱的吉他，吟唱汪国真的诗或者是吼起崔健的摇滚乐，是李本琪大学时代校园生活的普通景。")]),_._v(" "),v("p",[_._v("与其他同学相比，李本琪显得略胜一筹。他每年都担任学生会干部，并连年荣获一、二等奖学金，毕业时还被评为四川省优秀毕业生。当时的西南交通大学，学生一入校人生目标基本确定，那就是到铁路系统工作。李本琪从来没有想到，自己的人生能和航天发生联系。")]),_._v(" "),v("p",[_._v("1992年3月的一个下午，正在计算机系自动控制专业读大四的李本琪刚打完篮球、汗流浃背的他，风一般穿过校园静谧的林荫道，大跨步跃上长长的阶梯飞身前往教学楼——钥匙忘在宿舍了，他要到自习室找同学开门。路经教学楼值班室时，李本琪停下了脚步。“嘿，火箭露出来了!哎呀，镜头怎么不拉近点啊……”偌大的值班室里被同学们拥挤得水泄不通，原来中央电视台正在直播西昌卫星发射中心的卫星发射盛况。\n“这次发射不同凡响啊，是我国火箭发射外国卫星!”……同学们的热切议论让李本琪心生好奇，他顾不得浑身热汗一头扎在人堆里目不转睛地观看直播。")]),_._v(" "),v("p",[_._v("那次发射失利，在全国引起强烈反响，对航天从不关注的李本琪不知怎地，心里忽然空落落的，就像自己考试失败了，他特别想冲到航天发射一线大干一场。临近毕业时，一位老师在班上宣布：西昌卫星发射中心到学校招人，哪位同学愿意去?李本琪毫不犹豫第一个举起了手。后来一切出乎意料的简单，没有考试、没有面试，他很快就扛着行李到中心报到了。")]),_._v(" "),v("p",[_._v("一个巧合，一次心灵深处的撞击，让一个年轻人的命运与中国航天的发展交织在了一起。")]),_._v(" "),v("p",[v("a",{attrs:{href:"pic/img.bimg.126.net_photo_XgAbPYvTTnTrnuq38UbcJQ==_4288271270187740845.jpg"}},[v("img",{attrs:{src:"/pic/img.bimg.126.net_photo_XgAbPYvTTnTrnuq38UbcJQ==_4288271270187740845.jpg",alt:""}})])]),_._v(" "),v("hr"),_._v(" "),v("p",[v("strong",[_._v("数千名航天专家和科技人员由他指挥")])]),_._v(" "),v("p",[_._v("时光荏苒。一晃15年过去了，如今的李本琪已经是发射场上的头号指挥员，火箭测试发射的整个过程由他组织，发射场上数千名航天专家和科技人员由他指挥，火箭“点火”命令由他下达。自2000年6月至今，李本琪12次担任01指挥员，卫星发射次次全胜。")]),_._v(" "),v("p",[_._v("入伍l5年来，李本琪先后发现并排除各类故障近600起，确保了火箭不带任何隐患上天。在中国航天腾飞的征程中，李本琪留下了自己的足迹。")]),_._v(" "),v("p",[_._v("李本琪的01之路，一直与艰辛相伴，与危机相随。其间，既发生过暴风骤雨导致发射延迟的险情，也遇到过关键时刻火箭仪器故障频发的危机……各种意外险情均被他一一化解。2007年5月31日晚，3号发射场灯火通明，“鑫诺三号”卫星蓄势待发，发射窗口定在半夜零点。射前三个小时，有关人员忽然通报，天空中有积雨云积压，云层厚、云色黑，这种天气状况直接影响火箭飞行安全，有人建议推迟发射为宜。李本琪当即调来详细的气象资料，与气象专家紧急磋商。专家认为，从气象资料上看，射前一小时天气状况会有好转，那段时间是绝佳发射时机，如果推迟发射就要延误时日。李本琪认真思考后决定：按照预定时间发射。0:05分，长征三号甲火箭托举着尼日利亚卫星直刺夜空，长征系列运载火箭第98次飞行圆满成功。")]),_._v(" "),v("p",[v("strong",[_._v("他的口令精确到了微秒")])]),_._v(" "),v("p",[_._v("“嫦娥一号”发射成功后，一位专家向李本琪表达了谢意，因为\n“嫦娥一号”卫星发射实际时间与理论最佳时间误差只有0．2秒，这些微小误差节省的燃料足以让卫星在太空多工作一年。对于李本琪而言，这意味着，在火箭发射倒计时过程中，他所下达的数百道口令精确到了微秒。")]),_._v(" "),v("p",[_._v("李本琪说：“时间就是生命，担任01指挥员的每一次发射，我都站在悬崖边；时间又不仅仅是生命，还是荣誉、使命、尊严、气节等等作为01必须面对的东西。就那短短几分钟甚至几秒钟，我要给全国人民一个交代。”")]),_._v(" "),v("p",[_._v("2007年4月14日凌晨，西昌卫星发射中心发射场区大雨滂沱，长征三号甲运载火箭托举着我国“北斗”导航卫星矗立在发射台上。进入负4分钟程序后，测试人员报告：一个为火箭供气的连接器未按规定脱落，若不在3分钟内脱落，火箭点火升空时必然被其拉扯住，火箭、卫星乃至整个发射场都可能不保。此刻，百余号人的远控大厅鸦雀无声，所有首长、专家和参试人员的目光全部集中到了李本琪身上。只见他临危不乱，在1分钟内连续下达7道口令，相关岗位人员心领神会、默契配合。终于，1分钟后故障排除，火箭顺利升空。")]),_._v(" "),v("p",[_._v("短短1分钟，生死60秒。战友们说，李本琪是在刀尖上舞蹈的英雄，此话不假。因为那一分钟精彩“亮剑”，支撑起了长箭上“五星红旗”和“中国航天”标志的逆势起飞，那是所有航天人生命中最为华美的画面。那1分钟的成功，背后支撑的是15年披星戴月练出的硬功，是5475个日夜积累的苦功。")]),_._v(" "),v("p",[_._v("2006年，西昌卫星发射中心迎来了年均8次以上卫星发射的高密度任务期，对于主管发射场区技术工作的李本琪而言，火箭测试周期要缩短，流程要优化，他要与时间赛跑。")]),_._v(" "),v("p",[_._v("为适应中心高密度发射需要新建的三号发射塔，建设周期只用了一年的时间，这里面太多的工作需要协调，太多的设备需要磨合。预计一个月的全航区合练，因发射任务来临不得不缩短为10天。就在这10多天的时间里，李本琪带领参试人员全力以赴，一举解决了200多个问题，并重新编制了新的火箭吊装对接程序，确保了航区合练顺利进行和三号发射塔的准时启用。")]),_._v(" "),v("p",[_._v("以往，中心二号发射塔完成状态转换需要15天，才能执行另一种型号火箭的发射任务。经过李本琪与大伙儿优化程序，改进设备，创新方法，最快5天就可以完成；在他和参试人员的共同努力下，火箭测试发射流程由以往的40天缩短为28天，为夺取高密度发射赢得了宝贵时间。")]),_._v(" "),v("p",[v("strong",[_._v("叱咤风云的人月工资只有4000")])]),_._v(" "),v("p",[_._v("卫星、火箭的价值常常以千万美元来计算，李本琪当0l指挥员的表现也决定着价值数亿、数十亿的星箭“前途命运”。前两年李本琪负责发射场改造时每年经手的经费有上千万，这些钱他花起来眼睛都不眨一下，因为这投下的钱都会变成发射能力的提高。李本琪当年学计算机的同班同学，有的成了开上名车、住上洋房的大款，有的早已拿到了发达国家的绿卡，许多人邀请他一起创业，然而这个叱咤风云的人虽然每个月只有4000多元的工资，却说什么也不肯离开心爱的发射场，他心里算着另外一笔账。")]),_._v(" "),v("p",[_._v("“选择这个职业并且坚持干了下来，主要是内心由衷的喜欢。其实也曾经考虑过换职业，但因为工作需要就留了下来。能够坚持在大山沟里从事航天工作，我始终怀着一颗感恩的心，因为我出生在农村，我小时候家里非常穷，穷到什么程度?家里没有任何值钱的东西。可以说我家是在改革开放后富起来的，恢复高考后，母亲重新考大学，当时我正在上小学，我们母子共同学习，家庭环境逐步改善，国家真的给了我很多。我想现在正是回报的时候了，要多为国家做点事情。父母对我的工作非常支持!”李本棋说。")]),_._v(" "),v("p",[_._v("买不起好车，住不上洋房，无法给生病的孩子提供较好的治疗条件，在经济上，李本琪是一个不折不扣的穷人。然而，他的声音通过电视、广播传遍了全世界，内心是富裕的，富裕得让许多同学羡慕。他告诫自己，等离开航天城的时候，赚钱的机会多的是。")]),_._v(" "),v("p",[_._v("李本琪不是一个能够超脱现实的人，却在理想与现实之间找到了平衡点。当然，现实也回报给他巨大的成就。入伍15年来，他主持研发的《发控台监测系统》等两个科研项目获全军科技进步三等奖，他本人也多次荣获全军及总装“优秀人才奖”，并享受军队一类优秀专业技术人才岗位津贴，并荣立二等功1次。前不久，他还被推选为“全国五四青年奖章”候选人。")]),_._v(" "),v("p",[_._v("西南交通大学供稿，来源： 解放军生活， 写作时间： 2008-5-8 19:42:31")])]),_._v(" "),v("hr")])}),[],!1,null,null,null);v.default=n.exports}}]);