(window.webpackJsonp=window.webpackJsonp||[]).push([[753],{1112:function(_,t,p){"use strict";p.r(t);var i=p(14),g=Object(i.a)({},(function(){var _=this,t=_._self._c;return t("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[t("p",[_._v("|")]),_._v(" "),t("blockquote",[t("p",[t("strong",[_._v("祝贺我校地质工程学科成立五十年"),t("br"),_._v("\n文/土木工程学院")])]),_._v(" "),t("p",[_._v("2009年6月13日上午，西南交通大学地质工程学科成立50周年学术研讨会开幕式在网络学院国际会议厅隆重举行。中国工程院院士卢耀如，中国铁路建设投资公司总经理、顾聪校友、校党委副书记王顺洪、副校长濮德璋、铁一院副院长刘为民校友、美国麻省理工学院研究员、我校兼职教授朱正亚、土木学院李乔院长、钱永久书记等20余位嘉宾出席了大会。")]),_._v(" "),t("p",[_._v("[](pic/img.bimg.126.net_photo_y8SBSbWzR4OUMJ83sB-\nKeg==_4271664246561992538.jpg)"),t("a",{attrs:{href:"pic/img.bimg.126.net_photo_TzZHyD1V4HGX0dgzhesxPQ==_4271664246561992535.jpg"}},[t("img",{attrs:{src:"/pic/img.bimg.126.net_photo_TzZHyD1V4HGX0dgzhesxPQ==_4271664246561992535.jpg",alt:""}})])]),_._v(" "),t("p",[_._v("地质工程系主任巫锡勇教授首先致辞，他向社会各界、学校和校友们对学科发展的支持和帮助表达谢意。1958年以前唐山铁道学院铁道建筑系只有一个工程地质教研室，经过半个世纪的努力奋斗，西南交通大学的地质工程学科已硕果累累。地质教师主持的多项国家级、省部级科研项目，多次获国家级奖、省部级奖项。今年适逢我校地质工程知名专家李秉生、蒋爵光教授80寿辰，两位老教授为地质工程学科的发展做出了突出贡献，巫主任对两位教授致以敬意并祝他们健康长寿。")]),_._v(" "),t("p",[_._v("校党委副书记王顺洪肯定了地质工程学科成立以来取得的成就和作出的贡献，还专门提到地质工程毕业生深受用人单位的青睐，连续数年就业率都达到100%。王顺洪副书记衷心地祝愿地质系能够继往开来、再创辉煌。校友代表顾聪在致辞中感谢母校的培养并祝愿母校的明天更美好。")]),_._v(" "),t("p",[_._v("随后，卢耀如院士做了题为“地质灾害与城市安全”的特邀报告。报告分析了全球内外部影响地质生态环境的因素，以及我国地质生态环境现状，并就坍塌、岩溶等地质工程问题进行了讲解。")]),_._v(" "),t("p",[_._v("下午，“校友专题学术报告会”在国际会议厅举行。来自中铁一院、二院、三院、四院、五院、福建省交通厅设计院、中南大学的校友代表分别就我国高速重载铁路发展中的地质工程问题、城市轨道交通发展中的地质工程问题、汶川大地震等作了专题报告。")]),_._v(" "),t("p",[_._v("会议期间，在镜湖宾馆院士厅举行了“地质工程学科50周年学科规划研讨会”。校党委顾利亚书记、校学科建设与发展处刘建新处长、富海鹰副处长与顾聪、朱正亚、刘为民等校友共聚一堂，纷纷为促进我校地质工程学科的发展建言献策。")]),_._v(" "),t("p",[_._v("[ ![](/pic/img.bimg.126.net_photo_y8SBSbWzR4OUMJ83sB-\nKeg==_4271664246561992538.jpg)](pic/img.bimg.126.net_photo_y8SBSbWzR4OUMJ83sB-\nKeg==_4271664246561992538.jpg)")]),_._v(" "),t("p",[_._v("晚上，欢迎宴会暨李秉生教授、蒋爵光教授八十华诞庆典在镜湖宾馆举行。顾利亚书记出席宴会并发表讲话，她在讲话中感谢了地质工程系在五十年来为学校发展做出的贡献，希望大家齐心协力，共谱地质学科美好的明天，并向李秉生教授、蒋爵光教授献上八十华诞寿礼。宴会进行过程中，顾书记宣布了以本次研讨会为契机聘任为我校名誉教授、顾问教授及兼职教授的名单，并分别为卢耀如院士颁发了名誉教授聘书、为顾聪校友等颁发了顾问教授聘书。宴会在欢快热烈的气氛中进行，在场的嘉宾和校友们亲切交流，共同展望学科发展的美好未来。")]),_._v(" "),t("p",[t("a",{attrs:{href:"pic/img.bimg.126.net_photo_9N4TFuKbPKK2JP08AkhC9Q==_4271664246561992541.jpg"}},[t("img",{attrs:{src:"/pic/img.bimg.126.net_photo_9N4TFuKbPKK2JP08AkhC9Q==_4271664246561992541.jpg",alt:""}})])]),_._v(" "),t("p",[_._v("会议期间，华西都市报、成都商报、四川电视台科教频道等媒体对与会的嘉宾进行了采访报道。")])]),_._v(" "),t("hr")])}),[],!1,null,null,null);t.default=g.exports}}]);