(window.webpackJsonp=window.webpackJsonp||[]).push([[768],{1128:function(_,t,p){"use strict";p.r(t);var i=p(14),o=Object(i.a)({},(function(){var _=this,t=_._self._c;return t("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[t("p",[_._v("|")]),_._v(" "),t("blockquote",[t("p",[t("strong",[_._v("机械工程90年暨沈志云院士80华诞庆典隆重举行"),t("br"),_._v("\n机械工程学院")])]),_._v(" "),t("p",[_._v("5月28日下午2：30，机械工程90年暨沈志云院士80华诞庆典在九里校区大学生会堂隆重召开。返校的院友代表、机械工程学院和牵引动力中心的教职工和学生代表共1400余人参加了庆典活动。两院院士沈志云、中国工程院院士钱清泉、校党委书记顾利亚、校长陈春阳及在校的全体校领导、香港知名校友徐立夫、西昌卫星发射中心总工程师王泽明、前机械学院院长王金诺、国家级教学名师吴鹿鸣、机械学院院长周仲荣、院党委书记段雪芬等30余位领导和嘉宾在庆典主席台就座。")]),_._v(" "),t("p",[_._v("机械学院院长周仲荣代表机械学院发言，他简要介绍了机械学院在九十年的发展历程中所取得的成绩，充分肯定了沈志云院士在我国机车车辆领域所做出的突出贡献，并感谢沈院士在机械学院学科发展和人才培养方面不可替代的功绩。1919年，机械工程系于唐山创办，经历了许多风雨，中间甚至停办过两次。但机械学子一直秉承严谨治学、求真务实的态度，为机械工程的发展奉献了毕生的心血，同时机械学科也孕育了以沈志云院士等为杰出代表的一大批国内外知名的专家、学者和企业家。周院长表示，机械学院在面对辉煌的同时，会一如既往向国内外一流学府学习，为建设国际一流的机械工程学科而努力奋斗。")]),_._v(" "),t("p",[_._v("沈志云院士用“六十年重生，九十载辉煌，满天下桃李，为祖国争光”形容机械学院九十年来的发展，并向现场的老师、同学两次深深鞠躬，全场响起经久不息的掌声。沈志云院士早年出国留学，学成归国后，在党和国家的领导和培养下，致力于我国机车车辆方面的研究工作，几十年来为祖国的轨道交通事业做出了卓越的贡献，特别是他于1983年发表的非线性轮轨蠕滑力计算模型，更是赢得国内外专家学者的赞赏。从1949年到今天，在西南交大工作和生活了60年的沈老对学校怀有非常深厚的感情，如今已经80高龄的他，毅然放弃去国外与儿女团聚的机会，继续为学校的发展贡献力量。")]),_._v(" "),t("p",[t("a",{attrs:{href:"pic/img.bimg.126.net_photo_6KxiwiWXhsT-9ZF1Vi_WoQ==_4000603843989157287.jpg"}},[t("img",{attrs:{src:"/pic/img.bimg.126.net_photo_6KxiwiWXhsT-9ZF1Vi_WoQ==_4000603843989157287.jpg",alt:""}})]),t("a",{attrs:{href:"pic/img.bimg.126.net_photo_QJpvniQYRU0ydb3RssETZQ==_4290804544978015161.jpg"}})]),_._v(" "),t("p",[_._v("校长陈春阳在讲话中充分肯定了机械工程学科90年来在科学研究、人才培养、社会服务等方面所做出的突出贡献。对于近年来机械工程学科荣获国家科技进步一等奖、国家自然科学二等奖、国家级教学成果一等奖、入选中国高校十大科技进展等一系列高水平国家级奖项、建立了全国高校机械工程学科第一个国家级创新研究群体、2007年成为首批国家重点一级学科等一系列骄人成绩表示赞赏。陈校长指出，机械学院90年来的发展，凝聚着以沈志云院士为代表的老一辈科学家和机械学人的心血。西南交通大学机械工程学科创建以来的90年，是在以沈志云院士等为代表的杰出机械学人的引领下，艰苦创业、勇攀高峰、发展壮大的90年，也是为国家交通事业做出积极贡献的90年！")]),_._v(" "),t("p",[_._v("陈春阳校长希望机械工程学院以此次庆典大会的召开为契机，进一步加快发展步伐，不断提高人才培养质量，不断推进科技创新，不断加大服务轨道交通事业主战场的力度。学校将一如既往地支持机械工程学科的发展，并进一步加大支持力度，让百年老校的机械工程学科实现更好的发展。同时祝愿机械工程学院乘风破浪、蒸蒸日上，祝贺沈志云院士老当益壮，康泰幸福。")]),_._v(" "),t("p",[t("a",{attrs:{href:"pic/img.bimg.126.net_photo_QJpvniQYRU0ydb3RssETZQ==_4290804544978015161.jpg"}},[t("img",{attrs:{src:"/pic/img.bimg.126.net_photo_QJpvniQYRU0ydb3RssETZQ==_4290804544978015161.jpg",alt:""}})]),t("a",{attrs:{href:"pic/img.bimg.126.net_photo_6KxiwiWXhsT-9ZF1Vi_WoQ==_4000603843989157287.jpg"}})]),_._v(" "),t("p",[_._v("中国科学院院院长路甬祥、中国工程院院长徐匡迪以及中共衡山县委、衡山县人民政府也发来贺信祝贺沈院士80华诞，高度赞扬了沈院士在机车车辆领域所做出的突出贡献。原铁道部教卫司司长许守祜也在百忙之中发来贺信。教师代表吴鹿鸣教授、院友代表贾建民教授、中法4+4留学生代表雷明、本科生代表孙静分别做了发言，为机械工程90年和沈志云院士80华诞献上了深深祝福。")]),_._v(" "),t("p",[_._v("庆典还表彰了在机械学科从教40年以上的老教授，对他们默默耕耘、无私奉献的精神表示崇高敬意。")]),_._v(" "),t("p",[_._v("庆典结束后，机械学院举行了文艺演出。来自国内外的机械校友会聚一堂，庆祝机械工程90周年以及沈志云院士80华诞。两位在校学生手推蛋糕出现在舞台上，点燃了活动的高潮。全场一齐为沈院士唱响生日之歌。沈院士激动地说：“我从来没有这样度过生日，更没有在这样正式的场合下过生日，此时我感觉我太幸福了，一个人的生日由那么多人共同来过。”沈院士动情地演唱了《莫斯科郊外的晚上》，随着轻快的拍子，每一个人都为这一分一秒而感动。文艺演出以一曲声势浩大的合唱《盛世春潮》结束。")]),_._v(" "),t("p",[_._v("5月28日上午，在九里和犀浦校区举行了机械工程90年机械校友报告会，邀请知名院友与在校学生分享成功的经验，激励在校学生不断前进。")]),_._v(" "),t("p",[t("a",{attrs:{href:"pic/img.bimg.126.net_photo_bYaVy1w3bfB9_R3gRd456g==_2852748888963294163.jpg"}},[t("img",{attrs:{src:"/pic/img.bimg.126.net_photo_bYaVy1w3bfB9_R3gRd456g==_2852748888963294163.jpg",alt:""}})])])]),_._v(" "),t("hr")])}),[],!1,null,null,null);t.default=o.exports}}]);