(window.webpackJsonp=window.webpackJsonp||[]).push([[79],{440:function(n,r,b){"use strict";b.r(r);var v=b(14),_=Object(v.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("hr"),n._v(" "),r("p",[n._v("|  |  |  |  |  **"),r("br"),n._v("\n在西南交通大学-成都军区总医院"),r("br"),n._v("\n全面战略合作共建协议签约仪式上的讲话"),r("br"),n._v("\n校长 徐飞"),r("br"),n._v("\n2014年7月10日"),r("br"),n._v("\n**"),r("br"),n._v("\n尊敬的沈毅院长、杜宜凯政委，"),r("br"),n._v("\n尊敬的总院各位领导、医护工作者，"),r("br"),n._v("\n尊敬的各位来宾，"),r("br"),n._v("\n老师们、同学们："),r("br"),n._v("\n大家上午好！"),r("br"),n._v("\n今天，校院双方主要领导和代表欢聚一堂，共同见证西南交通大学-\n成都军区总医院签署战略合作共建协议，我的心情十分激动。这是西南交通大学跨越三个世纪办学历程中一件大事，也是军地合作、军民融合、校院强强联合、建设高水平研究型大学和研究型医院的一次有益尝试，对双方都有重要的现实意义和深远的历史意义。借此机会，我再次代表西南交通大学，向成都军区总医院各位领导和员工表示最衷心的感谢！同时，也向长期以来一直关心、帮助、支持学校建设与发展的军地各级领导和社会各界朋友，一并表示最诚挚的谢意！"),r("br"),n._v("\n西南交通大学创建于1896年，开创近现代中国高等工程教育的先河，是中国近代建校最早的高等学府之一，是中国交通矿冶、土木工程高等教育的发祥地，素有“东方康奈尔”的美誉。118年来，学校英才辈出，成果灿然，不仅培养了一大批各行各业的杰出人才，还培养了姚桐斌、陈能宽、吴自良等“两弹一星功勋奖章”获得者以及武怀让、马增玉、杨杏佛等多位革命先烈和爱国志士。学校具有光荣的爱国拥军办学历史，是全国唯一一所直接参与“抗美援朝”的高等学府，先后组建三批抗美援朝工程队奔赴朝鲜修建我方军用机场。"),r("br"),n._v("\n上个世纪七十年代以来，经过七五、八五的建设，特别是最近二十余年的发展，学校业已形成工科、理科、文科和生命学科“四大学科群”，理工交叉、文理渗透、四位一体、多学科协调发展的势头强劲。随着“人才强校主战略”、“国际化战略”和“数字化战略”三大战略的全面推进，以及“工科登峰”、“理科强基”和“文科跨越”三大行动计划的深入实施，学校正在加速向综合性、研究型、国际化的高水平一流大学目标奋力前行。"),r("br"),n._v("\n近年来，学校以生物医学工程、材料科学工程为基础，围绕人工心脏瓣膜、血管支架、骨骼肌肉系统修复和替换生物医用材料的研究，创制具有自主知识产权的植介入医疗器械产品，推动新型生物医用材料及相关产品产业化。同时，以生物学、化学及药学、中药学为基础，围绕重大疾病的防治需求，大力推进创新药物关键技术研发和产业化，建设创新药物工程中心，力争建成国内首屈一指的生物医药研发创新中心和产业孵化中心。学校将以此次签约为契机，大力实施“生命振兴”第四大行动计划，积极推动生命科学与医学有机结合、交叉融合，建立基础科学与临床需求的紧密联系，抓紧建设一流医学院。"),r("br"),n._v("\n成都军区总医院是全军首批“三级甲等医院”，前身系贺龙元帅领导的第二军团医疗队，是成都军区的医疗、保健、教学和科研中心，是成都市内医院的“三驾马车”之一。医院历史悠久、实力雄厚，人才济济、设备先进，成果丰硕，屡获国家级、省部级科技成果奖励，在全军医疗系统中享有盛誉。当前，正按照“学术融合、对内整合、对外联合”内整外联的发展道路，大力实施“三大战略工程”，朝着“军区全面领先、川内真正一流、军内整体上游、国内有所作为、国际有所知名”的奋斗目标，努力创建研究型现代战区总医院。"),r("br"),n._v("\n令我们双方非常欣喜的是，校院双方的使命一致，都有着“不加快发展就加快落后”和把事做好、做成、做优的高度共识。双方治院、治校理念一致，都有着实现超常规、跨越式发展，打造世界一流的共同愿景。双方“研究型”的建设目标一致，医院有“三大战略工程”，交大有“三大发展战略”，战略设计和具体路径高度契合，学科、人才与信息化、数字化和大数据的发展思路不谋而合。正是在这样的背景下，葆有着对未来社会、科技变革的高度敏感，以及对人类生命和健康的高度关切，今天，校院双方走到了一起，开启了双方合作发展、共建发展、互融发展的崭新篇章。"),r("br"),n._v("\n多学科的交叉融合、跨界研究是学科发展的潮流和创新源泉，医学科学特别是临床医学科学的发展，对其他学科有极强的依赖性。自然科学、社会科学领域的每一个重大发现、每一次重要发明，都有可能对医学的理论研究与临床实践产生巨大的推动甚至革命性的影响。医院临床中碰到的难点疑点，将借助大学的科研平台和多学科优势得以解决；大学的研究瓶颈，将依托医院几十年积累的临床经验和大数据实现突破。"),r("br"),n._v("\n从今天开始，我们将强强联合，全面推动合作、落实合作并进一步拓展合作、深化合作。首先，我们将在生物医学工程学科开展医学硕士和博士的联合培养，优先建立临床医学硕士、博士学位授权点，在条件成熟的情况下合作开展临床医学学科的本科生教育。同时，我们将充分利用国家在转化医学领域重大政策，全面开展科研合作，共建“西南交通大学-\n成都军区总医院转化医学研究中心”，促进基础研究与临床治疗的双向促进，全面助推基础研究与临床应用一体化，加快推进“研究型医院”建设步伐。再则，我们将共同开展医疗大数据研究，努力建设以全网络、全方位、全关联、全电子化为特征的“数字化医院”。"),r("br"),n._v("\n各位领导，同志们："),r("br"),n._v("\n迄今为止，人类对生命现象的认知和理解还有无数空白领域，对许多严重影响人类健康的疾病本质的认识和探索还有漫漫长路。我们期待，军区医院的各位专家和学校各领域的各位学者携起手来，共同肩负起攀登生命医学科学高峰的历史使命，不断探索和引领科技前沿，不断推动医学科学的发展与进步。在此，我也代表西南交大郑重承诺，我们将秉持合作、开放、共享、双赢的原则，严格恪守协议的要求，切实履行协议规定的责任和义务，把我们共同的事业建设好发展好，为老百姓的健康福祉，为军队医疗卫生事业和社会的可持续发展，不遗余力。我们有理由相信，在双方的共同努力下，一流大学、一流医学院和一流医院“三个一流”的宏伟目标，一定能够早日实现！"),r("br"),n._v("\n谢谢大家。")]),n._v(" "),r("table",[r("thead",[r("tr",[r("th")])]),n._v(" "),r("tbody")])])}),[],!1,null,null,null);r.default=_.exports}}]);